UPDATE `broadcast_text_locale` SET `Text_lang`='Drogguk était un Orc noble. Merci d\’avoir rendu à sa mort les honneurs qui lui étaient dus.', `VerifiedBuild`=26365 WHERE (`ID`=79991 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ensemble, nous pouvons repousser ces Orcs !', `VerifiedBuild`=26365 WHERE (`ID`=79984 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Prenez ces torches et mettez le feu aux dépouilles d\’Orcs qui souillent notre temple.', `Text1_lang`='Prenez ces torches et mettez le feu aux dépouilles d\’Orcs qui souillent notre temple.', `VerifiedBuild`=26365 WHERE (`ID`=79923 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='La catastrophe qui arrive nous anéantira tous. Si les Orcs n\’arrivent pas à s\’unir pour prendre les devants, ces étrangers déchaîneront une vague de feu et de mort qui aura raison de nous.', `VerifiedBuild`=26365 WHERE (`ID`=79914 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il n\’y a pas si longtemps, les Orcs étaient divisés en d\’innombrables clans, se disputant des miettes dans un monde qui ne demandait qu\’à être dompté.', `VerifiedBuild`=26365 WHERE (`ID`=79909 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Contrairement à certains membres de mon peuple, je n\’ai jamais eu de préjugés à l\’encontre des Draeneï, Khadgar.', `VerifiedBuild`=26365 WHERE (`ID`=79898 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je ne l\’ai ni oublié, ni pardonné. Mais fut un temps où Orgrim était un Orc honorable, en plus d\’être mon meilleur ami. Il faut que je l\’aide à regagner même un peu de son honneur perdu. Je lui dois au moins cela.', `VerifiedBuild`=26365 WHERE (`ID`=79851 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Mais combien d\’Orcs y a-t-il ?', `VerifiedBuild`=26365 WHERE (`ID`=79840 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Hum. Qui êtes-vous donc ? Êtes-vous avec les Orcs de fer ? Je me demande si votre chair grille aussi bien que la leur…', `VerifiedBuild`=26365 WHERE (`ID`=79777 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Nous autres Draeneï ne venons pas de ce monde. Notre terre se nommait Argus, mais elle fut ravagée par la Légion ardente.', `Text1_lang`='Nous autres Draeneï ne venons pas de ce monde. Notre terre se nommait Argus, mais elle fut ravagée par la Légion ardente.', `VerifiedBuild`=26365 WHERE (`ID`=79607 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Orcs sont nés pour être les maîtres des bêtes ! Les maîtres du monde !', `VerifiedBuild`=26365 WHERE (`ID`=79578 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Allez panser ceux qui sont encore en mesure de marcher ! Aucun Orc capable de tenir sur pied ne sera privé du droit de combattre.', `VerifiedBuild`=26365 WHERE (`ID`=79496 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Le clan Ombrelune et les Draeneï de Karabor ont vécu en paix pendant bien des années.$B$BMais tout a changé quand cette… Horde de Fer a débarqué…', `VerifiedBuild`=26365 WHERE (`ID`=79410 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='La Horde de Fer y réfléchira à deux fois avant de faire encore des prisonniers chez les Draeneï.', `VerifiedBuild`=26365 WHERE (`ID`=79298 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ton « Étoile noire » est tombée et sert maintenant les Draeneï. Tu es la honte de notre race !', `VerifiedBuild`=26365 WHERE (`ID`=79230 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ces Orcs se sont rendus coupables de crimes atroces contre mon peuple. Tuez-les tous.', `VerifiedBuild`=26365 WHERE (`ID`=79225 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Il est rassurant de voir que d\’anciennes alliances sont encore honorées. Les Draeneï m\’ont fait une place à leur côté.', `VerifiedBuild`=26365 WHERE (`ID`=79110 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Sont vraiment bizarres, ces Draeneï, pas vrai ?', `VerifiedBuild`=26365 WHERE (`ID`=79022 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Merci, $R. Elle se tue au travail au bout du couloir.', `VerifiedBuild`=26365 WHERE (`ID`=78998 AND `locale`='frFR');
