UPDATE `broadcast_text_locale` SET `Text_lang`='Fouettard a oublié ses bonnes manières. J\’ai vu joulie petite Orque. J\’ai voulu voir si elle savait danser. Désolé… désolé.', `VerifiedBuild`=26365 WHERE (`ID`=78392 AND `locale`='frFR');
