UPDATE `broadcast_text_locale` SET `Text_lang`='Je vais conduire ces Orcs en sûreté et je vous retrouverai dehors avec Gol\’kosh.', `VerifiedBuild`=26365 WHERE (`ID`=78142 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les cristaux d\’arkonite éparpillés dans la vallée d\’Ombrelune datent de notre arrivée catastrophique sur Draenor, il y a presque trois cents ans.$B$BLe fait qu\’ils fonctionnent toujours témoigne de l\’avancée technologique des Draeneï.', `VerifiedBuild`=26365 WHERE (`ID`=78127 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il y avait trois Ogres ainsi qu\’une Draeneï, d\’après les traces. Les survivants sont partis au nord. J\’en parlerai à Maraad quand j\’en saurai plus.', `VerifiedBuild`=26365 WHERE (`ID`=78121 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='La seule chose que j\’déteste encore plus que les Orcs, c\’est les Orcs rochenoires !', `VerifiedBuild`=26365 WHERE (`ID`=78115 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous devez être $gce fameux:cette fameuse:r; $R que Taalo nous a dit d\’attendre.', `VerifiedBuild`=26365 WHERE (`ID`=78111 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='<Cette Orque semble avoir campé ici plusieurs jours avant de succomber au froid. Le gel a figé son regard triste, tourné en direction du sud-ouest et des étendues gelées qui s\’y trouvent.$B$BElle tient une lettre soigneusement pliée contre sa poitrine.>', `VerifiedBuild`=26365 WHERE (`ID`=78073 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Nous avons déjà dû contrer des raids lancés par les Orcs, mais jamais je n\’avais fait face à une armée de cette taille.', `VerifiedBuild`=26365 WHERE (`ID`=78059 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Nos éclaireurs sont en position dans les marécages et dans les bois. Nous surveillons les Orcs ombrelunes de près.', `VerifiedBuild`=26365 WHERE (`ID`=78039 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Défendez cette barricade ! Je ne veux pas voir un Orc la franchir.', `VerifiedBuild`=26365 WHERE (`ID`=77978 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ces Gnomes… On dirait qu\’ils attachent plus de prix à leurs inventions qu\’à leur propre intégrité physique. Espérons que ce trait de caractère servira notre cause.', `VerifiedBuild`=26365 WHERE (`ID`=77914 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Nous devons activer ce cristal, mais nous ne pouvons rester les bras croisés pendant que ces Orcs construisent des armes pour attaquer Karabor.', `VerifiedBuild`=26365 WHERE (`ID`=77913 AND `locale`='frFR');
