UPDATE `broadcast_text_locale` SET `Text_lang`='Je vous demande de rester au sol et de distraire ces Orcs pendant que je cherche Romuul.', `VerifiedBuild`=26365 WHERE (`ID`=77900 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Akama, Taalo ! Montrez-leur de quel bois les Draeneï se chauffent !', `VerifiedBuild`=26365 WHERE (`ID`=77890 AND `locale`='frFR');
