UPDATE `broadcast_text_locale` SET `Text_lang`='Ça suffit ! Gardez donc votre temple, vermines de Draeneï… ce n\’est que partie remise.', `VerifiedBuild`=26365 WHERE (`ID`=77856 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Bloquez les sorties du village ! Aucun Draeneï ne doit sortir d\’ici vivant.', `VerifiedBuild`=26365 WHERE (`ID`=77844 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='<De toute évidence, ce Draeneï est mort d\’un coup de hache porté à l\’arrière du crâne. La pauvre créature n\’a sans doute rien vu venir.>', `VerifiedBuild`=26365 WHERE (`ID`=77748 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Velen nous convoque MAINTENANT ? Que peut-il y avoir de plus important que la protection de Karabor contre cette légion d\’Orcs ?', `VerifiedBuild`=26365 WHERE (`ID`=77746 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='<Toute l\’attention du jeune Draeneï est concentrée sur le combat.>', `VerifiedBuild`=26365 WHERE (`ID`=77745 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Des paroles dignes d\’un Orc ! Et voilà justement l\’occasion de faire vos preuves. Des Sire-Tonnerre débarquent !', `Text1_lang`='Des paroles dignes d\’un Orc ! Et voilà justement l\’occasion de faire vos preuves. Des Sire-Tonnerre débarquent !', `VerifiedBuild`=26365 WHERE (`ID`=77643 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='<La vieille Orque crache à nouveau. Elle semble y avoir laissé une dent au passage.>$B$BPEUH !$B$BRevenez quand vous aurez un peu plus de tripes !', `VerifiedBuild`=26365 WHERE (`ID`=77642 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='On a toujours cru qu\’Exurotus était une légende ; un conte symbolisant la nature d\’un véritable esprit élémentaire, qui se transmettait entre chamans.$b$bVoir une entité aussi vénérable asservie de la sorte est un crachat à la face des Orcs, du chamanisme, et un affront personnel.', `VerifiedBuild`=26365 WHERE (`ID`=77604 AND `locale`='frFR');
