UPDATE `broadcast_text_locale` SET `Text1_lang`='Très bien, mettons à l\’épreuve vos connaissances de l\’histoire ! Ce bateau de la Horde fut construit par les Gobelins. À l\’origine destiné à transporter Thrall et Aggra au Maelström, il fut détruit lors d\’une attaque surprise de l\’Alliance.', `VerifiedBuild`=26365 WHERE (`ID`=76220 AND `locale`='frFR');
