UPDATE `broadcast_text_locale` SET `Text1_lang`='Très bien, mettons à l\’épreuve vos connaissances de l\’histoire ! Qu\’ont donné les Aspects dragons aux Elfes de la nuit à la fin de la guerre des Anciens ?', `VerifiedBuild`=26365 WHERE (`ID`=76210 AND `locale`='frFR');
