UPDATE `broadcast_text_locale` SET `Text1_lang`='Très bien, mettons à l\’épreuve vos connaissances de l\’histoire ! La plupart des Trolls des forêts devinrent les ennemis de la Horde au cours de la Deuxième guerre. Quelle tribu de Trolls des forêts est en bons termes avec la Horde aujourd\’hui ?', `VerifiedBuild`=26365 WHERE (`ID`=76206 AND `locale`='frFR');
