UPDATE `broadcast_text_locale` SET `Text1_lang`='Très bien, mettons à l\’épreuve vos connaissances de l\’histoire ! Les premiers Orcs à peau brune commencèrent à faire leur apparition en Azeroth plusieurs années après la Troisième guerre, quand la porte des Ténèbres fut réactivée. Comment s\’appellent ces Orcs ?', `VerifiedBuild`=26365 WHERE (`ID`=76199 AND `locale`='frFR');
