UPDATE `broadcast_text_locale` SET `Text1_lang`='Vous pensez avoir gagné ? Nous sommes des Orcs ! Notre sang est pur, notre chef de guerre le plus grand qui ait jamais vécu ! Votre sang coulera dans cette baie !', `VerifiedBuild`=26365 WHERE (`ID`=75192 AND `locale`='frFR');
