UPDATE `broadcast_text_locale` SET `Text1_lang`='Il semblerait qu\’à l\’époque, les Korunes étaient au service du roi-tonnerre. C\’était il y a des milliers d\’années… avant même que les Pandarens ne viennent régner sur ces terres.', `VerifiedBuild`=26365 WHERE (`ID`=70607 AND `locale`='frFR');
