UPDATE `broadcast_text_locale` SET `Text_lang`='Mais y a une Humaine, Jana ou un truc comme ça, qui est super vénèr\’ !', `VerifiedBuild`=26365 WHERE (`ID`=70532 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Tu crois vraiment que je sais où ça se trouve, Dalaran ? C\’est à peine si je sais où sont mes pieds.$B$BMais tu sais ce que j\’ai entendu dire, $n ?$B$BOn est vraiment dans le douk depuis la dernière fois.$B$BJ\’ai appris que t\’es $gallé:allée; à Darnassus et que t\’as récupéré la Cloche divine.$B$BMais y a une Humaine, Jana ou un truc comme ça, qui est super vénèr\’ !$B$BElle dit que la Horde elle a trahi sa confiance ou un truc du genre… et elle vire tout ton peuple de sa ville !$B$BLe général orc Nazgrim, il a dit qu\’il fallait les aider à évaluer… euh non… acculer… euh… dégager de là !', `VerifiedBuild`=26365 WHERE (`ID`=70529 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Elfes de sang, ils étaient pas prêts à affronter les mogu korunes. Tout plein d\’Elfes sont morts, mais j\’ai entendu que tu en as sauvé beaucoup.', `VerifiedBuild`=26365 WHERE (`ID`=70487 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Bref, le chef des Elfes, Lor\’themachin… il a grave les boules !', `VerifiedBuild`=26365 WHERE (`ID`=70486 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='J\’ai entendu dire que plein de mimis-Elfes se sont fait couic à Mogujia.', `VerifiedBuild`=26365 WHERE (`ID`=70484 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Bref, j\’ai vu des mimis-Elfes aller vers le nord. Beaucoup d\’Elfes.', `VerifiedBuild`=26365 WHERE (`ID`=70479 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les mimis-Elfes et toi, vous avez trouvé quelque chose là bas ?', `VerifiedBuild`=26365 WHERE (`ID`=70467 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Mais, Elfe…', `VerifiedBuild`=26365 WHERE (`ID`=70196 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='… Très bien. Il est à toi, Elfe de sang.', `VerifiedBuild`=26365 WHERE (`ID`=70194 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='<Le regard de Jaina s\’emplit de tristesse.>$b$bAlors… vous feriez un grand ménage dans la ville ? Vous chasseriez les Saccage-Soleil de leur foyer ? Ces mêmes personnes qui ont fait découvrir la magie aux Humains ?$b$bVous et moi sommes de toute évidence très $gdifférents:différentes;. Je suppose que j\’envisage mon rôle, et la mission du Kirin Tor, un peu différemment. Ma décision est prise. Souhaitez-vous savoir ce que j\’en pense ?', `VerifiedBuild`=26365 WHERE (`ID`=70174 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ha… <tousse>… les races jeunes… si impatientes et irascibles. Vous, les Orcs, êtes incapables de vous contrôler, de contrôler votre rage et votre peur.', `VerifiedBuild`=26365 WHERE (`ID`=70170 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Nous ne sommes pas quelques sauvages à l\’esprit corrompu. Défier Hurlenfer, c\’est défier les plus grands Orcs de la Horde !', `VerifiedBuild`=26365 WHERE (`ID`=70023 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Avez-vous oublié ce que veut dire être un Orc ?! Nous sommes le sang de la Horde ! Pas comme ces gueux insulaires.', `Text1_lang`='Avez-vous oublié ce que veut dire être un Orc ?! Nous sommes le sang de la Horde ! Pas comme ces gueux insulaires.', `VerifiedBuild`=26365 WHERE (`ID`=70019 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ces Trolls ne peuvent pas continuer de tenir tête au chef de guerre ! Il faut s\’en occuper.', `Text1_lang`='Ces Trolls ne peuvent pas continuer de tenir tête au chef de guerre ! Il faut s\’en occuper.', `VerifiedBuild`=26365 WHERE (`ID`=70017 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Faites-moi une faveur, $p, ne racontez pas à Garrosh ce qui s\’est passé ici. Je pourrais le confronter directement, mais je me rends compte maintenant que tous les Orcs ne partagent pas mon point de vue sur la Horde.$b$bQui suis-je pour exiger leur allégeance si je dois devenir un autre dictateur à cette fin ?$b$bJ\’agirai au mieux en aidant les Sombrelances à fortifier leur foyer, pendant que leurs héros sont en guerre à l\’étranger. Quand Vol\’jin reviendra, nous déciderons de l\’étape suivante.', `VerifiedBuild`=26365 WHERE (`ID`=70011 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Orcs, c\’est pas les seuls qui saignent pour la Horde !', `Text1_lang`='Les Orcs, c\’est pas les seuls qui saignent pour la Horde !', `VerifiedBuild`=26365 WHERE (`ID`=69990 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Souhaitons aussi la bienvenue à Fracasse Martèle-Sabot, le terrible Tauren des Pitons-du-Tonnerre ! Mais visez-moi ces ÉNORMES protège-épaules !', `VerifiedBuild`=26365 WHERE (`ID`=69958 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='On a dégoté celui-ci en Silithus, où il tournait en rond en s\’ennuyant. À ce qu\’on dirait, quelqu\’un l\’a déjà bien amoché. Faites du bruit pour Ixx !', `VerifiedBuild`=26365 WHERE (`ID`=69946 AND `locale`='frFR');
