UPDATE `broadcast_text_locale` SET `Text1_lang`='Je n\’ai pas peur de vous, $R. Vous par contre, vous devriez avoir peur de moi.', `VerifiedBuild`=26365 WHERE (`ID`=67576 AND `locale`='frFR');
