UPDATE `broadcast_text_locale` SET `Text_lang`='Il y a bien longtemps, quand les Pandarens étaient les esclaves de l\’empire mogu, les armes nous étaient strictement interdites. Et nul ne pensait pouvoir affronter les mogu sans magie et sans acier.', `VerifiedBuild`=26365 WHERE (`ID`=66424 AND `locale`='frFR');
