UPDATE `broadcast_text_locale` SET `Text_lang`='Tu es un sujet de la Horde. Que tes impératifs et tes serments te reviennent en mémoire. Conformément aux ordres de notre chef de guerre, nous devons massacrer ces Elfes de la nuit.', `VerifiedBuild`=26365 WHERE (`ID`=56973 AND `locale`='frFR');
