UPDATE `broadcast_text_locale` SET `Text_lang`='Quand je n\’étais qu\’un jeune Pandaren, mes poings étaient faibles, mais les années d\’entraînement m\’ont ouvert tant de possibilités !', `VerifiedBuild`=26365 WHERE (`ID`=55830 AND `locale`='frFR');
