UPDATE `broadcast_text_locale` SET `Text_lang`='Allez trouver le Nain, Kerik, près de la forge de Theramore.\n\nC\’est un poivrot, et il a la main un peu lourde sur les explosifs, mais il est sympathique et il sait se sortir des terrains minés.', `Text1_lang`='Allez trouver le Nain, Kerik, près de la forge de Theramore.\n\nC\’est un poivrot, et il a la main un peu lourde sur les explosifs, mais il est sympathique et il sait se sortir des terrains minés.', `VerifiedBuild`=26365 WHERE (`ID`=52074 AND `locale`='frFR');
