UPDATE `broadcast_text_locale` SET `Text1_lang`='$n ! Je sais où est parti l\’autre Troll.', `VerifiedBuild`=26365 WHERE (`ID`=51735 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je suis ici pour m\’assurer que la tâche est accomplie à temps.$b$bBaie-du-Butin tente toujours de se remettre du raz-de-marée !$b$bNous n\’avons cerainement pas besoin qu\’une horde de Trolls saccageurs, de zombies, de serpents venimeux et de panthères en furie nous attaque.', `VerifiedBuild`=26365 WHERE (`ID`=51724 AND `locale`='frFR');
