UPDATE `broadcast_text_locale` SET `Text_lang`='J\’ai déjà vu des Gobelins conduire ces trucs. Je sais qu\’on peut aller plus vite que ça !', `VerifiedBuild`=26365 WHERE (`ID`=51355 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Plusieurs taches de sang séché sont visibles sur cet objet, comme si les Trolls avaient dû lutter pour se procurer cet artéfact. Il est cependant tout ce qu\’il y a de plus ordinaire et ne valait probablement pas qu\’on se batte pour lui.', `VerifiedBuild`=26365 WHERE (`ID`=51274 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Cet objet est orné de plusieurs gravures étranges, peut-être réalisées par les Trolls en tentant de découvrir son origine. Ils ne semblent cependant pas avoir rencontré grand succès, car l\’objet reste inerte.', `VerifiedBuild`=26365 WHERE (`ID`=51271 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je dois retourner au travail. Je suis un Gobelin très occupé !', `VerifiedBuild`=26365 WHERE (`ID`=51179 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je suis… un Tauren… des MERS !', `VerifiedBuild`=26365 WHERE (`ID`=51106 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Deux couteaux et une Gobeline. Que pourriez-vous demander de plus ?', `VerifiedBuild`=26365 WHERE (`ID`=51052 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Waouh, vous ne ressemblez pas du tout aux Elfes de la nuit que j\’ai déjà vus !', `VerifiedBuild`=26365 WHERE (`ID`=51011 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il y a un druide Worgen du nom de Dareth juste à l\’extérieur des portes de la ville ; il pourra peut-être vous aider.$B$BUn grand gars couvert de cuir blanc et de fourrures. Vous ne pouvez pas le manquer.', `Text1_lang`='Il y a un druide Worgen du nom de Dareth juste à l\’extérieur des portes de la ville ; il pourra peut-être vous aider.$B$BUn grand gars couvert de cuir blanc et de fourrures. Vous ne pouvez pas le manquer.', `VerifiedBuild`=26365 WHERE (`ID`=51010 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Encore un Troll ? Au moins, vous êtes pas un serpent…', `Text1_lang`='Encore un Troll ? Au moins, vous êtes pas un serpent…', `VerifiedBuild`=26365 WHERE (`ID`=50961 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Passe-moi le tournicouette, espèce de progéniture d\’Humain à cervelle de Nain ! Non, pas celui-là, l\’autre !', `VerifiedBuild`=26365 WHERE (`ID`=50844 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je peux voir ces maudits Elfes de la nuit d\’ici ! Quand je serai grand, je veux me battre pour le chef de guerre comme ces guerriers !', `VerifiedBuild`=26365 WHERE (`ID`=50830 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Sentez-vous le mal qui souille les fondations mêmes, $c ? Il a pour origine les profondeurs du fort des Ombres. Si nous ne l\’anéantissons pas, il finira par tout engloutir sur son passage.$B$B<L\’Elfe de la nuit tousse et dévoile une blessure sanglante au niveau des côtes.>', `VerifiedBuild`=26365 WHERE (`ID`=50748 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Je veux même pas en entendre parler, $n. Non, pas moyen de vous faire entrer dans la gorge des Vents brûlants. Impossible. Ça n\’arrivera pas. Combien de fois je vais devoir me répéter ? Je peux pas vous laisser entrer ! Vous ne comprenez pas le commun ? Vous êtes un Orc déguisé ou quoi ? N-O-N. NON !$B$BSi je vous laisse entrer, après je devrais laisser entrer tous les autres. Au début vos amis proches voudront en être, et puis leurs familles, et leurs amis à eux et ainsi de suite. La dernière fois que c\’est arrivé... bon, on va pas parler de la dernière fois.$b$bJe n\’ouvrirais PAS cette porte !', `VerifiedBuild`=26365 WHERE (`ID`=50744 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Humains et les peaux vertes ils nous ont pris ces terres, les terres de nos ancêtres.', `VerifiedBuild`=26365 WHERE (`ID`=50733 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Mais c\’est chez NOUS ici ! C\’est chez les ogres et les Trolls !', `VerifiedBuild`=26365 WHERE (`ID`=50732 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Beurk, l\'odeur de ces sales Humains me rend de plus en plus malade.', `Text1_lang`='Beurk, l\'odeur de ces sales Humains me rend de plus en plus malade.', `VerifiedBuild`=26365 WHERE (`ID`=50518 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Par rapport à la porte de la Herse, c\’est de l\’autre côté du pont. Guidez-vous aux cris des Gobelins.', `Text1_lang`='Par rapport à la porte de la Herse, c\’est de l\’autre côté du pont. Guidez-vous aux cris des Gobelins.', `VerifiedBuild`=26365 WHERE (`ID`=50498 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Maudits soient ces Orcs !', `VerifiedBuild`=26365 WHERE (`ID`=50377 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='C\'était l\'une des cités royales des Elfes, lorsqu\'Azshara était encore leur reine. Cette ville recèle les clés de connaissances depuis longtemps perdues et de reliques à la valeur historique inimaginable.', `VerifiedBuild`=26365 WHERE (`ID`=50313 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Ce misérable Humain s\’est échappé !', `VerifiedBuild`=26365 WHERE (`ID`=50274 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le Cercle cénarien, il est là depuis un bout de temps, mec. Nous, les Trolls, on est nouveaux ici.', `VerifiedBuild`=26365 WHERE (`ID`=50271 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous croyez que cette naine m\’aurait donné carte blanche après l\’excellent travail que j\’ai fait dans le port de Hurlevent ?$b$bJ\’essaie juste de faire monter cette statue sur la rampe pour la remettre en place aussi vite que possible, mais elle trouve à redire à chacune de mes idées. Pour elle, la solution à tous les problèmes, c\’est de les faire exploser ! J\’ai l\’impression de travailler avec un Gnome ou un Gobelin. Mais eux seraient sobres, au moins.$b$bRegardez-moi cette équipe de bras cassés sans aucune discipline. Et vous savez pourquoi il n\’y a pas de discipline ? Parce qu\’elle sape mon autorité devant les gars !$b$bUne mauvaise ambiance, un ramassis de feignants… On n\’est pas sorti de l\’auberge.', `VerifiedBuild`=26365 WHERE (`ID`=50270 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='…', `Text1_lang`='…', `VerifiedBuild`=26365 WHERE (`ID`=50215 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous avez été $gmordu:mordue; par un Worgen. Ça n\’est probablement pas grave, mais ça picote un peu.$B$B|TInterface\\Icons\\INV_Misc_monsterfang_02.blp:32|t', `VerifiedBuild`=26365 WHERE (`ID`=50192 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Les zombies, qu\’il s\’agisse de Gobelins ou non, sont une abomination aux yeux de la Lumière. De plus, ils ne représentent aucun intérêt.$B$BPas encore, en tout cas.', `VerifiedBuild`=26365 WHERE (`ID`=50054 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Les zombies, qu\’il s\’agisse de Gobelins ou non, sont une abomination aux yeux de la Lumière. De plus, ils ne représentent aucun intérêt.$B$BPas encore, en tout cas.$B$BBesoin d\’une formation, $n ?', `VerifiedBuild`=26365 WHERE (`ID`=50053 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je vous en prie, essayez de vous mettre dans la peau d\’un Orc. J\’ai eu une semaine très chargée et j\’apprécierais si vous gardiez vos commentaires pour vous.', `VerifiedBuild`=26365 WHERE (`ID`=50001 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Oui, mec, pour l\’instant. Apprendre à beaucoup d\’autres ils sont pas très chauds, mais tant qu\’on fait prospérer la vie sur les îles ils nous aident. Quand Gonk il est parti après la chute de Zalazane, il nous a dit comment l\’appeler si les autres loas ils respectent pas le marché.$B$BQue ça en arrive là, j\’espère pas... mais au cas où, les gens du Cercle cénarien, on leur parle.$B$B<Zen\’tabra ricane vivement.>$B$BOh, la tête de cet Elfe de la nuit, c\’était un truc à voir...', `VerifiedBuild`=26365 WHERE (`ID`=49884 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Utilisez votre Massacr\’herbes pour faucher ces plantes, $gpetit Gobelin:petite Gobeline; !', `VerifiedBuild`=26365 WHERE (`ID`=49846 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Kilag Sangrecroc devrait être sur le chemin de l\’ouest, $gGobelin:Gobeline;.', `VerifiedBuild`=26365 WHERE (`ID`=49843 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='On va avoir besoin de nouveaux amis si on veut survivre à tout ça. Espérons que les Orcs ne vous tueront pas à vue.', `VerifiedBuild`=26365 WHERE (`ID`=49835 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Restez attentifs, messieurs ! Il y a des Orcs et des worgs par ici !', `VerifiedBuild`=26365 WHERE (`ID`=49809 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='*hic* Toi et moi, Barbe-en-Feu. Marteaux-gnardis POUR LA VIE.', `VerifiedBuild`=26365 WHERE (`ID`=49398 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='T\’es un chic type, Barbe-en-Feu. *burp* Un vrai de vrai chic type.', `VerifiedBuild`=26365 WHERE (`ID`=49396 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Rappelle-toi $gle Gobelin:la Gobeline; que tu as été, $gmon frère:ma sœur; ! Tu étais $gmon meilleur ami:ma meilleure amie; ! Bats-toi !', `Text1_lang`='Rappelle-toi $gle Gobelin:la Gobeline; que tu as été, $gmon frère:ma sœur; ! Tu étais $gmon meilleur ami:ma meilleure amie; ! Bats-toi !', `VerifiedBuild`=26365 WHERE (`ID`=49385 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Tu ne te souviens pas de moi, $n ? Je ne sais plus combien de petits boulots on a faits ensemble, à l\’époque, à Kezan. Ensuite je suis tombé salement accro au Kaja\’Cola, et c\’est toi qui m\’as sauvé ! C\’est toi qui m\’as fait passer en Kalimdor pour rejoindre le cartel Gentepression. Tu es $gle seul Gobelin:la seule Gobeline; génér... désint... vaguement honnête que j\’aie jamais connu. C\’est pour ça que je me suis engagé dans l\’Aube d\’argent : je savais que c\’est ce que tu aurais fait. Comment ils ont pu te faire ça ?', `Text1_lang`='Tu ne te souviens pas de moi, $n ? Je ne sais plus combien de petits boulots on a faits ensemble, à l\’époque, à Kezan. Ensuite je suis tombée salement accro au Kaja\’Cola, et c\’est toi qui m\’as sauvée ! C\’est toi qui m\’as faite passer en Kalimdor pour rejoindre le cartel Gentepression. Tu es $gle seul Gobelin:la seule Gobeline; génér... désint... vaguement honnête que j\’aie jamais connu. C\’est pour ça que je me suis engagée dans l\’Aube d\’argent : je savais que c\’est ce que tu aurais fait. Comment ils ont pu te faire ça ?', `VerifiedBuild`=26365 WHERE (`ID`=49384 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Rappelle-toi $gle:la; Worgen que tu as été, $gmon frère:ma sœur; ! Tu nous avais sauvés ! Bats-toi !', `Text1_lang`='Rappelle-toi $gle:la; Worgen que tu as été, $gmon frère:ma sœur; ! Tu nous avais $gsauvés:sauvées; ! Bats-toi !', `VerifiedBuild`=26365 WHERE (`ID`=49374 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Tu ne te souviens pas de moi ? On était tous les deux au service d\’Arugal, aux Pins-Argentés. On a toléré sa torture sadique pendant des années. C\’est toi qui m\’as sauvé la nuit où on s\’est échappés du donjon d\’Ombrecroc. Sans toi, je serais mort. TOI ! $GLe:La; Worgen $gle:la; plus noble que j\’aie connu. Qu\’est-ce qu\’ils ont fait de toi, $n ? Comment ça a pu arriver ?', `Text1_lang`='Tu ne te souviens pas de moi ? On était tous les deux au service d\’Arugal, aux Pins-Argentés. On a toléré sa torture sadique pendant des années. C\’est toi qui m\’as sauvée la nuit où on s\’est $géchappés:échappées; du donjon d\’Ombrecroc. Sans toi, je serais morte. TOI ! $GLe:La; Worgen $gle:la; plus noble que j\’aie connu. Qu\’est-ce qu\’ils ont fait de toi, $n ? Comment ça a pu arriver ?', `VerifiedBuild`=26365 WHERE (`ID`=49372 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Lilian, vous réalisez que vous êtes vous-même une Morte-vivante ?', `Text1_lang`='Lilian, vous réalisez que vous êtes vous-même une Morte-vivante ?', `VerifiedBuild`=26365 WHERE (`ID`=49340 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je ne suis pas une abomination, juste un Mort-vivant. Et je veux simplement vous parler.', `Text1_lang`='Je ne suis pas une abomination, juste une Morte-vivante. Et je veux simplement vous parler.', `VerifiedBuild`=26365 WHERE (`ID`=49339 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Hé !$B$BVous voilà enfin, c\’est pas dommage.$B$BÇa fait des jours que j\’vous attends.$B$BLaisser un vieux Nain mariner... J\’ai pas éduqué junior comme ça.', `VerifiedBuild`=26365 WHERE (`ID`=49289 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Mesdames et Mesnains, nous sommes ici assemblés dans l\’ombre de Frappe-tonnerre pour célébrer l\’union de Fanny Tonnemar et Keegan Barbe-en-Feu.', `VerifiedBuild`=26365 WHERE (`ID`=49209 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='C\'est un grand jour, Barbe-en-Feu. Rien ne pourra arrêter les Marteaux-hardis réunis !', `VerifiedBuild`=26365 WHERE (`ID`=49199 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Qu\’est-ce que tu as dit, Sally ?! La seule d\’entre nous qui ressemble à un Gnome, c\’est toi. Surtout quand j\’aurai terminé de te couper en morceaux !', `VerifiedBuild`=26365 WHERE (`ID`=49160 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Sales Marteaux-hardis puants. Tu sais qu\’ils ne te considèrent qu\’à moitié comme un Nain, hein ? Et ça fait quoi de toi, ça ? Un Gnome ?', `Text1_lang`='Sales Marteaux-hardis puants. Tu sais qu\’ils ne te considèrent qu\’à moitié comme un Nain, hein ? Et ça fait quoi de toi, ça ? Un Gnome ?', `VerifiedBuild`=26365 WHERE (`ID`=49159 AND `locale`='frFR');
