UPDATE `broadcast_text_locale` SET `Text_lang`='Je n\’arrive pas à croire au toupet de ces Trolls. Se rebeller, après tout ce que j\’ai fait pour eux !', `VerifiedBuild`=26365 WHERE (`ID`=49038 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Faites bien attention à vous, $gmon p\’tit gars:gamine;. Entre le worg en bas et les Orcs qui rôdent dans la passe, $gun:une:c; $c pourrait trouver le moyen de se faire tuer.', `VerifiedBuild`=26365 WHERE (`ID`=48959 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Pour la Fanny, hé ho, et Barbe-en-Feu l\’bougon !', `VerifiedBuild`=26365 WHERE (`ID`=48933 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Là vient le Barbe-en-Feu qui fait tout un fracas', `VerifiedBuild`=26365 WHERE (`ID`=48926 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Mais le grand Barbe-en-Feu vint lui faire une promesse', `VerifiedBuild`=26365 WHERE (`ID`=48915 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ah, je commence à voir quel genre |2 $c vous êtes, $n, hein ?! Et donc, le deuxième couplet raconte comment Fanny et Barbe-en-Feu se sont mis ensemble.$B$BVous avez une rime pour « promesse » ?', `VerifiedBuild`=26365 WHERE (`ID`=48875 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Orcs de Rochenoire sont ma famille. Mon père était un Rochenoire, comme son père avant lui. Ma mère était une Rochenoire. Et j\’en suis un aussi.$B$BMais avant tout, je suis un Orc. Et cela signifie : l\’honneur avant tout.$B$BMon général m\’a autorisé à rester pour terminer ma mission ici dans les steppes Ardentes. Et je ne partirai pas avant d\’avoir montré ses erreurs au clan Rochenoire.', `VerifiedBuild`=26365 WHERE (`ID`=48849 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Cette fois il n\’y aura aucune issue, Orc ! Tu as crié ton dernier lok\’tar !', `VerifiedBuild`=26365 WHERE (`ID`=48788 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Elle déménage du feu de bière, celle-là. Elle ferait une belle Barbe-en-Feu. Pensez qu\’les Tonnemar seraient d\’accord ?', `VerifiedBuild`=26365 WHERE (`ID`=48767 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Faudrait être $gexalté:exaltée; par les Gobelins du cartel Baille-Fonds avant que je vous apprenne à monter.', `Text1_lang`='Faudrait être $gexalté:exaltée; par les Gobelins du cartel Baille-Fonds avant que je vous apprenne à monter.', `VerifiedBuild`=26365 WHERE (`ID`=48749 AND `locale`='frFR');
