UPDATE `broadcast_text_locale` SET `Text_lang`='Genn, l\’attention des Worgens doit rester braquée sur la ville. C\’est le seul moyen de permettre aux survivants d\’arriver à Havre-du-Soir.', `VerifiedBuild`=26365 WHERE (`ID`=36103 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Bien sûr que je vais partager mes connaissances avec vous. Surtout si cela peut nous aider à vaincre les Worgens.', `VerifiedBuild`=26365 WHERE (`ID`=36068 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Surtout, pas de bruit. Il y a des Worgens partout.', `VerifiedBuild`=26365 WHERE (`ID`=36066 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='D\’abord les Orcs, et ensuite, ces Gobelins pathétiques près du rivage.', `Text1_lang`='D\’abord les Orcs, et ensuite, ces Gobelins pathétiques près du rivage.', `VerifiedBuild`=26365 WHERE (`ID`=36063 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le dernier groupe d\’Orcs. Ils devraient tomber rapidement !', `Text1_lang`='Le dernier groupe d\’Orcs. Ils devraient tomber rapidement !', `VerifiedBuild`=26365 WHERE (`ID`=36061 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Les Worgens disparaîtront. Tout comme nous.$B$BSeuls la Terre et ses secrets subsiteront. Souhaitez-vous apprendre, $n ?', `VerifiedBuild`=26365 WHERE (`ID`=36059 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Nous avons besoin de vous, $n ! Il y a des Worgens partout !', `VerifiedBuild`=26365 WHERE (`ID`=35965 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je vous jure que vous êtes le plus mauvais lot d\’ouvriers que nous ayons reçus jusqu\’ici, les Trolls !', `VerifiedBuild`=26365 WHERE (`ID`=35909 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Misérable Gnome ! Ton arrogance te perdra !', `VerifiedBuild`=26365 WHERE (`ID`=35730 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Paroles d\’imbécile, vous voulez dire. La Horde n\’a pas besoin de votre aide pour anéantir les Morts-vivants, Humain. Ni de celle de ce roi vaniteux.', `VerifiedBuild`=26365 WHERE (`ID`=35556 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Peuh ! Ça, c\’était une démonstration de force digne d\’un vrai chef. Mon seul regret est de ne pas avoir tué l\’Humain avant l\’intervention du mage.', `VerifiedBuild`=26365 WHERE (`ID`=35553 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Attirons-en autant que nous le pourrons. Chaque Worgen qui nous pourchasse est un Worgen de moins sur le dos des rescapés !', `VerifiedBuild`=26365 WHERE (`ID`=35498 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Merci encore pour votre aide avec ces crétins paresseux, boss.$B$BSi vous voulez mon avis, nous n\’aurions pas dû être aussi indulgents avec les Trolls lorsque nous nous sommes libérés de leur joug.', `VerifiedBuild`=26365 WHERE (`ID`=35482 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Admirablement ? Ha ! C\'est avec plaisir que je vais regarder vos minables petits champions échouer, Humain.', `VerifiedBuild`=26365 WHERE (`ID`=35324 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Sous votre direction, les mines battent des records de rendement, malgré les vers et ces feignants de Trolls.', `VerifiedBuild`=26365 WHERE (`ID`=35300 AND `locale`='frFR');
