UPDATE `broadcast_text_locale` SET `Text_lang`='Ce n\'est pas comme ça qu\'un Orc se bat ! Mon devoir d\'obéissance au chef de guerre s\'oppose à mon honneur personnel. Votre Lumière n\'est rien pour moi, mais, comme vous semblez posséder une certaine sagesse, j\'aimerais connaître votre réponse.', `Text1_lang`='Ce n\'est pas comme ça qu\'un Orc se bat ! Mon devoir d\'obéissance au chef de guerre s\'oppose à mon honneur personnel. Votre Lumière n\'est rien pour moi, mais, comme vous semblez posséder une certaine sagesse, j\'aimerais connaître votre réponse.', `VerifiedBuild`=26365 WHERE (`ID`=34272 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Ils ont été fous de nous croire affaiblis à la lumière des évènements récents. Il n\’y a plus aucun espoir pour ces Trolls : tout ça va se terminer aujourd\’hui.', `VerifiedBuild`=26365 WHERE (`ID`=34221 AND `locale`='frFR');
