UPDATE `broadcast_text_locale` SET `Text_lang`='Mais combien de fois est-ce qu\’il va falloir que je vous tue, Orc ?!', `VerifiedBuild`=26365 WHERE (`ID`=33455 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Une potion pour faire fondre les Elfes ? Peut-être qu\’à vous, il vous faut ça, Réprouvé. Moi, j\’ai ma masse pour leur fendre le crâne !', `Text1_lang`='Une potion pour faire fondre les Elfes ? Peut-être qu\’à vous, il vous faut ça, Réprouvé. Moi, j\’ai ma masse pour leur fendre le crâne !', `VerifiedBuild`=26365 WHERE (`ID`=33318 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='<Lorsque vous vous approchez, l\’Elfe de la nuit se tourne vers vous et vous prend la main.>\n\nIl est trop tard pour moi. Notre orgueil nous a conduits à un destin mérité. Allez… d\’autres s\’en sont peut-être sortis. Sauvez ceux qui peuvent l\’être. \n\n<Elle vous lâche et la vie la quitte.>', `Text1_lang`='<Lorsque vous vous approchez, l\’Elfe de la nuit se tourne vers vous et vous prend la main.>\n\nIl est trop tard pour moi. Notre orgueil nous a conduits à un destin mérité. Allez… d\’autres s\’en sont peut-être sortis. Sauvez ceux qui peuvent l\’être. \n\n<Elle vous lâche et la vie la quitte.>', `VerifiedBuild`=26365 WHERE (`ID`=33161 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Orcs ont un cri de guerre : « LOK\’TAR OGAR » ! Ça veut dire : «La victoire ou la mort ». Voilà qui convient parfaitement…', `VerifiedBuild`=26365 WHERE (`ID`=32970 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je sais quelle malfaisance règne dans le cœur des Orcs.', `VerifiedBuild`=26365 WHERE (`ID`=32966 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Regarde-nous, Elfe. Chômeurs, indigents, et plantés là à boire de la piquette toute la journée dans un bar oublié au fond des égouts et qui a le nom d\’un charognard !', `VerifiedBuild`=26365 WHERE (`ID`=32877 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Hé, vous là, attendez une seconde. Je vois que vous n\’avez pas votre propre monture volante.$B$BVous n\’allez probablement pas servir à grand-chose aux autres Gobelins si vous ne pouvez pas vous déplacer correctement. Ils m\’ont payé pour prêter mes montures de seconde main aux gens comme vous - et gratuitement !$B$BÇa vous intéresse ?', `VerifiedBuild`=26365 WHERE (`ID`=32563 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='En avant pour la gloire, Orcs !', `Text1_lang`='En avant pour la gloire, Orcs !', `VerifiedBuild`=26365 WHERE (`ID`=32265 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il y a une banque tenue par les Gobelins ici-bas, juste au nord du caboulot des Cabales et corbeaux, dans l\’ouest des égouts.', `Text1_lang`='Il y a une banque tenue par les Gobelins ici-bas, juste au nord du caboulot des Cabales et corbeaux, dans l\’ouest des égouts.', `VerifiedBuild`=26365 WHERE (`ID`=32206 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les travaux sur les lignes telluriques continuent, avec des résultats satisfaisants. Nous avons eu la surprise de découvrir des similarités de structure entre les lignes de Draenor et celles d\’Azeroth. Serait-ce le signe d\’une origine commune ?\n\nMon associé Naberius me rapporte que les Orcs du Crâne-Ricanant ont passé une trêve avec les forces de l\’Alliance de la région. Toujours méfiant envers les Orcs, j\’ai pensé : « Ce sera soit paradisiaque, soit infernal. »', `Text1_lang`='Les travaux sur les lignes telluriques continuent, avec des résultats satisfaisants. Nous avons eu la surprise de découvrir des similarités de structure entre les lignes de Draenor et celles d\’Azeroth. Serait-ce le signe d\’une origine commune ?\n\nMon associé Naberius me rapporte que les Orcs du Crâne-Ricanant ont passé une trêve avec les forces de l\’Alliance de la région. Toujours méfiant envers les Orcs, j\’ai pensé : « Ce sera soit paradisiaque, soit infernal. »', `VerifiedBuild`=26365 WHERE (`ID`=31449 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Tout d\’abord, laissez-moi me présenter. Je suis l\’archimage Vargoth, dirigeant de la première expédition du Kirin Tor sur Draenor. Nous avons accompagné les troupes de l\’Alliance de l\’autre côté de la Porte des ténèbres, et avons maintenant installé notre camp de base, Kirin\’Var, à Farahlon, près de la demeure ancestrale des Orcs du clan du Crâne-Ricanant. Quel endroit merveilleux !', `Text1_lang`='Tout d\’abord, laissez-moi me présenter. Je suis l\’archimage Vargoth, dirigeante de la première expédition du Kirin Tor sur Draenor. Nous avons accompagné les troupes de l\’Alliance de l\’autre côté de la Porte des ténèbres, et avons maintenant installé notre camp de base, Kirin\’Var, à Farahlon, près de la demeure ancestrale des Orcs du clan du Crâne-Ricanant. Quel endroit merveilleux !', `VerifiedBuild`=26365 WHERE (`ID`=31444 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ce jour, il est sombre dans l\'histoire des Trolls, mec.', `VerifiedBuild`=26365 WHERE (`ID`=31382 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ne vous embêtez pas, petit Tauren. Ni Sabot-Tempête, ni vous et votre $gallié:alliée; ne pourrez vous dresser sur mon chemin.', `VerifiedBuild`=26365 WHERE (`ID`=31260 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Monseigneur, ce Draeneï a été capturé en même temps qu\’un contigent entier de chevaliers de la mort au caveau des Ombres. Ce sont des chevaliers de la Lame d\’ébène !', `VerifiedBuild`=26365 WHERE (`ID`=31186 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Pas beaucoup, Magni. J\’ai fait des cauchemars… Un Humain, grand… les cheveux clairs… une armure noire, du noir de la mort. J\’ai son nom sur le bout de la langue, mais…', `VerifiedBuild`=26365 WHERE (`ID`=31089 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Certaines blessures ne cicatrisent jamais, noble Nain. Une âme brisée ne peut être réparée.', `VerifiedBuild`=26365 WHERE (`ID`=31077 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Salut ! Je déteste vous presser, mais il y a une armée de Nains de fer qui veulent mettre un terme à ce que Creteus et vous êtes en train de faire... Nous devons sortir d\’ici.', `VerifiedBuild`=26365 WHERE (`ID`=30987 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le jormungar s\’écroula instantanément, pris de convulsions, sa tête quasiment coupée de son corps. Mon père se tourna vers le Nain avec admiration et leva le poing en signe de compliment...', `VerifiedBuild`=26365 WHERE (`ID`=30950 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le Nain se projeta vers l\’avant avec un bruit semblable à un coup de tonnerre et lança un marteau à la surface métallique brillante parcourue d\’éclairs directement dans la gorge du jormungar.', `VerifiedBuild`=26365 WHERE (`ID`=30949 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Mon père pensait qu\’ils étaient tous condamnés... Mais, de derrière lui, un furieux rugissement roula sur la neige. Il se retourna et vit le Nain des montagnes grandir, sa peau prendre la texture de la pierre, et ses mains crépiter d\’éclairs.', `VerifiedBuild`=26365 WHERE (`ID`=30948 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le Nain semblait perdu, il n\’avait aucun souvenir d\’où il venait ou même de son nom. N\’étant pas du genre à laisser un cousin nain mourir dans la neige, le groupe de mon père l\’emmena et continua en direction de fort du Givre.', `VerifiedBuild`=26365 WHERE (`ID`=30946 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ils ont croisé une piste de sang tracée dans la neige et l\’ont suivie jusqu\’à trouver un Nain errant et parlant un dialecte qu\’ils ne comprenaient pas... Et figurez-vous que ce n\’était pas un nain comme nous, mais un nain des montagnes - ce que notre espèce n\’avait jamais vu auparavant.', `VerifiedBuild`=26365 WHERE (`ID`=30945 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je comprends les attentes de Garrosh. L\’attrait du combat est profond en chaque Orc, et surtout contre un ennemi qui a versé le sang de notre peuple. Mais le chef de guerre a déjà maintes fois prouvé que la stratégie est aussi importante que la force.$B$BNous nous mettrons en marche quand le temps sera venu. Nous ne sommes plus une meute de sauvages décérébrés guidés par le démon. Nous sommes la nouvelle Horde.', `VerifiedBuild`=26365 WHERE (`ID`=30927 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je n\’ai jamais cru Thrall, jusqu\’à ce que je le voie de mes propres yeux. Les Orcs… libérés de la corruption démoniaque ! De fiers et honorables guerriers qui se taillent une place sur un monde encore intact.$B$BImpatient, je le suis; oui. De faire mes preuves à Thrall et sa Horde. Mon père n\’a jamais eu à affronter le péril mort-vivant que vous appelez le roi-liche, mais, de tout ce que j\’en entends, il constitue une redoutable menace pour la Horde.$B$BJe vais supplier Thrall de me laisser mener ses plus grands guerriers en Norfendre pour détruire le roi-liche ! Mais cette peste qui arrive est la priorité.', `VerifiedBuild`=26365 WHERE (`ID`=30925 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il allait bien falloir nous occuper du roi-liche, ce n\’était qu\’une question de temps. C\’est tout Azeroth qu\’il menace, pas seulement les Humains.$B$BMais la décision de partir en guerre ne se prend pas à la légère, nous précipiter en Norfendre serait une folie. Le roi-liche a sûrement anticipé une telle manœuvre.$B$BIl faut bien peser les différents choix qui s\’offrent à nous. Nos armées sont plus puissantes que jamais, mais une mauvaise décision tactique pourrait être notre ruine.$B$BSi seulement Garrosh le comprenait...', `VerifiedBuild`=26365 WHERE (`ID`=30921 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le champion des Trolls haches-d\’hiver vous défie, $n ! J\’espère que vous êtes $gprêt:prête; !', `VerifiedBuild`=26365 WHERE (`ID`=30714 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Un Drakkari, un pion de leur soi-disant Assaut écarlate, un Gnome, une vierge guerrière hyldnir, et maintenant vous ? C\’est une blague ?', `VerifiedBuild`=26365 WHERE (`ID`=30697 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='C\'est moi qui prend les morceaux pour les Gobelins… Arrête de tout voler !', `VerifiedBuild`=26365 WHERE (`ID`=30515 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Regardez autour de vous, digérez bien tout cette dévastation, $c. Il n\’y a pas un des Trolls qui vivaient ici qui ne soit mort ou en fuite dans les niveaux supérieurs.$B$BEt ce n\’est pas le pire.$B$BDans un geste désespéré, les Trolls drakkari encore vivants ont sacrifié leurs propres dieux animaux pour pouvoir en boire le sang et y gagner une puissance incommensurable. Même si cette nouvelle force leur a permis de repousser le Fléau, les conséquences en sont catastrophiques. Les ruines de l\’antique empire de Zul\’Drak en sont bien la preuve.', `VerifiedBuild`=26365 WHERE (`ID`=30433 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Trolls, c\’est caca !', `VerifiedBuild`=26365 WHERE (`ID`=30398 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Trolls c\’est plus d\’honneur. Les Trolls c\’est des assassins !', `VerifiedBuild`=26365 WHERE (`ID`=30343 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Trolls sont plus les copains de Grifferage !', `VerifiedBuild`=26365 WHERE (`ID`=30338 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Faut partir maintenant, méchant Troll !', `VerifiedBuild`=26365 WHERE (`ID`=30336 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Comment le Gobelin a fait pour laisser ses outils là-bas, alors ?', `VerifiedBuild`=26365 WHERE (`ID`=30295 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ces chevaliers de la mort n\'ont vraiment aucun honneur. D\'abord, ils me prennent mes plus belles armes et armures, travaillées avec la plus grande attention, forgées avec la bénédiction du roi-liche. Ensuite ils partent se promener on ne sait où et reviennent habillés comme des Gobelins en murmurant qu\'ils veulent mettre leur armure au clou pour quelques pièces de cuivre ! C\'est pitoyable !', `VerifiedBuild`=26365 WHERE (`ID`=30189 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Si seulement je pouvais organiser un ravitaillement du Norfendre, alors je pourrais peut-être forger une arme qui glacerait le cœur des Morts-vivants.', `VerifiedBuild`=26365 WHERE (`ID`=30188 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les âmes des Morts-vivants sont maudites et torturées. Si je suis infecté, il faut me promettre de me brûler avant ma transformation…', `Text1_lang`='Les âmes des Morts-vivants sont maudites et torturées. Si je suis infectée, il faut me promettre de me brûler avant ma transformation…', `VerifiedBuild`=26365 WHERE (`ID`=30112 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Alors, Troll, tu as réfléchi à ma proposition ? Quelle que soit ta décision, je suis sûr que Cuiracier en sera tout content.', `VerifiedBuild`=26365 WHERE (`ID`=30109 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='J\’ai une idée, Troll. Et si tu me disais ce que tu sais sur Drak\’Tharon, et qu\’ensuite j\’offrais un petit en-cas à Cuiracier ? Tu en penses quoi ?', `VerifiedBuild`=26365 WHERE (`ID`=30108 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le temps passe, Troll. Si jamais tu as envie de me parler de Drak\’Tharon, préviens-moi.', `VerifiedBuild`=26365 WHERE (`ID`=30106 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Une cage vide, et un Troll de moins dans la fosse. Alors, les Trolls, est-ce qu\’il y en a encore qui ne sont pas convaincus ?', `VerifiedBuild`=26365 WHERE (`ID`=30103 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Si les Elfes de sang vont en Norfendre, alors l\’heure de notre vengeance aura enfin sonné ! Le roi-liche regrettera d\’avoir marché sur notre chère Lune-d\’Argent !', `Text1_lang`='Si les Elfes de sang vont en Norfendre, alors l\’heure de notre vengeance aura enfin sonné ! Le roi-liche regrettera d\’avoir marché sur notre chère Lune-d\’Argent !', `VerifiedBuild`=26365 WHERE (`ID`=30093 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il paraît que les Taurens ont des cousins en Norfendre. J\’espère que ce sont de bons chasseurs… et qu\’ils chassent le mort-vivant.', `Text1_lang`='Il paraît que les Taurens ont des cousins en Norfendre. J\’espère que ce sont de bons chasseurs… et qu\’ils chassent le mort-vivant.', `VerifiedBuild`=26365 WHERE (`ID`=30092 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Que cette musique est douce à mes oreilles. Criez pour moi, Humain.', `VerifiedBuild`=26365 WHERE (`ID`=29656 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Bonjour, $gl\'ami:l\'amie;, et bienvenue dans notre salon de coiffure ! Vous venez pour une coupe ? Une mise en plis ? Une couleur ? Autre chose, peut-être ? Nous vous proposons : barbe et pilosité faciale, ajout de piercings, retaille des cornes et défenses, et même le remodelage des traits des Morts-vivants ! C\'est-y pas chou ? $B$BAsseyez-vous, nous allons nous occuper de vous tout de suite. Pour quelques petites pièces, c\'est $gun nouveau:une nouvelle; vous qui sortira d\'ici, encore plus chou qu\'avant...', `Text1_lang`='Bonjour, $gl\'ami:l\'amie;, et bienvenue dans notre salon de coiffure ! Vous venez pour une coupe ? Une mise en plis ? Une couleur ? Autre chose, peut-être ? Nous vous proposons : barbe et pilosité faciale, ajout de piercings, retaille des cornes et défenses, et même le remodelage des traits des Morts-vivants ! C\'est-y pas chou ? $B$BAsseyez-vous, nous allons nous occuper de vous tout de suite. Pour quelques petites pièces, c\'est $gun nouveau:une nouvelle; vous qui sortira d\'ici, encore plus chou qu\'avant...', `VerifiedBuild`=26365 WHERE (`ID`=29498 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Bonjour, $gl\'ami:l\'amie;, et bienvenue dans notre salon de coiffure ! Vous venez pour une coupe ? Une mise en plis ? Une couleur ? Autre chose, peut-être ? Nous vous proposons : barbe et pilosité faciale, ajout de piercings, encrage des tatouages elfiques, et même la retaille des cornes et tentacules des Draeneï !$B$BAsseyez-vous, nous allons nous occuper de vous tout de suite. Pour quelques petites pièces, c\'est $gun nouveau:une nouvelle; vous qui sortira d\'ici, encore plus $gséduisant:séduisante; qu\'avant...', `Text1_lang`='Bonjour, $gl\'ami:l\'amie;, et bienvenue dans notre salon de coiffure ! Vous venez pour une coupe ? Une mise en plis ? Une couleur ? Autre chose, peut-être ? Nous vous proposons : barbe et pilosité faciale, ajout de piercings, encrage des tatouages elfiques, et même la retaille des cornes et tentacules des Draeneï !$B$BAsseyez-vous, nous allons nous occuper de vous tout de suite. Pour quelques petites pièces, c\'est $gun nouveau:une nouvelle; vous qui sortira d\'ici, encore plus $gséduisant:séduisante; qu\'avant...', `VerifiedBuild`=26365 WHERE (`ID`=29495 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Hé, $n, faut réfléchir. Faut penser au passé, mec. Nous, on est des Sombrelances, mec ! Comme nos frangins et nos frangines ! Faut vous rappeler quand Zalazane, on l\’a combattu et on lui a pris sa tête et on a libéré les îles de l\’Écho. Mais ouais, MEC ! Les SOUVENIRS, mec ! Vous, z\’étiez $gun champion:une championne; des Trolls sombrelances !', `Text1_lang`='Hé, $n, faut réfléchir. Faut penser au passé, mec. Nous, on est des Sombrelances, mec ! Comme nos frangins et nos frangines ! Faut vous rappeler quand Zalazane, on l\’a combattu et on lui a pris sa tête et on a libéré les îles de l\’Écho. Mais ouais, MEC ! Les SOUVENIRS, mec ! Vous, z\’étiez $gun champion:une championne; des Trolls sombrelances !', `VerifiedBuild`=26365 WHERE (`ID`=29408 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Moi, vous m\’avez oublié, hein, mec ? L\’Fléau, qu\’il soit maudit… Tout c\’qui faisait de vous un cador du mojo, ils ont voulu vous l\’enlever. Chaque petit bout d\’ bien qu\’y avait en vous, jusqu\’au dernier… Tout ce qui faisait de vous $gun héros:une héroïne; des Trolls !', `Text1_lang`='Moi, vous m\’avez oubliée, hein, mec ? L\’Fléau, qu\’il soit maudit… Tout c\’qui faisait de vous un cador du mojo, ils ont voulu vous l\’enlever. Chaque petit bout d\’ bien qu\’y avait en vous, jusqu\’au la dernier… Tout ce qui faisait de vous $gun héros:une héroïne; des Trolls !', `VerifiedBuild`=26365 WHERE (`ID`=29407 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous ne vous souvenez pas de moi ? Il y a longtemps, très, très longtemps, nous étions Humains, jusqu\’à ce que Lordaeron tombe aux mains du Fléau. Votre transformation en zombie eut lieu peu après la mienne. Puis nos esprits furent libérés par la Dame noire.', `Text1_lang`='Vous ne vous souvenez pas de moi ? Il y a longtemps, très, très longtemps, nous étions Humains, jusqu\’à ce que Lordaeron tombe aux mains du Fléau. Votre transformation en zombie eut lieu peu après la mienne. Puis nos esprits furent libérés par la Dame noire.', `VerifiedBuild`=26365 WHERE (`ID`=29397 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous ne vous souvenez pas de moi, hein ? Maudit Fléau… Ils ont voulu vous enlever tout ce qui faisait de vous une valeureuse force de jugement. Chaque parcelle de bien en vous, jusqu\’à la dernière… Tout ce qui faisait de vous $gun Orc:une Orque; !', `Text1_lang`='Vous ne vous souvenez pas de moi, hein ? Maudit Fléau… Ils ont voulu vous enlever tout ce qui faisait de vous une valeureuse force de jugement. Chaque parcelle de bien en vous, jusqu\’à la dernière… Tout ce qui faisait de vous $gun Orc:une Orque; !', `VerifiedBuild`=26365 WHERE (`ID`=29387 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Je veux mourir comme une Orque…', `VerifiedBuild`=26365 WHERE (`ID`=29384 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous ne vous souvenez pas de moi, hein ? Maudit Fléau… Ils ont voulu vous enlever tout ce qui faisait de vous une valeureuse force de jugement. Chaque parcelle de bien en vous, jusqu\’à la dernière… Tout ce qui faisait de vous $gun:une; Draeneï !', `VerifiedBuild`=26365 WHERE (`ID`=29361 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous ne vous souvenez pas de moi, hein ? Maudit Fléau… Ils ont voulu vous enlever tout ce qui faisait de vous une minuscule force de jugement. Chaque parcelle de bien en vous, jusqu\’à la dernière… Tout ce qui faisait de vous $gun:une; Gnome !', `Text1_lang`='Vous ne vous souvenez pas de moi, hein ? Maudit Fléau… Ils ont voulu vous enlever tout ce qui faisait de vous une minuscule force de jugement. Chaque parcelle de bien en vous, jusqu\’à la dernière… Tout ce qui faisait de vous $gun:une; Gnome !', `VerifiedBuild`=26365 WHERE (`ID`=29351 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Des Worgens partout ! N\’abandonnez pas, je suis derrière vous !', `VerifiedBuild`=26365 WHERE (`ID`=29321 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous ne vous souvenez pas de moi, hein ? Maudit Fléau… Ils ont voulu vous enlever tout ce qui faisait de vous une valeureuse force de jugement. Chaque parcelle de bien en vous, jusqu\’à la dernière… Tout ce qui faisait de vous $gun Humain:une Humaine; !', `Text1_lang`='Vous ne vous souvenez pas de moi, hein ? Maudit Fléau… Ils ont voulu vous enlever tout ce qui faisait de vous une valeureuse force de jugement. Chaque parcelle de bien en vous, jusqu\’à la dernière… Tout ce qui faisait de vous $gun Humain:une Humaine; !', `VerifiedBuild`=26365 WHERE (`ID`=29314 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous ne vous souvenez pas de moi, hein ? Maudit Fléau… Ils ont voulu vous enlever tout ce qui faisait de vous une valeureuse force de jugement. Chaque parcelle de bien en vous, jusqu\’à la dernière… Tout ce qui faisait de vous $gun Tauren:une Taurène; !', `VerifiedBuild`=26365 WHERE (`ID`=29290 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Quoi, Orbaz ? Que c\’est un Elfe de sang ?', `VerifiedBuild`=26365 WHERE (`ID`=29192 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ces Trolls font partie de ceux que vous avez démasqués sur le champ de bataille. Un coup de maître, vraiment…', `VerifiedBuild`=26365 WHERE (`ID`=29176 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ce petit jeu commence à me fatiguer, Humain. Parle-moi de « l\'Aube cramoisie » ou je t\'arrache la colonne vertébrale de mes propres mains !', `Text1_lang`='Ce petit jeu commence à me fatiguer, Humain. Parle-moi de « l\'Aube cramoisie » ou je t\'arrache la colonne vertébrale de mes propres mains !', `VerifiedBuild`=26365 WHERE (`ID`=29140 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je commence à me fatiguer de rester courtois avec toi, Humain ! Parle-moi de cette « Aube cramoisie » !', `Text1_lang`='Je commence à me fatiguer de rester courtoise avec toi, Humain ! Parle-moi de cette « Aube cramoisie » !', `VerifiedBuild`=26365 WHERE (`ID`=29135 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='C\'était une Naine. Un paladin. Elle a dépensé tout son mana à soigner ses compagnons, avant de succomber à sa propre infection.', `Text1_lang`='C\'était une Naine. Un paladin. Elle a dépensé tout son mana à soigner ses compagnons, avant de succomber à sa propre infection.', `VerifiedBuild`=26365 WHERE (`ID`=28690 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Ce spécimen était un Humain mâle. Un fermier. Il a tué toute sa famille, puis est entré dans Hurlevent.', `VerifiedBuild`=26365 WHERE (`ID`=28680 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Nous construisons une nouvelle route vers le port de Hurlevent. Enfin, dès que ce mur aura été détruit… mais cette cinglée de Naine a apporté assez de poudre pour raser le quartier entier !$B$BJe ne sais pas ce qui a pris au maître Alexston de l\’engager... Elle a mauvais caractère, elle jure comme une charretière et elle est têtue comme une mule !', `VerifiedBuild`=26365 WHERE (`ID`=28566 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Tiens, Troll… un cadeau !', `VerifiedBuild`=26365 WHERE (`ID`=28505 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Aïee ! Je ne sens pas le moindre Troll dans les environs…', `VerifiedBuild`=26365 WHERE (`ID`=28452 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je croyais que nos terres étaient à l\’abri des Morts-vivants ?', `VerifiedBuild`=26365 WHERE (`ID`=28378 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='T\’en penses quoi, pour de vrai, de d\’voir travailler avec des Orcs ?', `VerifiedBuild`=26365 WHERE (`ID`=27854 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Mmm, ça me va, le Troll. Ça pourra pas être pire que le grog qu\’ils servent par ici…', `VerifiedBuild`=26365 WHERE (`ID`=27845 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='M\’oblige pas à te secouer les puces, le Troll.', `VerifiedBuild`=26365 WHERE (`ID`=27844 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Allez, l\'Troll. Gobe-moi ça !', `VerifiedBuild`=26365 WHERE (`ID`=27843 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Un véritable chef-d\’œuvre ! Donnez ça au Troll, Hordum. Il répondra sincèrement à toutes vos questions…', `VerifiedBuild`=26365 WHERE (`ID`=27841 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Voir ces cinglés de Trolls drakkari abattre une nécropole du Fléau droit dans notre jardin, c\’est vraiment la dernière chose qu\’il nous fallait.$B$BMais comment est-ce qu\’ils ont fait leur affaire ?', `VerifiedBuild`=26365 WHERE (`ID`=27714 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Qu\’est-ce que vous regardez comme ça, de là-haut ?$B$BJe vous ferai dire que les Potpilon sont une famille prestigieuse et maîtres de l\’art du pilonnage depuis des générations.$B$BOui, le pilonnage, parfaitement !$B$BIl n\’y a pas d\’alchimiste plus magistral que les Potpilon dans tout Azeroth, ni n\’importe où ailleurs !$B$BPour l\’instant, je ne porte peut-être que le titre d\’apprenti, mais mon talent n\’a pas d\’égal ! Il faut bien passer par ce genre de basse besogne pour arriver quelque part dans la vie.$B$BBien sûr, il se peut que vous soyez juste là pour m\’aider ?$B$B<Le minuscule Gnome sourit avec arrogance.>', `VerifiedBuild`=26365 WHERE (`ID`=27592 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Attention, $c. Ces Trolls ont tué leur propre dieu serpent !', `Text1_lang`='Attention, $c. Ces Trolls ont tué leur propre dieu serpent !', `VerifiedBuild`=26365 WHERE (`ID`=27449 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je suis Ubungo. Je représente la tribu zandalari.$B$BIl se trame quelque chose de mauvais, $r. Les Drakkari foncent vers leur propre perte avec ce qu\’ils préparent.$B$BIl y a des Trolls qui n\’apprennent jamais.', `VerifiedBuild`=26365 WHERE (`ID`=27441 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Si j\’étais un Gobelin honnête, j\’avouerais que c\’est la meilleure bière de toute la fête.', `VerifiedBuild`=26365 WHERE (`ID`=27437 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='… le Troll flaellesh craque dans le feush…', `VerifiedBuild`=26365 WHERE (`ID`=27291 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Hé, mec !$B$BChuis content d\’vous revoir, … $n, c\’est ça ? Ouais, ouais, c\’est ça, évidemment.$B$BVous et moi, on va être potes. Drakuru, il le sait.$B$B<Un sourire s\’étale lentement sur le visage peinturluré du Troll.>', `VerifiedBuild`=26365 WHERE (`ID`=27285 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Avez-vous entendu parler des taunkas ? Ils sont censés avoir un lien avec les Taurens. Vous imaginez ?', `VerifiedBuild`=26365 WHERE (`ID`=27246 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Et voilà. Ce soir, ça va encore être abats de chauve-souris au menu, pour les Gnomes…', `VerifiedBuild`=26365 WHERE (`ID`=27129 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Bienvenue aux Sources de granit, $gl\'ami:l\'amie;.$B$BSi vous réussissez à attraper un de ces Trolls des glaces vivant, veillez à me prévenir. Nous en offrons un bon prix.', `VerifiedBuild`=26365 WHERE (`ID`=27124 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Arthas a donné l\'ordre de purger Stratholme, en abattant jusqu\'aux derniers les Humains qui habitent la ville.$B$BIl a découvert que le Fléau a infecté la ville en y introduisant sa peste. Et cette peste ne fait pas que tuer les gens.$B$BCeux qui en meurent se relèvent sous la forme de zombies décérébrés et partent alimenter la machine de guerre du Fléau.', `VerifiedBuild`=26365 WHERE (`ID`=27093 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Gluth, l\’ignoble chien pesteux de Naxxramas. On raconte que son appétit était si vorace que même la chair fraîche ne suffisait pas à satisfaire sa faim ; que Feugen lui donnait chaque jour à manger une armée de zombies en recyclant les restes des Morts-vivants trop faibles pour combattre.', `VerifiedBuild`=26365 WHERE (`ID`=27077 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous vous souvenez comme c\’était calme ici avant que les Elfes n\’arrivent ? Ça me manque, ce temps-là.', `VerifiedBuild`=26365 WHERE (`ID`=27001 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je connaissais un Nain qui prétendait que les Gnomes venaient de sous la terre. Qu\’on descend tous de Gnomes des sables. Quel ramassis d\’âneries ! Des Gnomes des sables ? N\’IMPORTE QUOI !', `Text1_lang`='Je connaissais un Nain qui prétendait que les Gnomes venaient de sous la terre. Qu\’on descend tous de Gnomes des sables. Quel ramassis d\’âneries ! Des Gnomes des sables ? N\’IMPORTE QUOI !', `VerifiedBuild`=26365 WHERE (`ID`=26977 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il y a quelque chose qui refoule méchamment, ici ! Aucune chance pour que ce soit moi : les Gnomes ont l\’odeur de la crème et des rayons de soleil. Pas comme les Nains, qui sentent comme s\’ils étaient nés dans une aisselle de trogg ! Y a pas de Nain parmi vous, hein ?', `Text1_lang`='Il y a quelque chose qui refoule méchamment, ici ! Aucune chance pour que ce soit moi : les Gnomes ont l\’odeur de la crème et des rayons de soleil. Pas comme les nains, qui sentent comme s\’ils étaient nés dans une aisselle de trogg ! Y a pas de Nain parmi vous, hein ?', `VerifiedBuild`=26365 WHERE (`ID`=26976 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous vous souvenez du journal qui avait été découvert ? Eh bien, ce Nain pourrait être exactement le Nain qu\’il nous faut pour retrouver la trace de Brann Barbe-de-Bronze et le capturer.', `VerifiedBuild`=26365 WHERE (`ID`=26878 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Comment ? Les Humains sont réellement stupides, hein ? Thel\’zan le Portebrune ! Thel\’zan la liche ! Celui qui est né de chair et de sang humains et a tout sacrifié pour obtenir le pouvoir, protégé par le roi-liche !', `VerifiedBuild`=26365 WHERE (`ID`=26831 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Humains… Sous la terre de Garde-Hiver, vous avez construit un mausolée ! Pourquoi croyez-vous que Naxxramas a attaqué précisément cet endroit ? Thel\’zan se terre au fin fond de votre propre crypte et vous envoie des milliers de milliers de cadavres ! Vous allez périr...', `VerifiedBuild`=26365 WHERE (`ID`=26828 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ne laissez pas les sales Orcs prendre le port !', `VerifiedBuild`=26365 WHERE (`ID`=26736 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous n\’êtes pas au courant, c\’est ça ? Les trappeurs. Ils ne sont pas Humains… Nous devons déguerpir d\’ici ! Venez, vite !', `VerifiedBuild`=26365 WHERE (`ID`=26539 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Krenna doit douter de mes capacités. Pendant que nos combattants d\’élite sont en marche pour aller affronter l\’Alliance et les trappeurs, ou pour aller semer la dévastation chez les Trolls, elle m\’a chargé du nettoyage de la vermine écorche-dragon.$B$BSi nous combinons nos efforts, nous pourrons peut-être tous les deux faire nos preuves à la conquérante.', `VerifiedBuild`=26365 WHERE (`ID`=26519 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='C\'est la dernière fois que ce pleutre de Troll déçoit mes attentes. Anthis ! Casse-lui les jambes !', `VerifiedBuild`=26365 WHERE (`ID`=26227 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Au quatrième top, il sera 12h00. Pour ceux d\'entre vous qui ne savent pas compter, c\'est midi. Oui, les Orcs, c\'est de vous que je parle.', `VerifiedBuild`=26365 WHERE (`ID`=26208 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='$GPetit:Petite; $n, pourquoi faites-vous appel à moi ? Êtes-vous de ligue avec les Trolls de cette terre ? Venez-vous pour me tuer et vous approprier ma puissance ?', `VerifiedBuild`=26365 WHERE (`ID`=25854 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Je ne voudrais pas même si tu étais le dernier Troll en Azeroth !', `VerifiedBuild`=26365 WHERE (`ID`=25784 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ne me fais pas descendre de cette caisse ! Je vais te fracasser le crâne, Orc !', `VerifiedBuild`=26365 WHERE (`ID`=25778 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je serais bien heureux de tous vous remplacer par des Gobelins ! Ne me décevez pas !', `VerifiedBuild`=26365 WHERE (`ID`=25776 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='D\’accord, Budd. C\’est vous le chat. Allez attraper un Troll…', `Text1_lang`='D\’accord, Budd. C\’est vous le chat. Allez attraper un Troll…', `VerifiedBuild`=26365 WHERE (`ID`=25666 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Venez, Budd. Il est l\’heure d\’aller jouer avec les Trolls de glace !', `Text1_lang`='Venez, Budd. Il est l\’heure d\’aller jouer avec les Trolls de glace !', `VerifiedBuild`=26365 WHERE (`ID`=25656 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Accompagné d\'une amie, je suis venu sauver une jeune Humaine des griffes du seigneur de la Légion, Kil\'jaeden, et de ses ignobles serviteurs.$B$BNous avons découvert que Kael\'thas essaie d\'invoquer Kil\'jaeden des profondeurs du Puits de soleil même !$B$BPour le bien de ce monde, nous ne pouvons pas le laisser faire ! Je suis là pour faire en sorte que cela n\'arrive jamais.', `VerifiedBuild`=26365 WHERE (`ID`=25561 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Profitez du fait que les protecteurs sont désactivés. Si l\’un des éclaireurs lamesoleils vous repère, je suis sûre qu\’il essayera d\’activer le protecteur le plus proche. Tuez-le rapidement pour l\’en empêcher. Une fois les Elfes de sang éliminés, tant que vous ne passerez pas à portée des capteurs de proximité des protecteurs, vous devriez pouvoir passer sans devoir livrer bataille. Cela vous fera gagner à la fois du temps et des ressources.', `VerifiedBuild`=26365 WHERE (`ID`=25531 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='L\’opération Soleil brisé a réussi une très belle avancée dans les zones extérieures de l\’île de Quel\’Danas. Nous repoussons les troupes de Kil\’jaeden et, dans la confusion générale, j\’ai pu mener un petit groupe d\’éclaireurs jusqu\’ici, sur le plateau du Puits de soleil. Mes troupes ont traversé la place Parhélion, ont repéré un groupe d\’Elfes de sang lamesoleils, et nous avons donné l\’assaut. Ce combat s\’est vite révélé désastreux.', `VerifiedBuild`=26365 WHERE (`ID`=25525 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Orc et Troll', `Text1_lang`='Orc et Troll', `VerifiedBuild`=26365 WHERE (`ID`=25249 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il prétend que toutes les créations des titans succombent à ce qu\’il appelle « la malédiction de la chair ». Il affirme que les Gnomes originels étaient autrefois des mécagnomes.', `Text1_lang`='Il prétend que toutes les créations des titans succombent à ce qu\’il appelle « la malédiction de la chair ». Il affirme que les Gnomes originels étaient autrefois des mécagnomes.', `VerifiedBuild`=26365 WHERE (`ID`=25208 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='À poil les Gnomes, voilà mon nom. Et tu sais pourquoi, mon pote ? Parce que toi t\'as debarqué ici sur un pauvre tricycle déglingué, alors que moi je suis arrivé en chevauchant ma wyverne de luxe toutes options à 5 000 pièces d\'or. C\'est là qu\'il est, mon nom.', `VerifiedBuild`=26365 WHERE (`ID`=25178 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Et tu te prends pour un artisan, espèce de fils de Gnome ?', `VerifiedBuild`=26365 WHERE (`ID`=25167 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Et de quoi vous parlez, au juste ? Vous braillez à propos de la clé plate, vous pleurez sur le procédé de fonte de l\’adamantite durcie, vous gémissez à propos d\’un Gnome qui vous a piqué votre idée…', `VerifiedBuild`=26365 WHERE (`ID`=25161 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Accompagné d\'une amie, je suis venu sauver une jeune Humaine des griffes du seigneur de la Légion, Kil\'jaeden, et de ses ignobles serviteurs.$B$BNous avons découvert que Kael\'thas essaie d\'invoquer Kil\'jaeden des profondeurs du Puits de soleil même !$B$BPour le bien de ce monde, nous ne pouvons pas le laisser faire ! Je suis là pour faire en sorte que cela n\'arrive jamais.', `VerifiedBuild`=26365 WHERE (`ID`=25095 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous, les Elfes de sang, vous feriez mieux de rejoindre le troupeau avant qu\’il ne soit trop tard.', `VerifiedBuild`=26365 WHERE (`ID`=25058 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Pathétique, vraiment. Les sin\’dorei ne sont pas faits pour se mêler à des corniauds comme les Draeneï.', `VerifiedBuild`=26365 WHERE (`ID`=25056 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ces soi-disant Gnomes, je m\'en vais leur faire une révision technique, et une minutieuse. Aux quatre coins de la décharge, qu\'on va les retrouver, démontés en petites pièces détachées, façon jeu de construction.', `Text1_lang`='Ces soi-disant Gnomes, je m\'en vais leur faire une révision technique, et une minutieuse. Aux quatre coins de la décharge, qu\'on va les retrouver, démontés en petites pièces détachées, façon jeu de construction.', `VerifiedBuild`=26365 WHERE (`ID`=24944 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Pour moi, tout ça ne ressemblait pas vraiment à une « guérison », et je n\’allais pas rester planté là à écouter parler un robot maboul pendant qu\’il massacrait mes hommes ! $B$BNous avons déguerpi, et c\’est ce qu\’aurait fait tout Gnome un tant soit peu sensé ! En fait, la plupart d\’entre nous ont tout simplement sauté de la plateforme en parachute, mais bon, vous voyez.$B$BPeu d\’entre nous ont réussi à revenir jusqu\’ici, et nous manquons encore d\’effectifs pour nous occuper de tout, entre les magnataures au nord et le Fléau à l\’est. Sans compter que la Horde vient de s\’installer tranquillement au nord-ouest !', `VerifiedBuild`=26365 WHERE (`ID`=24907 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il se fait appeler le maître des rouages Mécazod. Quand nous sommes arrivés, il était en train de transformer les survivants en machines !$B$BIl nous a accueillis chaleureusement et nous a expliqué qu\’il était l\’un des premiers Gnomes à avoir été créé par quelque chose qu\’il appelait « le Grand Architecte », un gardien titan de la légendaire ville d\’Ulduar.$B$BApparemment, le bouchon que mon équipe avait dégagé des tuyaux de pompage, c\’était lui. Il était resté bloqué il y a des milliers d\’années, suite à une panne. C\’était bien notre veine d\’avoir construit la station de pompage juste au-dessus de lui.', `VerifiedBuild`=26365 WHERE (`ID`=24905 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Comme je le disais, ce que nous avons trouvé là-bas défiait tout explication.$B$BImpossible de trouver mes hommes, mais à leur place il y avait une véritable armée de robots et d\’androïdes qui vaquaient à leurs occupations !$B$BLes androïdes ressemblaient tous à des Gnomes. Des gnomoïdes ! Et ils nous ont dit qu\’ils attendaient notre arrivée. En fait, à leur manière, ils réagissaient comme s\’ils nous connaissaient. Ils nous ont rapidement encerclés et conduits vers le sommet de la station de pompage.$B$BC\’est là que nous avons vu qui était leur chef et ce qu\’il faisait aux Gnomes survivants !', `VerifiedBuild`=26365 WHERE (`ID`=24904 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Enfin, tout ça je l\’ai appris plus tard, parce que je n\’étais pas là-bas à l\’époque.$B$BEn examinant ce qui bloquait l\’aspiration, ils sont tombés sur les pièces détachées d\’un robot qui ressemblait à un Gnome ! Évidemment, ces idiots ont bossé jour et nuit pour l\’assembler sans en parler à personne.$B$BC\’est à ce moment-là que nous avons perdu tout contact avec la station de pompage.', `VerifiedBuild`=26365 WHERE (`ID`=24902 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Un Gnome essaie de se faire des amis, et BOUM ! Comme ça, tout d\’un coup, il se retrouve enchaîné !', `VerifiedBuild`=26365 WHERE (`ID`=24766 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ce n\'est pas moi qui l\'ai prédit, mais Velen le Draeneï.', `VerifiedBuild`=26365 WHERE (`ID`=24469 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Raser Orgrimmar n\’était pas seulement un assaut contre les Orcs, c\’était un affront envers toute la Horde. Une attaque contre notre existence même !$B$BLe roi-liche cherche à instiller la peur…$B$BEt ceci…$B$B<Garrosh désigne les murs fortifiés qui l\’entourent.>$B$BCeci est le résultat de la campagne d\’Arthas.$B$BLa Horde ne connaît pas la peur...', `VerifiedBuild`=26365 WHERE (`ID`=24458 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Gobelins des Gourmandises Fumebois ont toujours des friandises délicieuses à vendre à cette époque de l\’année.', `Text1_lang`='Les Gobelins des Gourmandises Fumebois ont toujours des friandises délicieuses à vendre à cette époque de l\’année.', `VerifiedBuild`=26365 WHERE (`ID`=24341 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Gobelins des Gourmandises Fumebois ont toujours des friandises délicieuses à vendre à cette époque de l\’année.', `Text1_lang`='Les Gobelins des Gourmandises Fumebois ont toujours des friandises délicieuses à vendre à cette époque de l\’année.', `VerifiedBuild`=26365 WHERE (`ID`=24324 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='J\’ai déjà combattu aux côtés des Draeneï à Skettis, et dans la vallée d\’Ombrelune. Mais maintenant, nous sommes en Azeroth. Et la situation a l\’air très mauvaise.', `VerifiedBuild`=26365 WHERE (`ID`=24211 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous pressez souvent des Gnomes ?', `Text1_lang`='Vous pressez souvent des Gnomes ?', `VerifiedBuild`=26365 WHERE (`ID`=24139 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='C\'est impressionnant tout ce que peut vous apprendre un Gnome si vous le pressez assez fort : d\'ingénieux petits tours pour échapper à ses liens, la recette de délicieux gâteaux, et même comment fabriquer une machine volante.', `VerifiedBuild`=26365 WHERE (`ID`=24137 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Actuellement, nous nous occupons du test de Zul\'Aman, un donjon de raid à dix personnes situé dans les terres Fantômes. Il faut être au moins de niveau 68 pour y entrer.', `VerifiedBuild`=26365 WHERE (`ID`=24012 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je serai bientôt plus puissant que tous les Elfes ! Je servirai aux côtés de Kil\’jaeden !', `Text1_lang`='Je serai bientôt plus puissante que tous les Elfes ! Je servirai aux côtés de Kil\’Jaeden !', `VerifiedBuild`=26365 WHERE (`ID`=23987 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='J\’ai eu un sacré coup de chance. Il n\’y a pas beaucoup de Taurens qui peuvent devenir mousses sur une frégate comme celle-ci ! Grezzix me laisse même manœuvrer ces formidables canoës de combat.', `VerifiedBuild`=26365 WHERE (`ID`=23974 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='… la ville était réduite en cendres, ruines calcinées de sa splendeur passée. Les armées des Morts-vivants se levaient là où avaient jadis vécu les gens.', `VerifiedBuild`=26365 WHERE (`ID`=23747 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Là-bas, fourré au fin fond des poches d\’un Troll, j\’ai trouvé une magnifique gemme, que j\’ai vendue pour un prix non moins magnifique !', `VerifiedBuild`=26365 WHERE (`ID`=23743 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='C\'est là que j\'ai tué cet Orc d\'un grand coup, et que j\'ai emporté ces spallières.', `VerifiedBuild`=26365 WHERE (`ID`=23740 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='C\'est pas une manière de traiter le tapis d\'un Nain ! Ça ne se fait pas, c\'est tout !', `VerifiedBuild`=26365 WHERE (`ID`=23702 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Si vous cherchez à vous entraîner à la lutte contre les incendies, vous êtes au bon endroit. Y\’a peut-être un bon paquet de neige dans le coin, mais on peut jamais savoir quand Grillacier ou un de ces Gnomes va faire exploser un truc, hein ?', `Text1_lang`='Si vous cherchez à vous entraîner à la lutte contre les incendies, vous êtes au bon endroit. Y\’a peut-être un bon paquet de neige dans le coin, mais on peut jamais savoir quand Grillacier ou un de ces Gnomes va faire exploser un truc, hein ?', `VerifiedBuild`=26365 WHERE (`ID`=23555 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Si vous pensez que les Orcs tapent fort, essayez leur bière, elle tape encore plus fort ! Voyez par vous-même à la distillerie Drohn, seulement pendant la fête des Brasseurs !', `Text1_lang`='Si vous pensez que les Orcs tapent fort, essayez leur bière, elle tape encore plus fort ! Voyez par vous-même à la distillerie Drohn, seulement pendant la fête des Brasseurs !', `VerifiedBuild`=26365 WHERE (`ID`=23522 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Venez passer la fête des Brasseurs avec des Orcs qui savent ce que c\’est qu\’une bonne bière ! Passez voir la distillerie Drohn à la fête des Brasseurs !', `Text1_lang`='Venez passer la fête des Brasseurs avec des Orcs qui savent ce que c\’est qu\’une bonne bière ! Passez voir la distillerie Drohn à la fête des Brasseurs !', `VerifiedBuild`=26365 WHERE (`ID`=23521 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je recherche toutes sortes de choses, ma chérie ! Mais si c\’est des marques de Protecteur des Tonneaux que vous parlez, elles sont faites pour me montrer comment vous vous débrouillez au combat.$B$BÀ chaque fois que vous dégommez un Nain Sombrefer, vous gagnez une marque. Pour chaque marque, vous aurez un bon.$B$BÇa me chagrine un peu de distribuer des bons comme ça, mais bon, ça veut aussi dire que je vous reverrai. Et que je vous reverrai partir, aussi...', `Text1_lang`='Je recherche toutes sortes de choses, mon chéri ! Mais si c\’est des marques de Protecteur des Tonneaux que vous parlez, elles sont faites pour me montrer comment vous vous débrouillez au combat.$B$BÀ chaque fois que vous dégommez un Nain Sombrefer, vous gagnez une marque. Pour chaque marque, vous aurez un bon.$B$BÇa me chagrine un peu de distribuer des bons comme ça, mais bon, ça veut aussi dire que je vous reverrai. Et que je vous reverrai partir, aussi...', `VerifiedBuild`=26365 WHERE (`ID`=23492 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Alors vous voulez savoir ce que je recherche ? J\'aime cet esprit… Je recherche quelqu\'un de fort et courageux. Savoir réfléchir, c\'est très bien, mais il faut aussi avoir le muscle qui va avec.$B$BQuand ces casse-pieds de Nains sombrefers attaquent, vous pouvez leur balancer une chope à la tête. Il suffit de la vider et de la lancer. Rien de compliqué.$B$BPour chaque nain dégommé, vous gagnerez une marque. Venez me voir et je vous donnerai un bon en échange de chaque marque.', `Text1_lang`='Alors vous voulez savoir ce que je recherche ? J\'aime cet esprit… Je recherche quelqu\'un de fort et courageux. Savoir réfléchir, c\'est très bien, mais il faut aussi avoir le muscle qui va avec.$B$BQuand ces casse-pieds de Nains sombrefers attaquent, vous pouvez leur balancer une chope à la tête. Il suffit de la vider et de la lancer. Rien de compliqué.$B$BPour chaque nain dégommé, vous gagnerez une marque. Venez me voir et je vous donnerai un bon en échange de chaque marque.', `VerifiedBuild`=26365 WHERE (`ID`=23437 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Salut, beau Gnome, que puis-je faire pour vous aider ?', `VerifiedBuild`=26365 WHERE (`ID`=23436 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='J\’ai pris cet objet aux Trolls quand ils m\’ont capturée. Peut-être trouverez-vous un moyen de l\’utiliser contre eux.', `VerifiedBuild`=26365 WHERE (`ID`=23381 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Bouhouhouh, je suis un pauvre petit Gnome, et je n\'ai plus de capital - euh, attendez…', `Text1_lang`='Bouhouhouh, je suis un pauvre petit Gnome, et je n\'ai plus de capital - euh, attendez…', `VerifiedBuild`=26365 WHERE (`ID`=23354 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il faudrait inventer un bonbon avec un cœur fondant parfumé au Gnome.', `Text1_lang`='Il faudrait inventer un bonbon avec un cœur fondant parfumé au Gnome.', `VerifiedBuild`=26365 WHERE (`ID`=23297 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ces statues sont sensiblement différentes des autres représentations des Trolls.', `VerifiedBuild`=26365 WHERE (`ID`=23275 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les feux brûlent régulièrement depuis des années. Il est rare de voir des Trolls avec autant de suite dans les idées.', `VerifiedBuild`=26365 WHERE (`ID`=23272 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Des Trolls momifiés… Embaumés en toute hâte, je dirais…', `VerifiedBuild`=26365 WHERE (`ID`=23270 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous rejetez la faute sur les garde-paix ? On vous avait donné une mission simple. Le Troll était manifestement un escroc !', `VerifiedBuild`=26365 WHERE (`ID`=23267 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Mais pourquoi est-ce que tout le monde n\’arrête pas de répéter que la DERNIÈRE chose qu\’il nous faut, à nous les Gnomes, c\’est plus de sucre ?!', `Text1_lang`='Mais pourquoi est-ce que tout le monde n\’arrête pas de répéter que la DERNIÈRE chose qu\’il nous faut, à nous les Gnomes, c\’est plus de sucre ?!', `VerifiedBuild`=26365 WHERE (`ID`=23257 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je crois que tant qu\’à être là, je vais descendre et regarder si je ne trouve pas quelques trésors. Après tout, quelles sont les chances qu\’un Nain comme moi se fasse choper deux fois de suite ?', `VerifiedBuild`=26365 WHERE (`ID`=23215 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Peppy, je te jure, tu pourrais coucher un Nain.', `VerifiedBuild`=26365 WHERE (`ID`=23194 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Youpi ! C\’est super comment vous avez battu le grand méchant Troll et c\’est vraiment dommage que je ne puisse pas atteindre la poignée de cette cage parce que sinon je serais venue vous aider et je l\’aurais fait exploser en petits morceaux pour avoir tué tous les autres pauvres prisonniers et pour m\’avoir fait si peur et je suis désolée de vous demander encore un peu d\’aide mais est-ce que vous pourriez venir m\’ouvrir s\’il vous plaît ?', `VerifiedBuild`=26365 WHERE (`ID`=23156 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Bien sûr. Je l\'ai trouvé sur le cadavre d\'un Nain, non loin d\'ici.', `VerifiedBuild`=26365 WHERE (`ID`=23155 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il n\’y a plus de danger, petite Gnome. Vous pouvez sortir.', `Text1_lang`='Il n\’y a plus de danger, petite Gnome. Vous pouvez sortir.', `VerifiedBuild`=26365 WHERE (`ID`=23154 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Oh hourra – vous avez tué le méchant Troll juste à temps pour m\’éviter d\’être horriblement massacrée et de voir ma petite âme innocente asservie et utilisée dans ses plans diaboliques pour conquérir Azeroth et comme vous avez risqué votre vie pour me sauver d\’une mort certaine je dois vous récompenser qui que vous soyez d\’où que vous veniez et même si vous n\’étiez pas venus pour ça à l\’origine alors je vais faire de mon mieux pour trouver quelque chose à vous donner…$B$B<Ashli reprend son souffle.>$B$BVous aimez les bonbons ?', `VerifiedBuild`=26365 WHERE (`ID`=23153 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je sacrifie cet Humain au nom de notre ténébreux maître !', `VerifiedBuild`=26365 WHERE (`ID`=23082 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le Cavalier sans tête vit au monastère Écarlate avec tout plein d\’autres fantômes et d\’Humains effrayants.', `Text1_lang`='Le Cavalier sans tête vit au monastère Écarlate avec tout plein d\’autres fantômes et d\’Humains effrayants.', `VerifiedBuild`=26365 WHERE (`ID`=23023 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Comment ai-je pu laisser Petrov me persuader de venir ici avec ces incompétents de Nains ?!', `VerifiedBuild`=26365 WHERE (`ID`=22976 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Youhou ! Par ici… le Nain prisonnier. Allez, venez qu\’on discute un peu.', `VerifiedBuild`=26365 WHERE (`ID`=22969 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ouvrez l\’œil, $r, nous sommes au milieu des Morts-vivants ici.', `VerifiedBuild`=26365 WHERE (`ID`=22944 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Relâchez-moi, bande de poltrons ! Je vais vous montrer de quel bois se chauffe un Nain !', `VerifiedBuild`=26365 WHERE (`ID`=22881 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le Nain tousse, crache et frissonne dans sa cage.', `VerifiedBuild`=26365 WHERE (`ID`=22872 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ça ressemble bien à la Ligue d\'abandonner un Nain, sans défense et sans bière !', `VerifiedBuild`=26365 WHERE (`ID`=22818 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je ne suis pas un grand amoureux des Orcs, mais ouais, j\’aimerais bien qu\’il soit là. Ces choses, il les démembrerait !', `Text1_lang`='Je ne suis pas une grande amoureuse des Orcs, mais ouais, j\’aimerais bien qu\’il soit là. Ces choses, il les démembrerait !', `VerifiedBuild`=26365 WHERE (`ID`=22717 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ça me rappelle l\'époque où j\'étais en poste au fort Cénarien pendant la seconde Guerre des sables changeants… Tu te souviens de cet Orc ? Saurcroc ? Le truc le plus impressionnant que j\'aie jamais vu.', `Text1_lang`='Ça me rappelle l\'époque où j\'étais en poste au fort Cénarien pendant la seconde Guerre des sables changeants… Tu te souviens de cet Orc ? Saurcroc ? Le truc le plus impressionnant que j\'aie jamais vu.', `VerifiedBuild`=26365 WHERE (`ID`=22716 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je suis à peu près sûr que ce ne sont pas des Gnomes.', `Text1_lang`='Je suis à peu près sûre que ce ne sont pas des Gnomes.', `VerifiedBuild`=26365 WHERE (`ID`=22708 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Kraz n\’aurait jamais dû venir dans cet endroit maudit… il aurait dû savoir qu\’il ne faut pas se fier aux Humains. Aveugle et bête, voilà ce que j\’étais, $c, comme vous le serez si vous suivez ses conseils.$B$BMon fils a disparu depuis deux lunes. La dernière fois qu\’on l\’a vu, il était avec l\’Humain, Budd. Il m\’a dit que je pourrais le retrouver derrière les murs de Zul\’Aman...$B$BJ\’y ai suivi ses compagnons, et je me suis fait capturer avec les autres. Sans votre aide, c\’était la mort assurée.$B$BMerci, $gétranger:étrangère;.', `VerifiedBuild`=26365 WHERE (`ID`=22632 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Que vous est-il arrivé, Orc ?', `Text1_lang`='Que vous est-il arrivé, Orc ?', `VerifiedBuild`=26365 WHERE (`ID`=22631 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ne vous occupez pas de moi, $r.$B$BKraz a déjà un pied dans la tombe. Partez maintenant, $r, avant que les méchants Trolls ne vous attrapent vous aussi.', `VerifiedBuild`=26365 WHERE (`ID`=22621 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Je voulais m\’entraîner à lancer mes sorts sur les lapins en bas de chez moi pour renforcer ma magie comme ça j\’aurais été à Gnomeregan pour tuer tous les méchants et faire revenir tous les Gnomes mais maman a dit que j\’étais trop petite pour aller à l\’aventure et alors j\’ai rencontré Monsieur Budd il m\’a dit qu\’on a l\’âge qu\’on a dans sa tête et que je pouvais venir avec lui et ses amis et que j\’aurais tout l\’entraînement que je voudrais mais on s\’est fait prendre et mettre en cage…$B$B<Ashli prend une grande inspiration.>$B$BVous êtes vraiment $gun:une:r; $r ?', `VerifiedBuild`=26365 WHERE (`ID`=22613 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Et qu\’est-ce qu\’une Gnomette mignonette comme vous fait au milieu de ce gâchis ?', `Text1_lang`='Et qu\’est-ce qu\’une Gnomette mignonette comme vous fait au milieu de ce gâchis ?', `VerifiedBuild`=26365 WHERE (`ID`=22612 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Ohh, merci beaucoup de m\’avoir fait sortir de cette cage parce que j\’y suis restée si longtemps et ça sentait comme du gigot de mouton qui serait resté au soleil pendant une semaine et je n\’aime pas le mouton parce que quand j\’étais petite enfin plus petite j\’ai été changée en mouton pendant une minute alors que je sais ce qu\’on ressent quand on est un mouton et je ne veux pas que quelqu\’un me mange surtout pas un très grand Troll déguisé en chaton…$B$B<Ashli reprend son souffle.>$B$BComment sont mes cheveux ?', `VerifiedBuild`=26365 WHERE (`ID`=22611 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Qu\’est-ce qu\’un Nain comme vous fait dans un endroit pareil ?', `Text1_lang`='Qu\’est-ce qu\’un Nain comme vous fait dans un endroit pareil ?', `VerifiedBuild`=26365 WHERE (`ID`=22609 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Écoutez, $gétranger:étrangère;, je serais ravi d\’avoir une petite conversation avec vous, mais j\’ai l\’impression que ma tête va exploser.$B$BJ\’vous propose de vous débrouiller pour que ces bons à rien de Trolls arrêtent de faire tout ce vacarme. Et ensuite on pourra discuter, hein ?', `VerifiedBuild`=26365 WHERE (`ID`=22607 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Salut $c, je suis Ashli et j\’ai besoin que vous m\’aidiez parce que je suis enfermée dans cette cage parce que Budd m\’a envoyée ici avec des Trolls qui sentaient pas bon et des Humains pas malins et un Nain ivre et on s\’est fait prendre par des Trolls super-méchants et ils nous ont traînés ici et ils m\’ont mis ici avec le grand chat-troll qui est vraiment vraiment vilain et qui tue tous les gens dans les cages et je crois qu\’il va bientôt me tuer mais je ne peux pas sortir toute seule alors…$B$B<Ashli reprend son souffle.>$B$BVous pourriez tuer cette chose ?', `VerifiedBuild`=26365 WHERE (`ID`=22597 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Des gnomes ! Les Gnomes sont des gens merveilleux. Formidables ! Et ils en savent tellement long sur l\’art de l\’ingénierie ! Vous devez voyager vers Forgefer, jusqu\’à Brikabrok, et vous adresser à Fuseressort Pannengin.', `VerifiedBuild`=26365 WHERE (`ID`=22582 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il est temps pour vous d\’aller voir ailleurs. Telonis, un Elfe de la nuit établi sur la terrasse des Artisans à Darnassus, sera la prochaine étape de votre apprentissage.', `VerifiedBuild`=26365 WHERE (`ID`=22569 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='AHHHHRRRGH ! Tout ça, c\’est une idée |2 $n ! Je vous le jure… Certains de mes meilleurs amis sont des Trolls des forêts…', `VerifiedBuild`=26365 WHERE (`ID`=22551 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ce type est un crétin !$B$BGloire et fortune ? Ah ! Cet endroit est un piège mortel, $gmon pote:l\’amie; !$B$BJ\’ai vu des tas de Trolls en mon temps, mais ceux-là, ils sont différents… forts, sournois, rusés…$b$BJ\’ai eu l\’impression qu\’ils savaient qu\’j\’arrivais…', `VerifiedBuild`=26365 WHERE (`ID`=22306 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Laissez-moi vous donner un bon conseil : ne faites jamais confiance à un Gnome.', `VerifiedBuild`=26365 WHERE (`ID`=22299 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='<hic>Écoutez, $r, on discutera odeurs quand vous aurez passé un jour ou deux à vous battre avec des Trolls sauvages.', `VerifiedBuild`=26365 WHERE (`ID`=22236 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='J\’en sais rien, $gpetit:petite;. J\’suis nouveau.$B$BMais j\’peux vous dire qu\’j\’ai eu mon content de grog et de Trolls morts. L\’seul truc qui manque encore, c\’est l\’trésor que Budd arrête pas d\’nous promettre…', `VerifiedBuild`=26365 WHERE (`ID`=22229 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='S\’ils croient que je vais m\’amuser à fouiller une garde-robe de Troll, ils se mettent le doigt dans l\’œil jusqu\’à l\’os.', `VerifiedBuild`=26365 WHERE (`ID`=22190 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ce Troll était absolument malpropre.', `VerifiedBuild`=26365 WHERE (`ID`=22186 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Si d\’aventure vous franchissez les portes de la ville, $c, soyez sur vos gardes. Un Troll arborant un accoutrement tapageur pourrait tenter de vous approcher. Laissez-le à son exil, c\’est tout ce qu\’il mérite.', `VerifiedBuild`=26365 WHERE (`ID`=22131 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous avez l\’air d\’$gun:une:c; $c raisonnable. Vous rappelez-vous les jours glorieux de Theramore, avant que Jaina ne nous force à renoncer à combattre la Horde ? Vous n\’en avez pas assez d\’être $gobligé:obligée; de retenir vos coups alors que les Orcs ne cessent de vous provoquer ?', `VerifiedBuild`=26365 WHERE (`ID`=22053 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Hé ! Est-che que quelqu\’un aurait vu chette jolie Gobeline ? J\’aurais juré qu\’elles étaient toutes les deux dans le coinch.', `Text1_lang`='Hé ! Est-che que quelqu\’un aurait vu che beau Gobelin ? J\’aurais juré qu\’ils étaient tous les deux dans le coinch.', `VerifiedBuild`=26365 WHERE (`ID`=22012 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Merchi à toush de m\’écouter…. Au four… Autour… Aujourd\’hui, c\’est le jour où les Thrall… Les Tralala… Euh, non… Les TROLLS ! Voilà. Ouh... Les Trolls trimballent Thrall. Pour qu\’il chasse… qu\’il chache… qu\’il voie bien ce que vaut la Hordesh. POUR LA CORDE !', `Text1_lang`='Merchi à toush de m\’écouter…. Au four… Autour… Aujourd\’hui, c\’est le jour où les Thrall… Les Tralala… Euh, non… Les TROLLS ! Voilà. Ouh... Les Trolls trimballent Thrall. Pour qu\’il chasse… qu\’il chache… qu\’il voie bien ce que vaut la Hordesh. POUR LA CORDE !', `VerifiedBuild`=26365 WHERE (`ID`=22005 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Alors j\'ai mis mes meilleurs Gnomes au travail sur la fabrication d\'une machine qui remplirait ce rôle ! Nous avons calculé, à la seconde près, comment distribuer de la meilleure faç-', `Text1_lang`='Alors j\'ai mis mes meilleurs Gnomes au travail sur la fabrication d\'une machine qui remplirait ce rôle ! Nous avons calculé, à la seconde près, comment distribuer de la meilleure faç-', `VerifiedBuild`=26365 WHERE (`ID`=21990 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ça, je peux le comprendre. Mais le roi a quand même consenti à une évolution : laisser un Gnome mettre le tonneau en perce. J\'ai eu là une chance unique de travailler à la résolution d\'un nouveau type de problème : comment ouvrir le tonneau rapidement et efficacement, et en distribuer le contenu de telle manière à ce que tous les participants en reçoivent une part égale efficacement et rapidement.', `Text1_lang`='Ça, je peux le comprendre. Mais le roi a quand même consenti à une évolution : laisser un Gnome mettre le tonneau en perce. J\'ai eu là une chance de travailler à la résolution d\'un nouveau type de problème : comment ouvrir le tonneau rapidement et efficacement, et en distribuer le contenu de telle manière à ce que tous les participants en reçoivent une part égale efficacement et rapidement.', `VerifiedBuild`=26365 WHERE (`ID`=21989 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Quand le roi Magni est venu me parler de cette fête, j\’étais très enthousiaste. J\’ai commencé à lui expliquer que si les Gnomes participaient, nous pourrions vraiment en faire une sauterie dont on se souviendrait. Malheureusement, le roi Magni voulait que cela reste simple. Il tenait à rendre hommage au passé.', `Text1_lang`='Quand le roi Magni est venu me parler de cette fête, j\’étais très enthousiaste. J\’ai commencé à lui expliquer que si les Gnomes participaient, nous pourrions vraiment en faire une sauterie dont on se souviendrait. Malheureusement, le roi Magni voulait que cela reste simple. Il tenait à rendre hommage au passé.', `VerifiedBuild`=26365 WHERE (`ID`=21988 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je… Je suis défait… Le nouveau champion des Orcs est $n !', `VerifiedBuild`=26365 WHERE (`ID`=21431 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='J\’ai déjà vu des Gnomettes qui tapaient plus fort que ça !', `Text1_lang`='J\’ai déjà vu des Gnomettes qui tapaient plus fort que ça !', `VerifiedBuild`=26365 WHERE (`ID`=21354 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Pourquoi moi ? Pourquoi pas un Gobelin ou un Gnome…', `VerifiedBuild`=26365 WHERE (`ID`=21207 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Toi être méchant Orc... Méchaaan... or... argl !', `VerifiedBuild`=26365 WHERE (`ID`=21123 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ces montagnes sont pour les Ogres. Ni pour les Gronns, ni pour les Humains ou les Orcs !', `VerifiedBuild`=26365 WHERE (`ID`=21026 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Parfois ces maudits Elfes de sang s\’arrêtent pour réclamer une cochonnerie de composant ou de poison. Je dois veiller à avoir en permanence une bonne réserve de ce genre d\’objets, pour ne pas que le seigneur Illidan entende dire que les Gueules-de-dragon se montrent peu coopératifs...$B$B<Rumpus déglutit avec difficulté.>$B$BIl vaut mieux éviter ça...', `Text1_lang`='Parfois ces maudits Elfes de sang s\’arrêtent pour réclamer une cochonnerie de composant ou de poison. Je dois veiller à avoir en permanence une bonne réserve de ce genre d\’objets, pour ne pas que le seigneur Illidan entende dire que les Gueules-de-dragon se montrent peu coopératifs...$B$B<Rumpus déglutit avec difficulté.>$B$BIl vaut mieux éviter ça...', `VerifiedBuild`=26365 WHERE (`ID`=20996 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='C\'est cet Orc pas content. Lui éclater leur tête. Vous voulez Grok éclate sa tête à lui ?', `VerifiedBuild`=26365 WHERE (`ID`=20790 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Hé, Troll, pourquoi cette amulette elle marche pas !', `VerifiedBuild`=26365 WHERE (`ID`=20785 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le temps est venu de frapper les Elfes de sang encore présents au donjon de la Tempête. Prenez les armes, et que le chant de bataille d\’A\’dal décuple vos forces !', `VerifiedBuild`=26365 WHERE (`ID`=20779 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le temps est venu pour nous de nous soulever et de frapper les Gangr\’orcs survivants. Combattez pour votre chef de guerre ! Combattez pour la Horde !', `VerifiedBuild`=26365 WHERE (`ID`=20766 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='La citadelle des Flammes infernales est à nous ! La source de l\’armée de Gangr\’orcs d\’Illidan est anéantie ! Magtheridon a succombé aux coups |2 $n et de ses alliés !', `VerifiedBuild`=26365 WHERE (`ID`=20765 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Oyez, frères et sœurs d\’armes ! L\’heure de nos ennemis a sonné ! Nous devons frapper les Gangr\’orcs survivants ! Les exterminer comme la vermine qu\’ils sont ! La faveur des Fils de Lothar est avec vous !', `VerifiedBuild`=26365 WHERE (`ID`=20755 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Si un cheval a des fers à cheval, à moi, on va me donner des fers à Draeneï ?', `VerifiedBuild`=26365 WHERE (`ID`=20685 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Est-ce qu\’un Tauren paladin, c\’est une vache sacrée ?', `VerifiedBuild`=26365 WHERE (`ID`=20678 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='L\’odeur de ces Orcs mutants me dégoûte.', `VerifiedBuild`=26365 WHERE (`ID`=20653 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ileda des Elfes de sang, qui se trouve sur la place des Pérégrins à Lune-d\’Argent, apprend à ses élèves le maniement des épées à une main et à deux mains. Archibald, le maître d\’armes de Fossoyeuse, peut aussi vous enseigner ; il se trouve dans le quartier de la Guerre.', `Text1_lang`='Ileda des Elfes de sang, qui se trouve sur la place des Pérégrins à Lune-d\’Argent, apprend à ses élèves le maniement des épées à une main et à deux mains. Archibald, le maître d\’armes de Fossoyeuse, peut aussi vous enseigner ; il se trouve dans le quartier de la Guerre.', `VerifiedBuild`=26365 WHERE (`ID`=20619 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le maniement des bâtons est enseigné par Woo Ping, le maître d\’armes de l\’Arsenal de Weller, à Hurlevent, et Ilyenia Lunéclat, qui entraîne les Elfes de nuit sur la terrasse des Guerriers, à Darnassus.', `Text1_lang`='Le maniement des bâtons est enseigné par Woo Ping, le maître d\’armes de l\’Arsenal de Weller, à Hurlevent, et Ilyenia Lunéclat, qui entraîne les Elfes de nuit sur la terrasse des Guerriers, à Darnassus.', `VerifiedBuild`=26365 WHERE (`ID`=20577 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Handiir et Buliwyf peuvent vous montrer comment manier correctement une masse, quelle que soit sa taille. Vous pourrez trouver Buliwyf aux Armes de Timberline, à Forgefer. Handiir passe ses journées à l\’Exodar, entraînant les Draeneï à l\’étage des Commerces.', `Text1_lang`='Handiir et Buliwyf peuvent vous montrer comment manier correctement une masse, quelle que soit sa taille. Vous pourrez trouver Buliwyf aux Armes de Timberline, à Forgefer. Handiir passe ses journées à l\’Exodar, entraînant les Draeneï à l\’étage des Commerces.', `VerifiedBuild`=26365 WHERE (`ID`=20574 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='C\'est sans espoir, Draeneï ! Toi et ton auxiliaire ne pourrez m\'arracher le contrôle de ce misérable humain. Il est à moi !', `VerifiedBuild`=26365 WHERE (`ID`=20416 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Que la Lumière vous purifie, Humain ! Ne laissez pas la corruption démoniaque vous submerger.', `VerifiedBuild`=26365 WHERE (`ID`=20403 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je ne suis pas disposé à vous enseigner avant que vous ne soyez $gexalté:exaltée; par les Draeneï.', `Text1_lang`='Je ne suis pas disposée à vous enseigner avant que vous ne soyez $gexalté:exaltée; par les Draeneï.', `VerifiedBuild`=26365 WHERE (`ID`=20394 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je ne suis pas disposé à vous enseigner avant que vous n\’ayez prouvé votre amitié pour les Draeneï de l\’Exodar.', `Text1_lang`='Je ne suis pas disposée à vous enseigner avant que vous n\’ayez prouvé votre amitié pour les Draeneï de l\’Exodar.', `VerifiedBuild`=26365 WHERE (`ID`=20393 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ça me rappelle la fois où tu nous as fait fouiller tout Silithus à la recherche de preuves de l\'existence des Gnomes des sables.', `Text1_lang`='Ça me rappelle la fois où tu nous as fait fouiller tout Silithus à la recherche de preuves de l\'existence des Gnomes des sables.', `VerifiedBuild`=26365 WHERE (`ID`=20339 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Il y a longtemps, le Conseil des ombres a voulu organiser un rituel ténébreux dans Auchindoun. Leur échec a entraîné la création du désert que vous voyez alentour. La terre est maintenant hantée par les ossements et les esprits des Draeneï morts. On m\’a envoyée ici pour purifier les tombes de mes ancêtres, mais l\’endroit est tellement dangereux que je dois rester ici pour protéger les réfugiés.', `VerifiedBuild`=26365 WHERE (`ID`=20253 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Maudits soyez-vous ! Je vous maudis, satanés Gangr\’orcs sanglants ! Que vos âmes aillent pourrir dans le ventre de la puissance ténébreuse qui vous anime !', `VerifiedBuild`=26365 WHERE (`ID`=20184 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Auchindoun abrite les esprits de mes ancêtres. Cela ne se voit peut-être pas, mais tous les Roués furent autrefois des Draeneï… Nos esprits sont les mêmes.', `VerifiedBuild`=26365 WHERE (`ID`=20169 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='C\'est une réussite ! J\'ai terminé mon harmonisation avec la citadelle des Flammes infernales et la magie qu\'elle abrite. Ah, toute cette puissance !$B$BL\'endroit grouille de Gangr\’orcs… mais ce n\'est pas tout !! Je sens également une puissance démoniaque tapie en ces murs...$B$BSi vous m\'ouvrez votre âme, alors je pourrai me saisir de votre esprit - brièvement, cela va sans dire, et porter la plume au parchemin pour vous relater mes découvertes.', `VerifiedBuild`=26365 WHERE (`ID`=20007 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il y a dans cet endroit des mystères que nous, les Orcs, sommes impatients de découvrir. Des mystères de notre passé, des mystères de notre avenir, et des mystères qui nous permettent de voir en nous-mêmes.$B$BJ\’ai l\’espoir de percer tous ces mystères… m\’accompagnerez-vous sur ce chemin ?', `VerifiedBuild`=26365 WHERE (`ID`=19933 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Hein ? Où suis-je ? Ça ne va pas du tout ! Regardez-moi cet endroit ! Il n\’est pas sur ma liste ! Ça va me donner une de ces paperasses…$B$B<Le Gobelin griffone frénétiquement sur un petit carnet.>$B$BÉcoutez, si vous n\’avez rien de mieux à faire qu\’à rester $gplanté:plantée; ici à me regarder, pourquoi n\’allez-vous pas rendre visite à mon cousin, Kalin Kolportecœur, dans l\’une des capitales ? Le papier dit qu\’il s\’occupe d\’Orgrimmar, Hurlevent, les Pitons-du-Tonnerre, Forgefer, Fossoyeuse et Darnassus. Allez, sortez d\’ici !', `Text1_lang`='Hein ? Où suis-je ? Ça ne va pas du tout ! Regardez-moi cet endroit ! Il n\’est pas sur ma liste ! Ça va me donner une de ces paperasses…$B$B<La Gobeline griffone frénétiquement sur un petit carnet.>$B$BÉcoutez, si vous n\’avez rien de mieux à faire qu\’à rester $gplanté:plantée; ici à me regarder, pourquoi n\’allez-vous pas rendre visite à mon cousin, Kalin Kolportecœur, dans l\’une des capitales ? Le papier dit qu\’il s\’occupe d\’Orgrimmar, Hurlevent, les Pitons-du-Tonnerre, Forgefer, Fossoyeuse et Darnassus. Allez, sortez d\’ici !', `VerifiedBuild`=26365 WHERE (`ID`=19932 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le seigneur funeste Kazzak déchaînera bientôt toutes nos forces contre ces pitoyables Orcs et Humains !', `VerifiedBuild`=26365 WHERE (`ID`=19895 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Mais vous êtes des dragons ! Comment des Orcs pourraient-ils vous infliger ça ?', `Text1_lang`='Mais vous êtes des dragons ! Comment des Orcs pourraient-ils vous infliger ça ?', `VerifiedBuild`=26365 WHERE (`ID`=19865 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='« <Le gladiateur vétéran parle un Orc approximatif.> Quoi ? Vous avez un problème ? On verra ça dans l\'arène. Je ne perds pas mon temps avec votres petites « hordes » et « alliances ». »', `VerifiedBuild`=26365 WHERE (`ID`=19792 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous ! $C ! Nous avons besoin de votre aide ! Les brutaux Orcs Chanteguerre détruisent les belles merveilles d\’Orneval, et nous avons besoin que vous nous prêtiez votre puissance sur le champ de bataille. Revenez avec nous en Azeroth et combattez pour l\’honneur des Sentinelles d\’Aile-argent ! Vous joindrez-vous à nous ?', `Text1_lang`='Vous ! $C ! Nous avons besoin de votre aide ! Les brutaux Orcs Chanteguerre détruisent les belles merveilles d\’Orneval, et nous avons besoin que vous nous prêtiez votre puissance sur le champ de bataille. Revenez avec nous en Azeroth et combattez pour l\’honneur des Sentinelles d\’Aile-argent ! Vous joindrez-vous à nous ?', `VerifiedBuild`=26365 WHERE (`ID`=19751 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Qu\’y a-t-il ? Vous ne voyez donc pas que je suis un Nain occupé ?', `VerifiedBuild`=26365 WHERE (`ID`=19746 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les cellules de mana de l\’Œil du cyclone sont précieuses. Nous ne pouvons pas nous permettre de laisser les Draeneï s\’en emparer ! Vous, là ! Rendez-vous utile, venez combattre avec nous !', `Text1_lang`='Les cellules de mana de l\’Œil du cyclone sont précieuses. Nous ne pouvons pas nous permettre de laisser les Draeneï s\’en emparer ! Vous, là ! Rendez-vous utile, venez combattre avec nous !', `VerifiedBuild`=26365 WHERE (`ID`=19725 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Hors d\’ici, Elfe insignifiant. Je prends le contrôle !', `VerifiedBuild`=26365 WHERE (`ID`=19595 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il est peu surprenant qu\’un Elfe de sang possède un tel potentiel pour la chasse de démons. Après tout, les sin\’dorei n\’ont pas cette aversion naturelle pour la magie qu\’ont les Elfes de la nuit.$B$BCependant, je ne m\’attends pas à ce qu\’ils soient nombreux à réussir. La plupart d\’entre eux, y compris Varedis, recherchent la puissance pour elle-même. Cette puissance les consumera, car ils ne sont pas animés par cette pure flamme qui brûle dans le cœur d\’un vrai chasseur de démon.', `VerifiedBuild`=26365 WHERE (`ID`=19571 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Pour le récompenser de ses années de service, Illidan permit à Kael\’thas d\’envoyer cinq guerriers s\’entraîner auprès de lui. Des elfes seraient pour la première fois autorisés à devenir chasseurs de démons.$B$BL\’entraînement fut terrible. Illidan fit subir aux guerriers de Kael des épreuves qui auraient eu raison de la plupart des chasseurs de démons accomplis.$B$BTrois des Elfes de Kael moururent pendant l\’initiation. Un quatrième perdit la raison.$B$BL\’un d\’entre eux en vint à bout.', `VerifiedBuild`=26365 WHERE (`ID`=19566 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='« Antonidas a quelques conclusions intéressantes en ce qui concerne ces Orcs et leur « léthargie » ».', `VerifiedBuild`=26365 WHERE (`ID`=19545 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le Nain semble avoir été découvert et tué au cours d\’une mission d\’espionnage. Les opérateurs de la Forgemort ont laissé le corps à la vue de tous, peut-être pour qu\’il serve d\’avertissement à d\’éventuels fouineurs.', `VerifiedBuild`=26365 WHERE (`ID`=19325 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ouais, ouais… bienvenue à mon poste isolé.$B$BAlors, qu\’en pensez-vous ? Plutôt sympa comme installation, hein ?$B$BJe suis bien content d\’être débarrassé de ces snobs d\’Elfes de Sylvanaar, de toute façon.$B$BRendez-vous utile, il y a plein d\’expériences et de machins pour lesquels vous pouvez aider, par ici.', `VerifiedBuild`=26365 WHERE (`ID`=19313 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Contrôle de destrier d\’Humain.', `Text1_lang`='Contrôle de destrier d\’Humain.', `VerifiedBuild`=26365 WHERE (`ID`=19288 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='On trouve des maîtres des écuries juste devant les auberges des Draeneï et des Elfes de sang, sur l\’Éminence de l\’Aldor et le Degré des Clairvoyants.', `Text1_lang`='On trouve des maîtres des écuries juste devant les auberges des Draeneï et des Elfes de sang, sur l\’Éminence de l\’Aldor et le Degré des Clairvoyants.', `VerifiedBuild`=26365 WHERE (`ID`=19223 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les auberges de Shattrath sont pleines de réfugiés blessés. Mais il y a encore de la place chez les Draeneï de l\’Aldor ou les Elfes de sang des Clairvoyants. Leurs auberges sont situées respectivement sur l\’Éminence de l\’Aldor et sur le Degré des Clairvoyants.', `Text1_lang`='Les auberges de Shattrath sont pleines de réfugiés blessés. Mais il y a encore de la place chez les Draeneï de l\’Aldor ou les Elfes de sang des Clairvoyants. Leurs auberges sont situées respectivement sur l\’Éminence de l\’Aldor et sur le Degré des Clairvoyants.', `VerifiedBuild`=26365 WHERE (`ID`=19216 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il y a deux banques à Shattrath, toutes deux situées le long du bord du noyau central de la ville. L\’une est tenue par les Draeneï de l\’Aldor, l\’autre par les Elfes de sang des Clairvoyants.', `Text1_lang`='Il y a deux banques à Shattrath, toutes deux situées le long du bord du noyau central de la ville. L\’une est tenue par les Draeneï de l\’Aldor, l\’autre par les Elfes de sang des Clairvoyants.', `VerifiedBuild`=26365 WHERE (`ID`=19211 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je n\’arrive pas à croire qu\’ils m\’aient fait déplacer mon transporteur hors de la ville ! La plupart des Gnomes affectés par l\’accident ont retrouvé un semblant de forme humanoïde en quelques semaines seulement, et participer à une expérience gnome de cette ampleur aurait dû être un honneur pour eux !\n\nSans compter que nous avions bien besoin de leurs œufs !', `VerifiedBuild`=26365 WHERE (`ID`=19132 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Comment ça ? Un pauvre petit Gnome ? Je ferai mieux la prochaine fois, ô Tout-puissant.', `VerifiedBuild`=26365 WHERE (`ID`=19109 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='C\'est mon étude de cette culture archaïque qu\'incarnait le clan Sire-tonnerre qui m\'a amenée ici.$B$BLes membres du clan étaient faibles, et succombèrent à l\'illusion de puissance que leur donnait la transformation en Gangr\’orcs par le sang de Mannoroth.$B$BJ\'ai lu tous leurs écrits. Je souhaite tout particulièrement retrouver un artéfact, un tambour, une flèche et une tablette qui furent perdus quand nous avons écrasé les ogres de Flèchelame pour les repousser jusque dans leur bastion.', `VerifiedBuild`=26365 WHERE (`ID`=18970 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Cesssez de frapper le commandant glissssentaille, Orc ! Je vais parler ! La forrrmule… Elle est dissssimulée dans un coffre à la halte de Glisssentaille… Les nagas conssservent la clé…', `VerifiedBuild`=26365 WHERE (`ID`=18965 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Jamais je n\’oublierai ce jour, $r. Jamais. Bien que j\’avais depuis longtemps abandonné le chamanisme pour la magie de l\’ombre, je sentis la rupture. Une si profonde sensation de perte… Les mots ne suffiraient pas à vous l\’expliquer.$B$BQuand le lien avec les éléments fut rompu, tous les Orcs de Draenor en furent altérés. Si le moindre doute sur la puissance de l\’ombre avait jamais existé, il fut balayé par le déferlement de la lave en fusion sur la terre déchirée.', `VerifiedBuild`=26365 WHERE (`ID`=18942 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Lorsque vous vous tenez à l\’Autel de la damnation et voyez les esprits des damnés qui prient agenouillés, il y a un élément que vous ne voyez pas : moi, présent aux côtés de Gul\’dan.$B$B<Oronok hoche la tête.>$B$BJe vous ai dit avoir tenu bien des rôles, n\’est-ce pas ? Eh bien, l\’un de ces rôles était celui de lieutenant de Gul\’dan.$B$BJ\’ai accompli d\’horribles choses dans ma vie. J\’ai massacré hommes, femmes et enfants. J\’ai dansé dans le sang de Draeneï innocents... et pourtant...$B$B<Oronok s\’étouffe.>', `VerifiedBuild`=26365 WHERE (`ID`=18940 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Très bien. Les mots que vous recherchez portent un nom, $n. On les appelle la Formule de damnation. Il s\’agit des mots que Gul\’dan lui-même a prononcé pour déchirer cette terre. Du sort qui a rompu les derniers liens que gardaient les Orcs avec les éléments de Draenor.$B$B<Oronok hoche la tête.>$B$BOui, c\’est cette formule que vous devez reconstituer. Ce sont les mots de cette formule que vous devrez au final prononcer.', `VerifiedBuild`=26365 WHERE (`ID`=18936 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous devez être $gexalté:exaltée; par les Elfes de sang avant que je ne vous apprenne une compétence de monte, $c.', `VerifiedBuild`=26365 WHERE (`ID`=18932 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous devez obtenir l\’amitié des Elfes de sang avant que je ne vous apprenne une compétence de monte, $c.', `VerifiedBuild`=26365 WHERE (`ID`=18931 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ces Orcs sont vraiment impatients de faire valoir leurs revendications sur l\'Outreterre ! Si je vous disais combien ils me payent pour mon allégeance, vous pourriez me tuer de jalousie !', `VerifiedBuild`=26365 WHERE (`ID`=18926 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Vous ne pourrez pas acheter de faucon-pérégrin ici tant que vous n\’aurez pas prouvé votre valeur et votre dévouement envers notre peuple.$B\n\nNe revenez pas avant d\’être $gexalté:exaltée; par les Elfes de sang.\n$b\nAllons, du balai !', `VerifiedBuild`=26365 WHERE (`ID`=18925 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je suis venu en Outreterre à la recherche de puissants artéfacts pour aider la Ligue d\’Arathor contre les Profanateurs au bassin Arathi… Mais tout ce que j\’y ai trouvé, c\’est ce super pagne magique, un ridicule acolyte gnome et un Mort-vivant égotiste qui me colle et n\’arrête pas de répéter que je lui ai piqué son château des ombres ou quelque chose comme ça.\n$B\nQuoi qu\’il en soit, voudriez-vous vous joindre au combat de la Ligue pendant que je continue mes recherches ?', `VerifiedBuild`=26365 WHERE (`ID`=18849 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Encore à l\’heure actuelle, les Morts-vivants défendent leur liberté contre la Ligue d\’Arathor au bassin Arathi. Aiderez-vous les Profanateurs à vaincre les Humains et leurs compagnons de l\’Alliance ?', `VerifiedBuild`=26365 WHERE (`ID`=18825 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='À la demande de l\'Alliance, Bronwyn et moi-même avons été dépêchés ici en tant que représentants de la Ligue des explorateurs pour étudier ces montagnes.$B$BDe vous à moi, après un long séjour en Silithus, nous avions surtout besoin de vacances, et cet endroit semble suffisamment agréable.', `VerifiedBuild`=26365 WHERE (`ID`=18802 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Orcs de ma citadelle boiront votre sang et se repaîtront de vos chairs !', `VerifiedBuild`=26365 WHERE (`ID`=18776 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='La halte de l\’Expédition est un poste difficile à tenir, mais capital pour la défense du bastion de l\’Honneur. Les informations qui y sont collectées et l\’engagement de nos soldats contre la Légion et les Orcs de Zeth\’Gor sont un apport inestimable à la campagne de l\’Alliance dans les Flammes infernales.', `VerifiedBuild`=26365 WHERE (`ID`=18772 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='C\’est ici que Gul\’dan rompit le lien entre les Orcs et les esprits des éléments. L\’annihilation totale des Draeneï ne suffisait pas à étancher son inextinguible soif de puissance. Il fallait aussi qu\’il détruise Draenor, qu\’il rase cette terre et en draine toute l\’énergie pour en faire usage à la guerre qu\’il menait.$B$BIl ne subsiste plus maintenant que les restes de sa folie.$B$BRegardez cet autel, $r : ce monde est à jamais hanté...', `VerifiedBuild`=26365 WHERE (`ID`=18736 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='La nuit est trop calme, Theloria. Les Elfes de sang mijotent quelque chose. Je le sens.', `VerifiedBuild`=26365 WHERE (`ID`=18691 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='La journée est trop calme, Theloria. Les Elfes de sang mijotent quelque chose. Je le sens.', `VerifiedBuild`=26365 WHERE (`ID`=18689 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='On trouve pas mal d\’artisans talentueux à Brume-Azur. Que cherchez-vous ?', `Text1_lang`='On trouve pas mal d\’artisans talentueux à Brume-Azur. Que cherchez-vous ?', `VerifiedBuild`=26365 WHERE (`ID`=18324 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='La réserve de textiles de l\’Exodar, qui était jadis énorme, a dangereusement baissé. Votre réputation auprès des Draeneï augmenterait si vous nous aidiez avec un don de tissu.', `VerifiedBuild`=26365 WHERE (`ID`=18296 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ce n\’est pas lui qui m\’inquiète. Ce sont tous ces bleus qui viennent de débarquer… et ces Elfes violets qu\’ils ont amenés avec eux, et qui me filent la chair de poule. Ils n\’ont même pas encore fait leurs preuves.', `VerifiedBuild`=26365 WHERE (`ID`=18212 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Qu\’il gagne ou qu\’il perde, le lieutenant est cinglé de garder un Orc domestique comme ça. On peut pas leur faire confiance. Ce sont des animaux, pour sûr.', `Text1_lang`='Qu\’il gagne ou qu\’il perde, le lieutenant est cinglé de garder un Orc domestique comme ça. On peut pas leur faire confiance. Ce sont des animaux, pour sûr.', `VerifiedBuild`=26365 WHERE (`ID`=18208 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ouais, je l\’ai vu combattre. C\’est un Orc, énorme comme ils le sont tous. J\’ai perdu une semaine de paye en pariant contre lui. Je ne r\’ferai plus la même erreur !', `Text1_lang`='Ouais, je l\’ai vu combattre. C\’est un Orc, énorme comme ils le sont tous. J\’ai perdu une semaine de paye en pariant contre lui. Je ne r\’ferai plus la même erreur !', `VerifiedBuild`=26365 WHERE (`ID`=18207 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Peut-être que je peux vendre tout ça aux autres Gobelins ? Je vais devenir riche ! J\’ai bien dit riche !', `VerifiedBuild`=26365 WHERE (`ID`=18134 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Proposer ? Je propose que nous nous préparions. Que nous préparions ceux que nous aimons, nos familles et amis à la possibilité d\’un holocauste des Morts-vivants.', `VerifiedBuild`=26365 WHERE (`ID`=18095 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Jour après jour, tout ce que nous faisons, c\’est trimer dans cette mine. Un Elfe de sang n\’est pas fait pour ce genre de travail manuel.', `VerifiedBuild`=26365 WHERE (`ID`=18010 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je suis un Elfe de sang. Le travail manuel, ce n\’est pas pour moi.', `VerifiedBuild`=26365 WHERE (`ID`=18003 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='J\’suis sur une montagne avec un vieil arbre énorme. Y a des Elfes qui courent partout…', `VerifiedBuild`=26365 WHERE (`ID`=17976 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il finit par détruire tout le fort, libérer tous les Orcs emprisonnés dans les camps et prendre le pouvoir comme roi des Orcs… Chef de guerre, ou un truc comme ça.', `VerifiedBuild`=26365 WHERE (`ID`=17974 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Moulin-de-Tarren est détruit par une attaque incroyable des Morts-vivants et devient à jamais l\’ennemie d\’Austrivage.', `VerifiedBuild`=26365 WHERE (`ID`=17971 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='La Horde ne peut pas laisser le contrôle des cellules de mana de l\’Outreterre aux draeneï et à leur envahissants Alliés. Voulez-vous vous joindre à l\’assaut des Elfes de sang sur l\’Œil du cyclone ?', `Text1_lang`='La Horde ne peut pas laisser le contrôle des cellules de mana de l\’Outreterre aux draeneï et à leur envahissants Alliés. Voulez-vous vous joindre à l\’assaut des Elfes de sang sur l\’Œil du cyclone ?', `VerifiedBuild`=26365 WHERE (`ID`=17963 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Rassemblez ces pièces détachées de saccageur gangrené. Nous allons faire une surprise aux Gobelins !', `VerifiedBuild`=26365 WHERE (`ID`=17959 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Marchand des vents Marid, je suis revenu avec de nouvelles informations sur les drakes du Néant. Je suis prêt à devenir votre partenaire et à conclure un marché, et pour un petit supplément, je m\’occuperai de cette gêneuse d\’Elfe et de son ami humain.', `Text1_lang`='Marchand des vents Marid, je suis revenue avec de nouvelles informations sur les drakes du Néant. Je suis prête à devenir votre partenaire et à conclure un marché, et pour un petit supplément, je m\’occuperai de cette gêneuse d\’Elfe et de son ami humain.', `VerifiedBuild`=26365 WHERE (`ID`=17754 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='La forêt chante les germes du changement, mais elle crie aussi de douleur.$B$BNos nouveaux amis, les Draeneï, sont ici pour nous aider, tout comme vous. Nous devons mettre un terme à la destruction de mes frères et sœurs les arbres avant que tout ne soit perdu et qu\’Orneval n\’ait disparu.', `VerifiedBuild`=26365 WHERE (`ID`=17750 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il est bon de voir un ami de l\’Aldor dans ces contrées reculées. Je crains ne plus pouvoir supporter encore longtemps les bavardages insolents de ces Elfes.', `VerifiedBuild`=26365 WHERE (`ID`=17744 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ce… C\'était un rêve ? Brume-Azur, Brume-Sang, le marécage de Zangar, Ombrelune… Et même Zul\'Aman… Jane ! Jane, où es-tu ?!', `VerifiedBuild`=26365 WHERE (`ID`=17596 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='En tant que représentant du Consortium, je prête toujours une grande attention aux ramifications que mes interactions peuvent avoir sur nos contrats et nos affaires.$B$BIl est malheureux que les Elfes de sang ne traitent pas leurs partenaires avec le même respect.', `VerifiedBuild`=26365 WHERE (`ID`=17553 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Une image instable d\’un commandant des Elfes de sang se forme. D\’après le décor, elle semble se trouver dans le donjon de la Tempête.', `VerifiedBuild`=26365 WHERE (`ID`=17529 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il y a vingt ans environ, l\’Alliance a lancé la première expédition vers ce monde, dans l\’espoir d\’éradiquer les Orcs à jamais. Ce fut… un terrible conflit. Bien des âmes courageuses ont péri pour assurer la sécurité d\’Azeroth.$B$BMerci, $n, d\’avoir aidé ces âmes à trouver la paix.', `VerifiedBuild`=26365 WHERE (`ID`=17522 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il y a vingt ans environ, l\’Alliance a lancé la première expédition vers ce monde, dans l\’espoir d\’éradiquer les Orcs à jamais. Ce fut… un terrible conflit. Bien des âmes courageuses ont péri pour assurer la sécurité d\’Azeroth.$B$BMais certaines âmes restent… bloquées dans un combat éternel.', `VerifiedBuild`=26365 WHERE (`ID`=17521 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Orcs. J\’ai l\’impression de les avoir combattus toute ma vie.$B$BIl y a vingt ans, nous avons écrasé la Horde et abattu les murs de leur citadelle des Flammes infernales. Nous avons poussé ces misérables peaux-vertes au-delà de leurs tout derniers retranchements ! Ensuite, à part quelques petites escarmouches, les Orcs, brisés, ne nous ont plus jamais menacés.$B$BJusqu\’à ce jour.', `VerifiedBuild`=26365 WHERE (`ID`=17490 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Parlez-moi des Orcs des Flammes infernales.', `Text1_lang`='Parlez-moi des Orcs des Flammes infernales.', `VerifiedBuild`=26365 WHERE (`ID`=17489 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='$n, les Fils de Lothar combattent ici, dans la péninsule des Flammes infernales, depuis deux décennies. Nous sommes harcelés de tous les côtés par les Orcs indigènes et les démons infernaux de la Légion ardente et, bien que nos cœurs battent encore d\'une farouche énergie, les années de guerre ont réduit nos effectifs.$B$BVotre arrivée, et l\'afflux d\'une nouvelle génération de héros d\'Azeroth, sont vraiment de bonnes nouvelles !', `VerifiedBuild`=26365 WHERE (`ID`=17484 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il y a bien des années, mes hommes et moi avons suivi le sorcier Khadgar et le paladin Turalyon jusqu\’à ce monde. Notre seul objectif était d\’annihiler les Orcs et leurs infectes lignées. Mais quelque chose a affreusement mal tourné dans les dernières heures de notre guerre.$B$BÀ l\’ultime instant, cette noire terre fut déchirée de part en part, et mes chers compagnons sombrèrent dans le vide.', `VerifiedBuild`=26365 WHERE (`ID`=17470 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je ne sais pas ce qui est le pire : être mangé par des Gangr\’orcs ou travailler pour cet esclavagiste de Razelcraz ! Enfin bon, merci quand même !', `VerifiedBuild`=26365 WHERE (`ID`=17410 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Quand le bataillon d\’Elfes de sang traversa ce pont, les exarques et redresseurs de torts de l\’Aldor prirent position pour défendre la Terrasse de la Lumière. C\’est alors que l\’inattendu arriva.', `VerifiedBuild`=26365 WHERE (`ID`=17274 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Quand la Légion ardente retourna la Horde contre les Draeneï, la bataille la plus terrible eut lieu ici. Les Draeneï combattirent jusqu\’au dernier, mais la ville finit par tomber.', `VerifiedBuild`=26365 WHERE (`ID`=17268 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='L\’Aldor rassemble des fidèles de la Lumière, et le pardon et la rédemption sont des valeurs qu\’ils apprécient. Cependant, il fut difficile pour eux d\’oublier les exactions des Elfes de sang commises sous les ordres de Kael.', `VerifiedBuild`=26365 WHERE (`ID`=17253 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='La défection de Voren\’thal et de ses fidèles fut la plus grande perte jamais subie par les forces de Kael. Et il ne s\’agissait pas là de n\’importe quels Elfes de sang. Nombre des plus brillants érudits et magistères de Kael avaient suivi l\’influence de Voren\’thal.', `VerifiedBuild`=26365 WHERE (`ID`=17250 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Elfes de sang déposèrent leurs armes devant les défenseurs de la ville. Leur chef, un ancien connu sous le nom de Voren\’thal, pénétra sur la Terrasse de la Lumière et exigea de parler à A\’dal.', `VerifiedBuild`=26365 WHERE (`ID`=17247 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il ne fallut cependant pas attendre longtemps avant que la ville ne soit à nouveau assaillie. Cette fois, l\’attaque venait des armées d\’Illidan. Un important régiment d\’Elfes de sang avait été envoyé par l\’allié d\’Illidan, Kael\’thas Haut-Soleil, pour détruire la ville.', `VerifiedBuild`=26365 WHERE (`ID`=17245 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Qu\’est-ce que cette créature ? On dirait un Gobelin violet.', `VerifiedBuild`=26365 WHERE (`ID`=17237 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Vous savez, on pourrait facilement vous remplacer par des Gobelins. C\’est ce que vous voulez ???', `VerifiedBuild`=26365 WHERE (`ID`=17203 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Il s\’ensuivit de terribles représailles pour la défection des troupes. Illidan et Kael\’thas attaquèrent Shattrath sans relâche.$B$BDes armées d\’Elfes de sang, de nagas et de Roués déferlaient sur la ville pour se voir repoussées par la puissance des Sha\’tar.$B$BUne chose devint claire pour les naaru : s\’ils voulaient pourchasser la Légion, il faudrait d\’abord vaincre Illidan.', `VerifiedBuild`=26365 WHERE (`ID`=17162 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Pris au piège sur ce désert ravagé, nous avons fait de notre mieux pour survivre. Avec le temps, nous avons senti l\’approche d\’un nouveau mal venu du Néant distordu. Un mal plus abject encore que les ténébreux Orcs : la Légion ardente elle-même cherchait à s\’emparer de cette... Outreterre.', `VerifiedBuild`=26365 WHERE (`ID`=17159 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='J\’ai combattu aux côtés du seigneur Anduin Lothar pour repousser la Horde de nos terres en Azeroth. En tant qu\’héritier spirituel de Medivh, je me devais de le faire.$B$BQuand enfin est venu le temps d\’envahir ce sinistre monde et de mettre fin une bonne fois pour toutes à la malveillance des Orcs, je me suis avancé avec mes amis Turalyon, Alleria, Danath et Kurdran.', `VerifiedBuild`=26365 WHERE (`ID`=17157 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le jeune Orc croyait en votre salut. Mais il ne savait pas quel feu fait rage dans votre âme quand vous sentez, dans votre cœur, que nous sommes les mêmes.', `VerifiedBuild`=26365 WHERE (`ID`=17142 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Lui répéter ? Grande-mère, son père est mort pour nous offrir à tous une vie libre de la malédiction du sang. Je ne le lui dirai pas. Je lui montrerai ! Je lui montrerai à lui, et à quiconque douterait de la volonté de Hurlenfer, ce que Grom Hurlenfer a fait pour tous les Orcs !', `VerifiedBuild`=26365 WHERE (`ID`=17132 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Oh ! Il faut que quelqu\’un éteigne ce feu pour que je puisse aller sauver le Gobelin !', `VerifiedBuild`=26365 WHERE (`ID`=17072 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ils étaient les meilleurs amis du monde, savez-vous. Durotan et Orgrim s\’aventurèrent un jour dans la forêt de Terokkar, où un ogre faillit les tuer. Si les Draeneï n\’étaient pas arrivés à leur secours… Penser à cette époque m\’attriste. Nous avons infligé de si terribles choses aux draeneï...', `VerifiedBuild`=26365 WHERE (`ID`=17030 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je ne suis, devant vous, qu\’un jeune Orc à la recherche de souvenirs perdus.', `VerifiedBuild`=26365 WHERE (`ID`=17018 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je n\’ai jamais senti une telle force émaner d\’un Orc. Est-il… mortel… ?', `Text1_lang`='Je n\’ai jamais senti une telle force émaner d\’un Orc. Est-il… mortel… ?', `VerifiedBuild`=26365 WHERE (`ID`=17009 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Kirin\’Var a survécu à vingt longues années et d\’innombrables attaques des Orcs, mais il n\’a pu résister aux forces de Kael\’thas. Un homme qui se disait autrefois membre du Kirin Tor nous a tous assassinés, tous sauf un.$B$BL\’archimage Vargoth en a réchappé, alors Kael\’thas l\’a enfermé dans la Tour pourpre, qu\’il a scellée avec une magie si puissante qu\’elle empêche tout membre du Kirin Tor de traverser.', `VerifiedBuild`=26365 WHERE (`ID`=16963 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='J\’espère que les Elfes de sang qui sont de l\’autre côté ne pensent pas que je les espionne !', `VerifiedBuild`=26365 WHERE (`ID`=16952 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='L\’archimage Vargoth me confia son bâton lorsqu\’il me dit de fuir lors de l\’attaque des Elfes de sang contre la Tour pourpre. J\’obéis à ses ordres et espérais trouver refuge dans le vieux camp draeneï d\’Arklon.$B$BMais le répit fut de courte durée, car j\’avais marché tout droit vers l\’avant-garde d\’une cohorte de la Légion ardente. Les démons m\’arrachèrent le bâton, mais je réussis à m\’enfuir vivant.$B$BBien que mon maître ignore tout de mon échec, j\’en ressens encore la honte.', `VerifiedBuild`=26365 WHERE (`ID`=16869 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Grande-mère, je n\’avais jamais eu cet honneur. Durotan mourut bien avant mon époque, mais ses exploits héroïques sont connus de tous sur mon monde. Les Orcs d\’Azeroth habitent une région appelée Durotar, en hommage à votre fils. Et… <Vous faites une pause pour reprendre votre souffle et réfléchir à ce que vous allez dire à la grande-mère.>', `Text1_lang`='Grande-mère, je n\’avais jamais eu cet honneur. Durotan mourut bien avant mon époque, mais ses exploits héroïques sont connus de tous sur mon monde. Les Orcs d\’Azeroth habitent une région appelée Durotar, en hommage à votre fils. Et… <Vous faites une pause pour reprendre votre souffle et réfléchir à ce que vous allez dire à la grande-mère.>', `VerifiedBuild`=26365 WHERE (`ID`=16853 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='<La grande-mère Geyah vous sourit.>$B$BVous me rappelez mon fils, $n.$B$BC\’était une montagne, inamovible. Un pilier de force et d\’honneur.$B$B<Les yeux de Geyah s\’emplissent de larmes.>$B$BIl s\’est sacrifié pour notre survie. Pour que des générations d\’Orcs à venir puissent vivre dans un monde libre de la malédiction du sang... Sans Gul\’dan et sa ténébreuse Horde...$B$B<Geyah regarde au loin, perdue dans ses pensées.>$B$BOh, Durotan, comme j\’aimerais que tu sois là. Ton peuple a si désespérément besoin de toi...', `VerifiedBuild`=26365 WHERE (`ID`=16850 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Ainsi donc, les Elfes de sang se croient à deux doigts de régner sur le monde…', `VerifiedBuild`=26365 WHERE (`ID`=16682 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Pour que vous ne vous sentiez pas mis à l\’écart, je précise que tout ce que j\’ai dit, c\’est que les Elfes de sang sont vraiment désirables. Mais on perd un peu le sens à la traduction.', `VerifiedBuild`=26365 WHERE (`ID`=16679 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='À qui j\'espère faire croire ça ? Si vous faites affaire avec un Gobelin, il vaut mieux avoir une bourse blindée.', `VerifiedBuild`=26365 WHERE (`ID`=16672 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Avant, on ne pouvait pas faire confiance à un Gobelin…', `VerifiedBuild`=26365 WHERE (`ID`=16671 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='L\’autre jour, j\’en ai vu un qui descendait la rue en criant, « Dans mon couple, c\’est moi qui fais le Gnome ! »', `VerifiedBuild`=26365 WHERE (`ID`=16670 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Autrefois, les Gnomes étaient un peuple aimable et respectueux...', `VerifiedBuild`=26365 WHERE (`ID`=16669 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Quelle belle assemblée. Les Naines ont même l\’air de s\’être rasées.', `VerifiedBuild`=26365 WHERE (`ID`=16648 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le comédien imite un Orc.', `VerifiedBuild`=26365 WHERE (`ID`=16584 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le comédien imite une Humaine.', `VerifiedBuild`=26365 WHERE (`ID`=16583 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le comédien imite un Gnome.', `VerifiedBuild`=26365 WHERE (`ID`=16581 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Le comédien imite un Tauren.', `VerifiedBuild`=26365 WHERE (`ID`=16580 AND `locale`='frFR');
