UPDATE `broadcast_text_locale` SET `Text1_lang`='Il est regrettable que les Orcs de la citadelle des Flammes infernales fassent partie de nos nombreux ennemis dans cette péninsule.$B$BD\’un autre côté... quel adversaire pourrait mieux défier et mettre à l\’épreuve la Horde... que des Orcs ?', `VerifiedBuild`=26365 WHERE (`ID`=16141 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='L\’arène des Épreuves était autrefois un lieu plein de gloire et d\’honneur. Et puis le cartel de Gentepression est arrivé, avec ses chariots pleins d\’or et ses plumes plus affûtées que n\’importe quelle hache des Orcs...', `VerifiedBuild`=26365 WHERE (`ID`=16063 AND `locale`='frFR');
