UPDATE `broadcast_text_locale` SET `Text_lang`='Un druide ? Ah... Shalannius, vous cherchez Shalannius. Vous trouverez cet Elfe de la nuit à l\’extérieur de l\’Exodar, près de l\’amarrage de Valaar.', `Text1_lang`='Un druide ? Ah... Shalannius, vous cherchez Shalannius. Vous trouverez cet Elfe de la nuit à l\’extérieur de l\’Exodar, près de l\’amarrage de Valaar.', `VerifiedBuild`=26365 WHERE (`ID`=15820 AND `locale`='frFR');
