UPDATE `broadcast_text_locale` SET `Text1_lang`='Les Humains nous ont abandonnés à l\’heure la plus grave ! Leur prince lui-même a conduit l\’attaque sur nos terres.', `VerifiedBuild`=26365 WHERE (`ID`=15677 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Même les Orcs à la peau brune prennent plus de temps pour mourir. Quel gâchis… Au suivant !', `VerifiedBuild`=26365 WHERE (`ID`=15581 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Elfes de sang Aile-de-feu… Pour une raison ou une autre, ils veulent nous arrêter.$B$B<Le gardien Arblos lutte visiblement pour garder sa raison encore quelques secondes.>$B$BIls… Ils ont envoyé l\’un des Roués comme… comme émissaire… Mais il avait une bombe !$B$BIls sont morts, ils sont tous morts ! Ils ont tenté de fuir ! Les plus chanceux n\’ont rien su.$B$BJe me trouvais à la limite du rayon de l\’explosion. C\’était horr… IL NOUS REGARDE ENCORE !$B$B<Arblos prend un air abattu et son regard semble vous traverser, comme si vous n\’existiez pas.>', `VerifiedBuild`=26365 WHERE (`ID`=15558 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Et $n vient de s\’effondrer comme un vieux sac de crânes d\’Orcs !', `VerifiedBuild`=26365 WHERE (`ID`=15484 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Pourquoi voulez-vous me dévorer alors que vous avez tous ces Orcs à manger ?', `VerifiedBuild`=26365 WHERE (`ID`=15423 AND `locale`='frFR');
