UPDATE `broadcast_text_locale` SET `Text_lang`='Sentez le pouvoir des Orcs de Rochenoire !', `Text1_lang`='Sentez le pouvoir des Orcs de Rochenoire !', `VerifiedBuild`=26365 WHERE (`ID`=1874 AND `locale`='frFR');
