UPDATE `broadcast_text_locale` SET `Text_lang`='J\’ai remarqué un feu sur cette île là-bas. Un Humain, aussi. Allons vérifier, $n.', `VerifiedBuild`=26365 WHERE (`ID`=1669 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je suis impatient d\’entendre ce qu\’un Orc aussi fâché a à dire...', `VerifiedBuild`=26365 WHERE (`ID`=1664 AND `locale`='frFR');
