UPDATE `broadcast_text_locale` SET `Text_lang`='Les Gobelins remportent la course ! Vous aurez plus de chance la prochaine fois, les Gnomes.', `VerifiedBuild`=26365 WHERE (`ID`=1538 AND `locale`='frFR');
