UPDATE `broadcast_text_locale` SET `Text_lang`='Ah, sentez la magie envahir la grotte, mes petits Elfes... hahaha.', `VerifiedBuild`=26365 WHERE (`ID`=1406 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='J\’ai échoué... J\’ai manqué à mon devoir... déçu mon peuple… Les Worgens sont déchaînés et la Faux de ma déesse est perdue.', `VerifiedBuild`=26365 WHERE (`ID`=1359 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text1_lang`='Plus moelleuse qu\’une bière elfique et plus forte que celle des Orcs ! Il n\’y a pas de meilleure boisson dans tout Hurlevent !', `VerifiedBuild`=26365 WHERE (`ID`=1205 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Maîtresse, y a-t-il des Elfes de la nuit à Hurlevent ? Je n\’ai encore jamais vu d\’Elfe de la nuit.', `Text1_lang`='Maîtresse, y a-t-il des Elfes de la nuit à Hurlevent ? Je n\’ai encore jamais vu d\’Elfe de la nuit.', `VerifiedBuild`=26365 WHERE (`ID`=1138 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='J\’ai entendu dire qu\’il y a encore des Orcs enterrés dessous.', `Text1_lang`='J\’ai entendu dire qu\’il y a encore des Orcs enterrés dessous.', `VerifiedBuild`=26365 WHERE (`ID`=1134 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Les Orcs sont plus malins que ces messagers ! Mais où diable sont-ils ?', `VerifiedBuild`=26365 WHERE (`ID`=739 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Par ici ! Dans la pièce. Avez-vous trouvé la clé de mes entraves ? C\’est l\’Humain Borhuin qui les a.', `VerifiedBuild`=26365 WHERE (`ID`=598 AND `locale`='frFR');
UPDATE `broadcast_text_locale` SET `Text_lang`='Je ne ferai que vous ralentir, mais ce ne sont pas des Humains au teint porcin qui m\’empêcheront de recouvrer ma liberté !', `VerifiedBuild`=26365 WHERE (`ID`=595 AND `locale`='frFR');
