(138094, 'zhTW', '', '幫幫我！', 26365),
(138093, 'zhTW', '', '快遁入暗影！', 26365),
(138092, 'zhTW', '', '快撤退！', 26365),
(138091, 'zhTW', '', '撤退！', 26365),
(138090, 'zhTW', '', '我們不害怕未知的事物。', 26365),
