INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES
(50432, 'esES', 'Innovación titánica', '', '', '', '', '', '', '', '', 26365),
(50374, 'esES', 'La sangre de Azeroth', 'Entrega el mensaje de Magni Barbabronce.', '¿Acaso los líderes de la Alianza y de la Horda saben por qué están luchando?\n\n¡La sustancia que sale de la herida es la sangre de Azeroth!\n\nDebemos cerrar la herida, ¡no aprovecharnos de ella para obtener poder! Azeroth es nuestro único hogar, ¡no podemos dejar que muera!\n\nConfío en ti, $n, y sé que la Alianza te tiene en muy alta estima. ¡Debes razonar con ellos y hacer que detengan esta locura!\n\nEs la única esperanza que le queda a nuestro mundo...', '', '', '', '', '', '', 26365),
(50373, 'esES', 'Una llegada reciente', 'Habla con el archimago Khadgar.', 'Parece que la situación en Silithus cada vez está cobrando más atención.\n\nEl archimago Khadgar ha llegado mientras no estabas. Creo que ha venido para hablar contigo, $n.\n\nParecía urgente. Yo hablaría con él lo antes posible, si fuera tú...', '', '', '', '', '', '', 26365),
(50372, 'esES', 'Investigación desértica', 'Habla con Tammy Girarremiendo.', 'La espada oscura de Sargeras ha extendido una sombra de destrucción por estas tierras.\n\nParecía que el ataque había destruido las colmenas de los silítidos, pero he recibido informes de que los silítidos están regresando.\n\nDebemos descubrir cómo evitaron la destrucción y qué efecto está teniendo la espada en sus colmenas.\n\nLa corona quiere al resto de silítidos diseccionados y estudiados.\n\nTammy Girarremiendo dirige la investigación y necesita a alguien que se ocupe de la \"recogida\".', '', '', '', '', '', '', 26365),
(50371, 'esES', 'Citación a Ventormenta', 'Reúnete con Mathias Shaw en Ventormenta.', 'El rey Wrynn solicita que vuelvas a Ventormenta cuanto antes. Hay un asunto urgente que requiere de tu atención.\n\nTe daré todos los detalles en cuanto llegues.\n\nTe estaré esperando en Reposo del León. Date prisa... ¡No podemos desaprovechar esta oportunidad!', '', '', '', '', '', '', 26365),
(50364, 'esES', 'La sangre de Azeroth', 'Entrega el mensaje de Magni Barbabronce.', '¿Acaso los líderes de la Alianza y de la Horda saben por qué están luchando?\n\n¡La sustancia que sale de la herida es la sangre de Azeroth!\n\nDebemos cerrar la herida, ¡no aprovecharnos de ella para obtener poder! Azeroth es nuestro único hogar, ¡no podemos dejar que muera!\n\nConfío en ti, $n, y sé que la Horda te tiene en muy alta estima. ¡Debes razonar con ellos y hacer que detengan esta locura!\n\nEs la única esperanza que le queda a nuestro mundo...', '', '', '', '', '', '', 26365),
(50360, 'esES', 'La petición de Khadgar', 'Habla con el archimago Khadgar.', 'El archimago Khadgar llegó mientras no estabas. Parece que ha venido a hablar contigo, $n.\n\nRápido, ve a ver qué necesita. Lo quiero fuera de aquí lo antes posible.\n\n¡Lo último que necesitamos es a un humano husmeando en los asuntos de la Horda!', '', '', '', '', '', '', 26365),
(50358, 'esES', 'Investigación desértica', 'Habla con Pixni Bombarrobín.', 'La espada oscura de Sargeras ha extendido una sombra de destrucción por estas tierras.\n\nParecía que el ataque había destruido las colmenas de los silítidos, pero he recibido informes de que los silítidos están regresando.\n\n¡Si dependiese de mí, le prendería fuego a esas malditas colmenas de una vez por todas! Sin embargo, no depende de mí.\n\nLa Dama Oscura quiere diseccionar y estudiar a los silítidos supervivientes.\n\nPixni Bombarrobín dirige la investigación y necesita a alguien que se ocupe de la \"recogida\".', '', '', '', '', '', '', 26365),
(50341, 'esES', 'Un descubrimiento reciente', 'Habla con Nathanos Clamañublo.', 'Hoy mismo, los líderes de la Horda se han reunido en un banquete para celebrar nuestra victoria sobre la Legión Ardiente.\n\nDurante la comida, Gallywix ha solicitado una audiencia privada con la Dama Oscura.\n\nParece que ha encontrado una sustancia misteriosa en Silithus... un mineral mágico diferente a todos los que conocemos.\n\nLa Jefa de Guerra me ha ordenado que te ponga al día para prepararte para una misión especial.', '', '', '', '', '', '', 26365),
(50338, 'esES', 'El Equipo G', 'Mata al radiactivo viscoso.', '¿Cómo has llegado hasta aquí, $n?\n\nSoy Murd Doc, del Equipo G, y lo cierto es que nos vendría bien la ayuda de $gun:una; $r como tú.\n\nAyúdanos a derrotar a los habitantes irradiados que se han establecido en Gnomeregan y te convertirás en miembro de honor del Equipo G.\n\nCoge este paracaídas, salta a La Sala de Máquinas y derrota al radiactivo viscoso. Cuando termines, informa al comando M. E. Barrecus en el pasillo oeste. ¡Seguro que os lleváis bien!', '', '', '', '', '', '', 26365),
(50337, 'esES', 'El Equipo G', 'Mata al Electrocutor 6000.', 'Ese Murd Doc está majara, $c. Nunca jamás te montes en un avión con ese gnomo.\n\nEl sigilo no suele ser lo mío, y está claro que no va a ser lo tuyo. Baja por este pasillo hasta el Aeropuerto y deshazte del Electrocutor 6000.\n\nEl Electrocutor está al cargo de los aviones...\n\n... Y yo odio los aviones. ¡No pienso subir a ningún tipo de avión!\n\nDespués utiliza este paracaídas para dejarte caer y habla con el comando Fénix para comenzar tu siguiente misión.', '', '', '', '', '', '', 26365),
(50336, 'esES', 'El Equipo G', 'Mata al mekigeniero Termochufe.', 'Yo me alejaría de M. E. Barrecus y de Murd Doc, ninguno de los dos es especialmente estable.\n\nBueno, ¡manos a la obra! Baja por este túnel hasta que llegues a la Cámara Manitas. Espero que sepas qué hacer cuando veas al mekigeniero Termochufe.\n\nNuestro líder, el comando Hann Ibal, te espera por allí. Te recompensará generosamente por tu trabajo.\n\nProbablemente...', '', '¡Cuidado con las bombas!', 'Mekigeniero Termochufe', '', '', '', 26365),
(50335, 'esES', 'Dame mi venganza', 'Derrota a Randolph Moloch, lord Quemado y Hogger.', 'Me da igual a quién le jures lealtad, $r. ¡Necesito que Hogger muera!\n\nÉl y los otros cretinos de este lugar dejado de la mano de Luz han asesinado a mis hombres. Puede que la Alianza y la Horda tengan sus desacuerdos, pero no pienso permitir que se asesine a mi gente a sangre fría.', '', '', '', '', '', '', 26365),
(50319, 'esES', 'En tierras extrañas', 'Busca el tablón de mando del Jefe de Guerra en Orgrimmar.', 'Hola, soy el embajador Guardasombrío.\n\n<Guardasombrío echa un vistazo al papel que tiene en la mano>.\n\nEn nombre de la Jefa de Guerra, es un honor darte la bienvenida a Orgrimmar, $r.\n\n<Te mira, sonríe sin convicción y vuelve a mirar el papel>.\n\nAhora que una vida de aventuras se abre ante ti, te aseguro que... eeh...\n\n¿Sabes qué? Llevo todo el día leyendo esto. Ya estoy harto.\n\nHabla con tu emisario y averigua dónde puedes ir a buscar aventuras. Hala, largo.', '', '', '', '', '', '', 26365),
(50318, 'esES', 'Una llamada oportuna de Rasganorte', 'Reúnete con Vormu en Dalaran, por encima del Bosque Canto de Cristal.', 'Las llamas menguan a la sombra de una masa celeste y el miedo atenaza a los videntes. Busca a la sabia Vormu, en Dalaran, y préstale tu ayuda.\n\nNo, no en este Dalaran. En el otro. Sí, ya sé que todo esto es muy confuso...', '', '', '', '', '', '', 26365),
(50316, 'esES', 'Perturbación detectada: Ulduar', 'Vuelve a vivir la expedición de Ulduar.', 'Llegas tarde. ¿No te acuerdas de la última vez que tuvimos que lidiar con la corrupción que permitió que Yogg-Saron escapara de su prisión bajo Ulduar? Ahí también llegaste tarde. Deberías intentar ser más puntual...\n\nBueno, ¡en marcha! ¡Dioses antiguos! ¡Vigilantes! ¡Titanes! Tacha todo eso rápido de tu lista para que podamos seguir con nuestras cosas. Tenemos otros tiempos y otros lugares a los que ir.\n\n<Vormu te hace gestos con impaciencia para que te vayas>.', '', '', '', '', '', '', 26365),
(50313, 'esES', 'En tierras extrañas', 'Encuentra el tablero de ¡Se busca héroe! en la Ciudad de Ventormenta.', '¡$gBienvenido:Bienvenida; a Ventormenta, $r! El rey Anduin Wrynn te invita a explorar nuestra hermosa ciudad. Oye, tienes pinta de ser $gun gran devoto:una gran devota;. ¡Seguro que te interesa nuestra bonita catedral!\n\nPero ya la visitarás más adelante. Ahora un emisario de los forjados por la Luz te está esperando para hablarte de las aventuras que te aguardan al otro lado de nuestros muros.\n\nTe deseo una feliz estancia, $n. Ah, y perdona el desastre. ¡Me temo que muchos de nuestros ciudadanos no tienen la disciplina por la que sois tan famosos los soldados del Ejército de la Luz!', '', '', '', '', '', '', 26365),
(50305, 'esES', 'En tierras extrañas', 'Encuentra el tablero de ¡Se busca héroe! en la Ciudad de Ventormenta.', '¡$gBienvenido:Bienvenida; a Ventormenta, $r! El rey Anduin Wrynn te invita a explorar nuestra hermosa ciudad. Tienes pinta de disfrutar de la vida nocturna... ¡Tenemos muchas tabernas populares y una escena musical de lo más animada!\n\nPero ya lo descubrirás más adelante. Ahora un emisario de los ren\'dorei te está esperando para hablarte de las aventuras que te aguardan al otro lado de nuestros muros.\n\nTe deseo una feliz estancia, $n. Ah, y un consejillo: intenta mantener bajo control esos poderes oscuros tuyos. ¡No todos en Ventormenta son tan tolerantes como yo!', '', '', '', '', '', '', 26365),
(50303, 'esES', 'En tierras extrañas', 'Busca el tablón de mando del Jefe de Guerra en Orgrimmar.', 'Hola, soy el embajador Guardasombrío.\n\n<Guardasombrío echa un vistazo al papel que tiene en la mano>.\n\nEn nombre de la Jefa de Guerra, es un honor darte la bienvenida a Orgrimmar, $r.\n\n<Te mira, sonríe sin convicción y vuelve a mirar el papel>.\n\nAhora que una vida de aventuras se abre ante ti... ¡Argh!\n\n¡Ya vale! Llevo todo el día leyendo esta presentación y ya estoy harto.\n\nMira a ver dónde puedes luchar contra cosas y luego habla con tu emisario.\n\nHala, largo.', '', '', '', '', '', '', 26365),
(50254, 'esES', 'Un segundo aliado para la causa', 'Elige unos segundos aliados a los que reclutar.', 'Resulta útil tener un aliado nuevo, pero si tuviésemos dos, seríamos aún más fuertes.\n\nLa Alianza sigue conspirando contra nosotros. Debemos actuar rápido o nos atacarán antes de que estemos listos para contrarrestar su ofensiva.\n\nAvísame cuando estés $glisto:lista; para dirigirte a nuestros siguientes aliados. No podemos dudar.', '', '', '', '', '', '', 26365),
(50248, 'esES', 'Un segundo aliado para la causa', 'Elige unos segundos aliados a los que reclutar.', 'Has dado un primer paso importante para fortalecer a la Alianza, $n. Bien hecho.\n\nNo obstante, temo que con solo unos aliados nuevos no seamos capaces de hacer frente a la Horda. Su Jefa de Guerra no descansará hasta que las fuerzas de Orgrimmar superen con creces a los defensores de Ventormenta.\n\nDebo pedirte que te dirijas a nuestros siguientes aliados. No hay tiempo que perder.', '', '', '', '', '', '', 26365),
(50247, 'esES', 'El correo no debe parar', 'Ayuda al jefe de correos a clasificar al menos 15 cartas antes de que se acabe el tiempo.', 'Por culpa de todos los problemas que hemos tenido recientemente, me estoy quedando sin elemailtales que me ayuden a clasificar las cartas.\n\nEs un trabajo sencillo: solo hay que lanzar la carta en el portal correspondiente, y desde ahí saldrá hacia los rincones más remotos de Azeroth y más allá.\n\nPodríamos convertirlo en un juego. ¿Cuántas cartas crees que puedes clasificar en un minuto?', '', '', '', '', '', '', 26365),
(50242, 'esES', 'Elección de aliados', 'Habla con Baine y con Liadrin para saber más sobre los posibles aliados.', 'Baine Pezuña de Sangre y lady Liadrin han sugerido posibles aliados para reforzar nuestras filas. Ambos saben qué es lo mejor para la Horda.\n\nTú has demostrado tu valía como $gcampeón:campeona;, $n. Te confío la tarea de escoger qué camino seguir.\n\nEscucha lo que tengan que decir y, cuando hayas tomado una decisión, comunícamela y pondremos en marcha nuestro plan para fortalecer a la Horda.', '', '', '', '', '', '', 26365),
(50239, 'esES', 'Elección de aliados', 'Habla con Turalyon y con Alleria para saber más sobre los posibles aliados.', 'El alto exarca Turalyon y Alleria Brisaveloz han sugerido posibles aliados para nuestras filas. Estos héroes han dedicado sus vidas a defender nuestro mundo y confío mucho en su consejo.\n\nHas sido $gun valiente campeón:una valiente campeona; para la Alianza, $n. Te confío la tarea de escoger qué camino seguir.\n\nEscucha lo que tengan que decir y, cuando hayas tomado una decisión, dime qué aliados crees que deberíamos reclutar primero.', '', '', '', '', '', '', 26365),
(50232, 'esES', 'Superviviente Crepuscular', 'Hazte con la cabeza de Michiel Zancavacío.', 'Esta tierra solía ser un hervidero de miembros del Martillo Crepuscular.\n\nSe creía que este culto había sido exterminado, pero la oscuridad siempre sobrevive en las sombras.\n\nMuchos de los cultores murieron durante el ataque de Sargeras, pero aún quedan unos cuantos. Además, la destrucción de Silithus ha envalentonado a su líder, Michiel Zancavacío.\n\nNo podemos permitir que ese fanático anime a los supervivientes a pasar a la acción. ¡Tráeme su cabeza!', '', '', '', '', '', '', 26365),
(50231, 'esES', 'Larvas por docenas', 'Recoge $1oa crías silítidas.', '¡¿Cómo sobrevivieron los silítidos a la espada de Sargeras?!\n\nNunca me he topado con una especie capaz de adaptarse tan rápido a los cambios en su entorno como ellos.\n\n¡Tiene que haber alguna forma de domar a esos bichos y de beneficiarnos de su biología evolutiva!\n\nDebemos entrenarlos desde pequeños... Olvídate de los adultos irascibles, ¡necesitamos a las crías!\n\nTráeme todas las larvas silítidas que puedas transportar. ¡Encontraré la forma de domesticarlas!', '', '', '', '', '', '', 26365),
(50230, 'esES', 'La fuente del poder', 'Consigue $1oa cerebros silítidos.', '¡Los silítidos están irritados y son más fuertes que nunca!\n\nYo creo que reciben poder de algún tipo de fuente subterránea. ¡Probablemente cerca de la espada de Sargeras!\n\nMe han ordenado estudiar los efectos que este poder misterioso tiene en los silítidos.\n\n¡Tráeme todos los cerebros silítidos intactos que puedas y comenzaré a investigar!', '', '', '', '', '', '', 26365),
(50229, 'esES', 'Un poquito de tela', 'Recoge $1oa trozos de seda Crepuscular.', '¿Eres tú, $n? Soy yo, ¡Angus Cebatormenta!\n\nHay cultores por doquier, así que intenta no hacer ruido. Acércate y cúbrete la nariz mientras hablamos.\n\n¡Esta es la única letrina en todo Silithus! Créeme: me he pasado toda la noche buscando otra. Decidí colarme en esta por una pequeña emergencia antes de que los cultores se despertaran, pero tengo un... eeh... problemita...\n\nNo queda tela para... Bueno, ya sabes para qué.\n\n¿Podrías buscarme un trozo de tela y pasármela por debajo de la puerta?', '', '', '', '', '', '', 26365),
(50228, 'esES', 'Superviviente Crepuscular', 'Hazte con la cabeza de Michiel Zancavacío.', 'Silithus solía ser un hervidero de miembros del Martillo Crepuscular.\n\nSe creía que este culto había sido exterminado, pero ha sobrevivido en las sombras. Aunque su número se ha reducido, el culto sigue siendo tan fanático como siempre.\n\nMuchos de los cultores que se encontraban en este lugar murieron durante el ataque de Sargeras, pero aún quedan unos cuantos. Además, la destrucción de Silithus ha envalentonado a su líder, Michiel Zancavacío.\n\nNo podemos permitir que Michiel anime a los supervivientes a pasar a la acción. ¡Debemos detenerlo!', '', '', '', '', '', '', 26365),
(50227, 'esES', 'Larvas por docenas', 'Recoge $1oa crías silítidas.', '¿Cómo sobrevivieron los silítidos a la espada de Sargeras? ¡Es asombroso!\n\nNunca me he topado con una especie capaz de adaptarse tan rápido a los cambios en su entorno como ellos.\n\nLas larvas, o crías, como se las llama normalmente, pueden evolucionar y convertirse en distintos tipos de criaturas dependiendo de las necesidades de la colmena.\n\n¿Es la mente de la colmena la que indica el desarrollo evolutivo de la larva o este depende de algún factor medioambiental?\n\n¡La ciencia necesita una respuesta!', '', '', '', '', '', '', 26365),
(50226, 'esES', 'La fuente del poder', 'Consigue $1oa cerebros silítidos.', 'Aunque la destrucción de Silithus provocó que los silítidos desaparecieran durante un tiempo, recientemente han vuelto a salir de debajo de la tierra.\n\n¡Estos nuevos silítidos parecen más fuertes que los anteriores!\n\nTengo la teoría de que los silítidos se están alimentando de alguna fuente de poder por debajo de la espada de Sargeras.\n\nMe han encargado la tarea de estudiar los efectos que tiene este poder misterioso en las colmenas silítidas.\n\nTráeme todos los cerebros silítidos intactos que puedas y empezaré a investigar.', '', '', '', '', '', '', 26365),
(50131, 'esES', 'Una nueva perspectiva', 'Alcanza la reputación Neutral con Esporaggar.', 'Te agradezco todo lo que has hecho por los míos, pero siguen teniendo miedo de los forasteros.\n\nEnfréntate a los señores de la marisma y salva las bolsas de esporas de La Cañada Emergente. ¡El futuro de mi pueblo está en juego!', '', '', '', '', '', '', 26365),
(50130, 'esES', 'Un gesto amistoso', 'Alcanza la reputación Amistoso con Esporaggar.', 'No es fácil vivir en la marisma, $n. Nosotros queremos vivir en armonía con aquellos que la consideran su hogar, pero los enemigos que tenemos ahí fuera amenazan nuestra existencia.\n\nÉchanos una mano. Ayuda a la gente de Esporaggar a prosperar.', '', '', '', '', '', '', 26365),
(50071, 'esES', '¡Por la Luz!', 'Vuelve a Ventormenta y habla con el rey Anduin Wrynn.', 'Los draenei forjados por la Luz estarán encantados de unirse a la Alianza.\n\nAhora compartimos una misma causa, $n.\n\nVolvamos a Ventormenta. La capitana Fareeya hablará con nuestro pueblo.', '', '', '', '', '', '', 26365),
(50055, 'esES', 'La perspectiva del Portavoz', 'Lleva la carta de Khadgar a Magni Barbabronce en Silithus.', 'Sargeras ha dejado una cicatriz en nuestro mundo, $n. Una cicatriz que tardará mucho en sanar...\n\nParece que las cicatrices entre la Alianza y la Horda tampoco han sanado todavía.\n\nYa se han perdido demasiadas vidas en la contienda para salvar Azeroth de la destrucción. No pienso quedarme a contemplar cómo se pierden aún más.\n\nAntes de irme, tengo que pedirte algo. Llévale esta carta a Magni Barbabronce. Está en Silithus, buscando una forma de cerrar la herida.\n\nEsta carta contiene información que podría resultarle útil.', '', '', '', '', '', '', 26365),
(50054, 'esES', 'La Costa Hirviente', 'Completa el campo de batalla Costa Hirviente.', 'Hemos recibido informes sobre una isla cercana a la costa de Silithus que está llena de depósitos de azerita.\n\nEs de vital importancia que aseguremos la isla antes de que la Alianza descubra su existencia.\n\nNathanos Clamañublo se está encargando personalmente de la expedición y solicita que te presentes ante él. ¡Parte con honor!', '', '', '', '', '', '', 26365),
(50053, 'esES', '¡Prospectores vagos!', 'Motiva a $1oa prospectores vagos.', 'He vivido lo suficiente para saber que no se puede confiar a un goblin el trabajo de un orco.\n\nPuede que Gallywix y su tropa estén al servicio de la Jefa de Guerra, pero vamos a tener que vigilarlos muy de cerca si queremos que hagan algo.\n\nYa he visto a muchos trabajadores holgazaneando o durmiendo en sus horas de trabajo. ¡La Horda no tolera la vagancia!\n\n¡Encárgate de poner a esos prospectores holgazanes en su lugar!', '', '', '', '', '', '', 26365),
(50052, 'esES', 'No se admiten espías', 'Mata a $1oa operativos del IV:7 en Silithus.', 'Eh, $n, me alegro de que estés aquí.\n\n¡La situación me supera! Bichos asesinos, una sustancia extraña... esa espada GIGANTE que sale del suelo... ¡Este sitio no me da buena espina!\n\n¡Y para complicar más las cosas, he recibido informes de que hay espías de la Alianza merodeando por el campamento!\n\nEscucha, la Dama Oscura tiene planes para ese mineral misterioso y no voy a ser yo quien la decepcione, no sé si me entiendes...\n\nCorre al campamento de minería y encárgate de esos espías del IV:7 antes de que se carguen toda nuestra operación.', '', '', '', '', '', '', 26365),
(50049, 'esES', 'La perspectiva del Portavoz', 'Lleva la carta de Khadgar a Magni Barbabronce en Silithus.', 'Sargeras ha dejado una cicatriz en nuestro mundo, $n. Una cicatriz que tardará mucho en sanar...\n\nParece que las cicatrices entre la Alianza y la Horda tampoco han sanado todavía.\n\nYa se han perdido demasiadas vidas en la contienda para salvar Azeroth de la destrucción. No pienso quedarme a contemplar cómo se pierden aún más.\n\nAntes de irme, tengo que pedirte algo. Llévale esta carta a Magni Barbabronce. Está en Silithus, buscando una forma de cerrar la herida.\n\nEsta carta contiene información que podría resultarle útil.', '', '', '', '', '', '', 26365),
(50048, 'esES', 'La Costa Hirviente', 'Completa el campo de batalla Costa Hirviente.', 'Hemos recibido informes sobre una isla cercana a la costa de Silithus que está llena de depósitos de azerita.\n\nEs de vital importancia que aseguremos la isla antes de que la Horda descubra su existencia.\n\nEl maestro Mathias Shaw se está encargando personalmente de la expedición y solicita que te presentes ante él. ¡Buena suerte!', '', '', '', '', '', '', 26365),
(50047, 'esES', 'Muestras gratuitas', 'Recoge $1oa muestras de mena misteriosa.', 'La corona ha ordenado la infiltración del IV:7 en la operación minera de los goblins. Quiere que reunamos toda la información posible sobre esta misteriosa sustancia nueva.\n\nMis agentes se han infiltrado en el campamento, pero el tiempo pasa y tenemos que hacernos con unas muestras de la mena extraída cuanto antes.\n\nEn otras palabras: necesito a alguien capaz de adoptar una estrategia más directa. Alguien que pueda enfrentarse a adversidades abrumadoras y salir con vida.\n\nCuando regreses, llévale las muestras de mena a Khargus Mantopétreo para que las analice.', '', '', '', '', '', '', 26365),
(50046, 'esES', '¡Es un sabotaje!', 'Coloca $1oa cargas de dinamita.', 'La Horda no ha esperado demasiado antes de ponerse a extraer esta misteriosa mena de la gran herida descaradamente.\n\nPor suerte para nosotros, la ingeniería goblin no es tan sofisticada como nuestras creaciones de Gnomeregan. Solo necesitamos un poco de dinamita para inutilizar por completo una trituradora goblin.\n\n¡Qué maquinaria tan burda!\n\nCoge estas cargas de dinamita y colócalas en las trituradoras inactivas que hay por el campamento de minería.\n\n¡Esos goblins avariciosos tardarán semanas en recuperarse de esta!', '', '', '', '', '', '', 26365),
(50035, 'esES', 'Prueba de Bret - SQL', '', '', '', '', '', '', '', '', 26365),
(49982, 'esES', 'Testigos de la herida', 'Habla con Grol Hojaguerra en Silithus.', 'El Cártel Pantoque ya ha iniciado operaciones mineras en Silithus para extraer esa sustancia misteriosa conocida.\n\nLa Jefa de Guerra ha insistido en que te encargues personalmente de la supervisión de las operaciones en Silithus y de impedir que la Alianza interfiera.\n\nGallywix no parecía muy feliz con esta decisión, pero nadie desobedece a Sylvanas...\n\nDebes ir a Silithus de inmediato. El comandante de la base te está esperando.', '', '', '', '', '', '', 26365),
(49981, 'esES', 'Testigos de la herida', 'Reúnete con el comandante del IV:7 en Silithus.', 'He enviado operativos a Silithus para que establezcan un campamento base.\n\nTienen órdenes de monitorizar las operaciones de la Horda y reunir toda la información que puedan encontrar sobre esta sustancia misteriosa.\n\nAhora mismo uno de mis comandantes del IV:7 está esperando tu llegada. Date prisa.', '', '', '', '', '', '', 26365),
(49977, 'esES', 'Citación a Orgrimmar', 'Habla con Nathanos Clamañublo.', 'La Jefa de Guerra solicita que vuelvas a Orgrimmar cuanto antes. Hay un asunto urgente que requiere de tu atención.\n\nTe daré todos los detalles en cuanto llegues.\n\nTe estaré esperando junto a la entrada del Fuerte Grommash. La Dama Oscura tiene planes para ti, $n. Yo de ti no la haría esperar...', '', '', '', '', '', '', 26365),
(49976, 'esES', 'Regalos de los caídos', 'Habla con Mathias Shaw.', 'Hoy, el rey Wrynn se ha dirigido a los ciudadanos de Ventormenta en Reposo del León y ha dado un discurso en el que anunciaba formalmente el fin de la guerra.\n\nCuando la multitud se ha dispersado, me he reunido en privado con el Rey y con sus consejeros para hablar de una sustancia misteriosa descubierta en Silithus.\n\nEsta mena brillante parece de lo más poderosa. La Horda no ha perdido el tiempo y se ha lanzado a extraerla de la gran herida.\n\nEl Rey me ha ordenado que te dé todos los detalles para prepararte para una importante misión.', '', '', '', '', '', '', 26365),
(49973, 'esES', 'La villa de Thalyssra', 'Reúnete con lady Liadrin en la Villa de la Primera Arcanista, en Suramar.', 'El señor regente Theron ha invitado a los Nocheterna a que se reúnan con él en Lunargenta.\n\nPrimero debemos extenderles la invitación. Prefiero hacer estas cosas en persona.\n\nVen conmigo a Suramar y nos reuniremos con lady Thalyssra.', '', '', '', '', '', '', 26365),
(49933, 'esES', 'Por la Horda', 'Reúnete con el embajador Guardasombrío en la Embajada de Orgrimmar.', 'Tras años aislados, volvemos a formar parte de algo más grande. Nuestra unión con la Horda nos permite empezar de nuevo, una oportunidad única que no podemos desperdiciar.\n\nEl embajador de la Jefa de Guerra te espera en Orgrimmar, $n. Una vez allí, emprenderás un viaje por Azeroth. Lucha para defender esta tierra y reclamar un lugar para los shal\'dorei en ella.\n\nMuéstrale al mundo quiénes somos, $c. Honra a la Horda y, al hacerlo, honrarás también a Suramar.', '', '', '', '', '', 'Reúnete con: Embajador Guardasombrío. Zona: Embajada de Orgrimmar.', 26365),
(49930, 'esES', 'Búsqueda de aliados', 'Reúnete con la Jefa de Guerra Sylvanas Brisaveloz en la Embajada de Orgrimmar.', 'El liderazgo desinteresado de la Horda en la guerra contra la Legión nos ha dejado sin recursos. Nuestros enemigos ansían robar lo que es nuestro. No lo permitiremos.\n\nHe autorizado la creación de una embajada en Orgrimmar desde la que buscar aliados para nuestra causa. Reúnete allí conmigo de inmediato.', '', '', '', '', '', '', 26365),
(49929, 'esES', 'Búsqueda de aliados', 'Reúnete con el rey Wrynn en la Embajada de Ventormenta.', 'La Horda no se detendrá ante nada para obtener la sangre de Azeroth. Tras la guerra contra la Legión, temo que nuestras filas estén demasiado mermadas para hacerles frente.\n\nEs hora de que la Alianza busque nuevos miembros que se unan a nuestra causa. Para ello, hemos establecido una embajada en Ventormenta. Reúnete allí conmigo.', '', '', '', '', '', '', 26365),
(49928, 'esES', 'Legado del Vacío', 'Reúnete con Alleria Brisaveloz en la Falla de Telogrus.', 'Durante tus viajes por Azeroth has conseguido dominar las fuerzas que intentaban controlarte.\n\nTe has convertido en un arma sin igual para la Alianza, $p. Vuelve a la falla de Vacío para que podamos honrarte por tus logros.', '', '', '', '', '', '', 26365),
(49866, 'esES', '¡A Pandaria!', 'Preséntate ante Rell Brisa Nocturna en Ventormenta.', 'Ven inmediatamente al Castillo de Ventormenta para reunirte con el rey Varian Wrynn.$b$bNos han informado de un conflicto en los mares del sur y necesitamos la ayuda de $gun:una; $c $gexperto:experta; para la investigación.', '', '', '', '', '', '', 26365),
(49865, 'esES', 'Primer interviniente en Cataclysm', 'Preséntate ante Naraat el Hablatierra en Ventormenta.', 'Alamuerte, el aspecto dracónico de la muerte, ha enviado a sus esbirros a despojar de vida Azeroth.$b$bNaraat el Hablatierra puede contarte más acerca del Monte Hyjal, Vashj\'ir y otras zonas amenazadas por Alamuerte.', '', '', '', '', '', '', 26365),
(49864, 'esES', 'Guerras en dos frentes', 'Usa los encargos de misión especial para seleccionar el destino de tu siguiente aventura.', '¡$n! Te han llamado una vez más para que ayudes a la Alianza en asuntos de gran importancia.\n\n¿Quieres auxiliar a la Alianza en sus esfuerzos contra Alamuerte y el Martillo Crepuscular?\n\n¿O prefieres volar al sur, en dirección a Pandaria, para hacerte con sus recursos antes de que lo haga la Horda?\n\n¡Elige y lánzate a la batalla!', '', '', '', '', '', '', 26365),
(49863, 'esES', '¡A Rasganorte!', 'Toma un barco hacia Rasganorte.$b$bPuedes embarcar en el que va a la Tundra Boreal, en el Puerto de Ventormenta, o en el que va al Fiordo Aquilonal, en Los Humedales.', 'El Rey Exánime se agita al norte.$b$bUn ejército de no-muertos marcha a sus órdenes por todo Rasganorte, propagando su añublo allí por donde pasa.$b$bNecesitamos que lo detengas, $n.', '', '', '', '', '', '', 26365),
(49862, 'esES', '¡A Terrallende!', 'Cruza el Portal Oscuro en Las Tierras Devastadas.', 'La Legión Ardiente, un inmenso ejército de demonios, ha atacado Terrallende, el destrozado mundo natal de los draenei. Se necesitan aventureros de la Alianza para proteger lo que queda de Terrallende de la aniquilación total a manos de esta invasión demoníaca.$b$bViaja a Las Tierras Devastadas y cruza el Portal Oscuro.', '', '', '', '', '', '', 26365),
(49860, 'esES', 'La aventura llama', 'Usa los encargos de misión especial para seleccionar el destino de tu siguiente aventura.', 'Las historias sobre tu heroísmo han llegado a los confines de Azeroth... y más allá. Han solicitado tus habilidades en otra parte.\n\n¿Quieres cruzar el Portal Oscuro para luchar contra la Legión Ardiente en el mundo destruido de Terrallende?\n\n¿O volarás al norte para unirte a la batalla contra el Rey Exánime en Rasganorte?\n\nTú decides, $gaventurero:aventurera;.', '', '', '', '', '', '', 26365),
(49855, 'esES', 'Desastre en el Monte Hyjal', 'Habla con el emisario Cenarion Pezuñanegra para viajar a Claro de la Luna.', '¡Nuestros hermanos del Círculo Cenarion nos informan de que los esbirros de Alamuerte han iniciado una ofensiva de destrucción contra el Árbol del Mundo y amenazan con reducirlo a cenizas!$b$bPuedes hablar con el emisario Cenarion Pezuñanegra para entrar en Claro de la Luna. Se encuentra en el distrito tauren del Valle de la Sabiduría, en Orgrimmar.', 'Viaje a Claro de la Luna disponible', '', '', '', '', 'Habla con: Emisaria Son del Viento. Zona: Amparo de la Noche, Claro de la Luna.', 26365),
(49852, 'esES', '¡A Pandaria!', 'Preséntate en el Fuerte Grommash en Orgrimmar.', 'Por la presente se te ordena presentarte de inmediato en el Fuerte Grommash.$b$bNos han informado de un nuevo continente rico en recursos al sur y queremos enviarte con un pequeño contingente de soldados a reclamarlo para la Horda.', '', '', '', '', '', '', 26365),
(49851, 'esES', 'Primer interviniente en Cataclysm', 'Preséntate ante el clarividente Krogar en Orgrimmar.', 'Alamuerte, el aspecto dracónico de la muerte, ha enviado a sus esbirros a despojar de vida Azeroth.$b$bEl clarividente Krogar, en Orgrimmar, puede contarte más acerca del Monte Hyjal, Vashj\'ir y otras zonas amenazadas por Alamuerte.', '', '', '', '', '', '', 26365),
(49846, 'esES', 'Guerras en dos frentes', 'Usa los encargos de misión especial para seleccionar el destino de tu siguiente aventura.', '¡$n! Te han llamado una vez más para que ayudes a la Horda en asuntos de gran importancia.\n\n¿Quieres auxiliar a la Horda en sus esfuerzos contra Alamuerte y el Martillo Crepuscular?\n\n¿O prefieres volar al sur, en dirección a Pandaria, para hacerte con sus recursos antes de que lo haga la Alianza?\n\n¡Elige y lánzate a la batalla!', '', '', '', '', '', '', 26365),
(49817, 'esES', '¡A Rasganorte!', 'Toma un zepelín a Rasganorte.$b$bPuedes subirte al zepelín del Fiordo Aquilonal en los Claros de Tirisfal, o al zepelín de la Tundra Boreal en Orgrimmar.', 'El Rey Exánime se agita al norte.$b$bUn ejército de no-muertos marcha a sus órdenes por todo Rasganorte, propagando su añublo allí por donde pasa.$b$bNecesitamos que lo detengas, $n.', '', '', '', '', '', '', 26365),
(49816, 'esES', '¡A Terrallende!', 'Cruza el Portal Oscuro en Las Tierras Devastadas.', 'La Legión Ardiente, un inmenso ejército de demonios, ha atacado Terrallende, el destrozado mundo natal de los orcos. Se necesitan aventureros de la Horda para proteger lo que queda de Terrallende de la aniquilación total a manos de esta invasión demoníaca.$b$bViaja a Las Tierras Devastadas y cruza el Portal Oscuro.', '', '', '', '', '', '', 26365),
(49813, 'esES', 'La aventura llama', 'Usa los encargos de misión especial para seleccionar el destino de tu siguiente aventura.', 'Las historias sobre tu heroísmo han llegado a los confines de Azeroth... y más allá. Han solicitado tus habilidades en otra parte.\n\n¿Quieres cruzar el Portal Oscuro para luchar contra la Legión Ardiente en el mundo destruido de Terrallende?\n\n¿O volarás al norte para unirte a la batalla contra el Rey Exánime en Rasganorte?\n\nTú decides, $gaventurero:aventurera;.', '', '', '', '', '', '', 26365),
(49812, 'esES', 'Delas en Dalaran', 'Busca a Delas Dienteluna en el Monumento de Antonidas, en Dalaran.', 'Delas Dienteluna necesita nuestra ayuda. A cambio, nos ayudará a descifrar el códice de mando.$b$bTe ruego que la encuentres lo antes posible. Sabemos que le gusta el monumento dedicado al gran líder caído del Kirin Tor, Antonidas.', '', '', '', '', '', 'Busca a: Delas Dienteluna. Zona: Monumento de Antonidas, Dalaran.', 26365),
(49788, 'esES', 'Por la Alianza', 'Reúnete con el embajador Moorgard en la Embajada de Ventormenta.', 'Dominar los susurros requiere concentración y disciplina constantes. Tu dedicación inspira a todos aquellos que responden al nombre de ren\'dorei, pues nos hemos convertido en hijos del Vacío.\n\nAunque has aprendido muchas lecciones aquí, ha llegado el momento de que te aventures más allá de la Falla de Telogrus.\n\nDirígete a Ventormenta, el corazón de la Alianza. Allí te recibirá el embajador del Rey y te guiará en tu viaje.\n\nDebemos defender Azeroth, y no se me ocurre nadie mejor que tú para responder a esa llamada, $p.', '', '', '', '', '', 'Reúnete con: Embajador Moorgard. Zona: Embajada de Ventormenta.', 26365),
(49787, 'esES', 'Las Tierras Fantasma', 'Encuentra el hogar de Umbric en las Tierras Fantasma.', 'Un elfo de sangre llamado Umbric y sus seguidores fueron expulsados de la Ciudad de Lunargenta por investigar la magia del Vacío.\n\nUna medida un tanto extrema, aunque es cierto que es una práctica prohibida.\n\nSin embargo, esto abre nuevas posibilidades para la Alianza. Si encontramos a estos elfos desterrados, puede que podamos reclutarlos para nuestra causa y, más importante aún, ayudarlos a entender el Vacío antes de que los corrompa.', '', '', '', '', '', '', 26365),
(49784, 'esES', 'Legado de los Nocheterna', 'Reúnete con la primera arcanista Thalyssra en el Bastión Nocturno.', 'Te encuentras entre los primeros shal\'dorei que se han aventurado a explorar Azeroth. Pero es hora de volver a casa.\n\nRegresa a Suramar para que podamos agradecerte que estés ayudando a nuestro pueblo a recuperar su lugar en el mundo.', '', '', '', '', '', '', 26365),
(49783, 'esES', 'Legado de los Monte Alto', 'Reúnete con Mayla Monte Alto en Tótem del Trueno.', 'Tu viaje por Azeroth ha sido largo, $p. Durante todo este tiempo, has traído honor a Monte Alto.\n\nAhora Tótem del Trueno te reclama. Es hora de agradecerte todo lo que has hecho por nosotros.', '', '', '', '', '', '', 26365),
(49782, 'esES', 'Legado de los forjados por la Luz', 'Reúnete con la capitana Fareeya en el Vindicaar.', 'Le has ofrecido un servicio muy valioso a la Alianza, $p. Ni yo misma lo habría hecho mejor. Bueno, sí... pero por poco.\n\nReúnete conmigo en el Vindicaar. Es hora de recompensarte por tus esfuerzos.', '', '', '', '', '', '', 26365),
(49773, 'esES', 'Por la Horda', 'Reúnete con el embajador Guardasombrío en la Embajada de Orgrimmar.', 'Empieza una nueva era en Monte Alto, $n.\n\nTras siglos separados, hemos vuelto a forjar el vínculo con nuestros hermanos de Kalimdor. Y en la batalla por salvar nuestra tierra, hemos encontrado nuevos amigos entre los miembros de la Horda.\n\nPara mostrar la fuerza que aportamos a nuestros aliados, tauren valientes como tú deben responder a la llamada de la aventura.\n\nTe han invitado a ir a Orgrimmar. Reúnete con el embajador en la embajada de la Jefa de Guerra y emprende tu viaje por Azeroth.\n\nQue la Madre Tierra te acompañe, $n.', '', '', '', '', '', 'Reúnete con: Embajador Guardasombrío. Zona: Embajada de Orgrimmar.', 26365),
(49772, 'esES', 'Por la Alianza', 'Reúnete con el embajador Moorgard en la Embajada de Ventormenta.', 'El Ejército de la Luz se fundó con un objetivo en mente: derrotar a la Legión. Aunque hemos cumplido nuestra misión, ahora tenemos una nueva.\n\nAzeroth es un mundo plagado de enemigos. Usa tus habilidades para combatir la oscuridad y defender los ideales de la Alianza.\n\nDirígete a Ventormenta. Busca al embajador del Rey en la embajada y luego imparte la justicia de la Luz por aquellas tierras.\n\nPheta thones gamera, $n.', '', '', '', '', '', 'Reúnete con: Embajador Moorgard. Zona: Embajada de Ventormenta.', 26365),
(49756, 'esES', 'Fuerzas oscuras', 'Derrota a Qy\'telek, heraldo de Uul\'gyneth.', 'Los dioses antiguos han enviado a uno de sus ignotos a Cima del Trueno. ¡Ese maligno está profanando la ciudad de mi gente!\n\nDebemos unirnos para luchar contra la escoria que contamina este lugar.\n\nCreo que si acabamos con ese heraldo, nuestros enemigos abandonarán Mulgore. ¡Que nuestras armas sean certeras, $n!', '', 'Heraldo de Uul\'gyneth', 'Qy\'telek', '', '', '', 26365),
(49698, 'esES', 'Los forjados por la Luz', 'Viaja al Vindicaar con Turalyon.', 'Tú $gmismo:misma; has visto lo que mi gente es capaz de hacer, $n. Durante miles de años nos hemos especializado en luchar contra la Legión.\n\nCreo que ha llegado el momento de tomar las armas junto a nuestros hermanos y hermanas de la Alianza.\n\nReúnete con nosotros en el Vindicaar. Tenemos mucho de qué hablar con la capitana Fareeya.', '', '', '', '', '', 'Reúnete con: Capitana Fareeya. Zona: El Vindicaar.', 26365),
(49614, 'esES', 'Los Nocheterna', 'Vuelve con la Jefa de Guerra en Orgrimmar.', 'La Jefa de Guerra estará satisfecha de nuestro éxito.\n\nThalyssra y sus Nocheterna serán unos fuertes aliados, seguro.', '', '', '', '', '', '', 26365),
(49613, 'esES', 'Ciudad de Lunargenta', 'Reúnete con lady Liadrin en la Ciudad de Lunargenta.', 'Los Nocheterna han estado miles de años aislados. Ahora que han resurgido, nos corresponde guiarlos por el buen camino.\n\nEl señor regente Theron los ha invitado a un encuentro con nosotros en Lunargenta.\n\nVen, $n, vayamos a Lunargenta para reunirnos con Thalyssra.', '', '', '', '', '', '', 26365),
(49567, 'esES', 'Se busca héroe: ¡Nagrand!', 'Habla con el teniente Balfor en Nagrand, en Draenor.', 'Todos los ciudadanos capaces de la Alianza se presentarán ante el teniente Balfor en Nagrand, en Draenor.\n\nYrel recomienda que la Alianza establezca una avanzada en Nagrand para ayudar en nuestra lucha contra el clan Grito de Guerra.\n\nConsigue un vuelo a Nagrand y reúnete con el teniente Balfor y la caravana de suministros.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con: Teniente Balfor. Zona: Nagrand, Draenor.', 26365),
(49566, 'esES', 'Se busca héroe: ¡Cumbres de Arak!', 'Habla con Azik en las Cumbres de Arak.', 'Todos los ciudadanos capaces de la Alianza se presentarán ante Azik en las Cumbres de Arak.\n\nHan visto arakkoa sin alas huyendo de las Cumbres. Según los rumores, huyen de una superarma de la Horda de Hierro.\n\nDebemos averiguar qué provoca que se vayan de su hogar y por qué se les han marchitado las alas.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con: Azik. Zona: Cumbres de Arak.', 26365),
(49565, 'esES', 'Se busca héroe: ¡Talador!', 'Habla con el supervisor Eksos en Talador.', 'Todos los ciudadanos capaces de la Alianza se presentarán ante el supervisor Eksos en Talador.\n\nLa Horda de Hierro quiere controlar Talador. La zona es perfecta para enviar refuerzos a cualquier otra parte de Draenor de forma rápida y sin resistencia.\n\nEl supervisor Eksos está estableciendo una avanzada en Talador. Ve allí y detén a la Horda de Hierro.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con: Supervisor Eksos. Zona: Talador.', 26365),
(49564, 'esES', 'Se busca héroe: ¡Gorgrond!', 'Habla con Yrel en Gorgrond.', 'Todos los ciudadanos capaces de la Alianza se presentarán ante Yrel en Gorgrond.\n\nYrel y Maraad solicitan el envío de una pequeña expedición a las tierras salvajes de Gorgrond.\n\nEsperamos conocer mejor el territorio con la esperanza de que nos enseñe a explotar puntos débiles de la Horda de Hierro.\n\nAyúdalos en su expedición.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con: Yrel. Zona: Gorgrond.', 26365),
(49561, 'esES', 'Se busca héroe: ¡Desierto del Pavor!', 'Habla con la maestra arquera Li en el Espinazo del Dragón.', 'Todos los ciudadanos capaces de la Alianza se presentarán ante la maestra arquera Li en el Espinazo del Dragón, en el Valle de la Flor Eterna.\n\nEl Shadopan intenta impedir que el ejército mántide invada toda Pandaria.\n\nPuedes llegar hasta la maestra arquera Li haciéndote con una cometa y volando hasta el Espinazo del Dragón.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con: Maestra arquera Li. Zona: Espinazo del Dragón.', 26365),
(49560, 'esES', 'Se busca héroe: ¡Estepas de Tong Long!', 'Habla con Taran Zhu en las Estepas de Tong Long.', 'Todos los ciudadanos capaces de la Alianza se presentarán ante Taran Zhu en las Estepas de Tong Long.\n\nEl Shadopan quiere recuperar su tierra de las garras de los yaungol, pero no son suficientes en número.\n\nAyúdalos en su ataque contra los yaungol.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con: Taran Zhu. Zona: Estepas de Tong Long.', 26365),
(49559, 'esES', 'Se busca héroe: ¡Cima Kun-Lai!', 'Habla con el mensajero grúmel en el Valle de los Cuatro Vientos.', 'Todos los ciudadanos capaces de la Alianza deben ayudar al eremita Cho en la Cima Kun-Lai.\n\nHabla con un mensajero grúmel en el Valle de los Cuatro Vientos. Te indicará cómo ir a la Cima Kun-Lai.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con: Mensajero grúmel. Zona: Valle de los Cuatro Vientos.', 26365),
(49558, 'esES', 'Se busca héroe: ¡Espesura Krasarang!', 'Habla con Ken-Ken en la Espesura Krasarang.', 'Todos los ciudadanos capaces de la Alianza se presentarán en la Atalaya de Zhu, en la Espesura Krasarang.\n\nCorren rumores de que los pandaren que viven en la Espesura Krasarang están raros.\n\nHabla con Ken-Ken en la Atalaya de Zhu. Tal vez él tenga alguna idea de qué les ha pasado a los pandaren para que estén tan apáticos.', '', '', '', '', '', 'Habla con: Ken-Ken. Zona: Espesura Krasarang.', 26365),
(49557, 'esES', 'Se busca héroe: ¡Valle de los Cuatro Vientos!', 'Habla con Chen Cerveza de Trueno en el Valle de los Cuatro Vientos.', 'Todos los ciudadanos capaces de la Alianza se presentarán ante Chen Cerveza de Trueno en el Valle de los Cuatro Vientos.\n\nEl espíritu aventurero de los Cerveza de Trueno es algo de lo que la Alianza puede aprender.\n\nViaja con ellos y descubre los secretos de Pandaria.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con: Chen Cerveza de Trueno. Zona: Valle de los Cuatro Vientos.', 26365),
(49556, 'esES', 'Se busca héroe: ¡Bosque de Jade!', 'Preséntate ante la gran almirante Jes-Tereth en el Castillo de Ventormenta.', 'Todos los ciudadanos capaces de la Alianza se presentarán en el Castillo de Ventormenta, en Ventormenta.\n\nLa gran almirante Jes-Tereth está reuniendo un equipo para una misión de máxima prioridad.\n\nDirígete de inmediato al Castillo de Ventormenta para informarte.\n\n¡Gloria a la Alianza!', '', '', '', '', '', '', 26365),
(49555, 'esES', 'Se busca héroe: ¡Corona de Hielo!', 'Habla con el oficial Van Rossem en Dalaran, por encima del Bosque Canto de Cristal.', 'Todos los ciudadanos capaces de la Alianza se presentarán ante el oficial Van Rossem en Dalaran, por encima del Bosque Canto de Cristal.\n\nLa Expedición de Denuedo necesita más aventureros para reforzar su ataque a la Ciudadela de la Corona de Hielo y al propio Arthas.\n\nEs hora de hacer que el Rey Exánime pague por sus crímenes.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con: Oficial Van Rossem. Zona: Dalaran, por encima del Bosque Canto de Cristal.', 26365),
(49554, 'esES', 'Se busca héroe: ¡Las Cumbres Tormentosas!', 'Habla con Rin Duoctano en Dalaran, por encima del Bosque Canto de Cristal.', 'Todos los ciudadanos capaces de la Alianza se presentarán ante Rin Duoctano en Dalaran, por encima del Bosque Canto de Cristal.\n\nLos goblins de K3 han construido una ciudad en Las Cumbres Tormentosas y han enviado a su embajador a reclutar aventureros.\n\nHabla con Rin y mira qué necesita la gente de K3. Serían unos aliados valiosos en nuestra lucha contra el Rey Exánime.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con: Rin Duoctano. Zona: Dalaran, por encima del Bosque Canto de Cristal.', 26365),
(49553, 'esES', 'Se busca héroe: ¡Cuenca de Sholazar!', 'Habla con el archimago Pentarus en Dalaran, por encima del Bosque Canto de Cristal.', 'Todos los ciudadanos capaces de la Alianza se presentarán ante el archimago Pentarus en Dalaran, por encima del Bosque Canto de Cristal.\n\nSe envió a Hemet Nesingwary junto a su equipo de expedición a explorar la Cuenca de Sholazar, pero ha desaparecido.\n\nNo sabemos gran cosa acerca del exuberante paisaje de Sholazar, pero tal vez en la cuenca haya secretos que puedan darnos alguna ventaja en nuestra lucha contra el Rey Exánime.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con: Archimago Pentarus. Zona: Dalaran, por encima del Bosque Canto de Cristal.', 26365),
(49552, 'esES', 'Se busca héroe: ¡Zul\'Drak!', 'Habla con el señor cruzado Lantinga en Zul\'Drak.', 'Todos los ciudadanos capaces de la Alianza se presentarán ante el señor cruzado Lantinga en Zul\'Drak.\n\nLa Plaga está masacrando a la población local en Zul\'Drak y convirtiéndola para que sea parte de su ejército.\n\nAyuda a la Cruzada Argenta y a la Espada de Ébano a detener esta amenaza.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con: Señor cruzado Lantinga. Zona: Zul\'Drak.', 26365),
(49551, 'esES', 'Se busca héroe: ¡Fiordo Aquilonal!', 'Habla con Macalroy en el Fiordo Aquilonal.', 'Todos los ciudadanos capaces de la Alianza se presentarán ante Macalroy en el Fiordo Aquilonal.\n\nLa ciudad portuaria de Valgarde se encuentra en medio del Fiordo Aquilonal y permite a la Alianza contar con una posición de ventaja para atacar a la Plaga.\n\nAyuda a los soldados allí destinados.\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con: Macalroy. Zona: Fiordo Aquilonal.', 26365),
(49550, 'esES', 'Se busca héroe: ¡Valle Sombraluna!', 'Habla con el comandante del aire Nuainn en el Valle Sombraluna, en Terrallende.', 'Todos los ciudadanos capaces de la Alianza se presentarán ante el comandante del aire Nuainn en el Valle Sombraluna, en Terrallende.\n\nLa Legión Ardiente tiene sitiado el Bastión Martillo Salvaje, poniendo en peligro a nuestra gente y amenazando sus vidas.\n\n¡Debemos reforzar el bastión y detener el asalto incesante de la Legión Ardiente!\n\n¡Gloria a la Alianza!', '', '', '', '', '', 'Habla con: Comandante del aire Nuainn. Zona: Valle Sombraluna, Terrallende.', 26365),
(49549, 'esES', 'La orden del Jefe de Guerra: ¡Nagrand!', 'Habla con el guardia de piedra Brox en Nagrand, en Draenor.', 'Por la presente se ordena a todos los miembros aptos de la Horda que se presenten ante el guardia de piedra Brox en Nagrand, en Draenor.\n\nThrall quiere establecer una avanzada de la Horda y necesita ayuda para detener al clan Grito de Guerra.\n\nHabla con Bron Cuerno del Cielo para volar hasta Nagrand y reunirte con la caravana de suministros.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con: Guardia de piedra Brox. Zona: Nagrand, Draenor.', 26365),
(49548, 'esES', 'La orden del Jefe de Guerra: ¡Cumbres de Arak!', 'Habla con Azik en las Cumbres de Arak.', 'Por la presente se ordena a todos los miembros aptos de la Horda que se presenten ante Azik en las Cumbres de Arak.\n\nHan visto arakkoa sin alas huyendo de las Cumbres. Según los rumores, huyen de una superarma de la Horda de Hierro.\n\nDebemos averiguar qué provoca que se vayan de su hogar y por qué se les han marchitado las alas.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con: Azik. Zona: Cumbres de Arak.', 26365),
(49547, 'esES', 'La orden del Jefe de Guerra: ¡Talador!', 'Habla con el supervisor Grobash en Talador.', 'Por la presente se ordena a todos los miembros aptos de la Horda que se presenten ante el supervisor Grobash en Talador.\n\nLa Horda de Hierro quiere controlar Talador. La zona es perfecta para enviar refuerzos a cualquier otra parte de Draenor de forma rápida y sin resistencia.\n\nEl supervisor Grobash está estableciendo una avanzada en Talador. Ve allí y detén a la Horda de Hierro.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con: Supervisor Grobash. Zona: Talador.', 26365),
(49546, 'esES', 'La orden del Jefe de Guerra: ¡Gorgrond!', 'Habla con Durotan en Gorgrond.', 'Por la presente se ordena a todos los miembros aptos de la Horda que se presenten ante Durotan en Gorgrond.\n\nDurotan busca aliados que nos ayuden a movernos por el paisaje agreste de Gorgrond con la esperanza de encontrar poderes antiguos que nos ayuden a destruir a la Horda de Hierro.\n\nHabla con él y averigua qué secretos guardan esas tierras.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con: Durotan. Zona: Gorgrond.', 26365),
(49545, 'esES', 'La orden del Jefe de Guerra: ¡Cresta Fuego Glacial!', 'Habla con el clarividente Drek\'Thar en la Cresta Fuego Glacial.', 'Por la presente se ordena a todos los miembros aptos de la Horda que se presenten ante el clarividente Drek\'Thar en la Cresta Fuego Glacial.\n\nLa destrucción del Portal Oscuro ha evitado la invasión de Azeroth por parte de la Horda de Hierro, pero ahora debemos llevar la lucha hasta ellos.\n\nCon la ayuda del clarividente Drek\'Thar, podremos establecer una posición estratégica en Draenor para enfrentarnos a nuestros enemigos.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con: Clarividente Drek\'Thar. Zona: Cresta Fuego Glacial.', 26365),
(49543, 'esES', 'La orden del Jefe de Guerra: ¡Desierto del Pavor!', 'Habla con la maestra arquera Li en el Espinazo del Dragón.', 'Por la presente se ordena a todos los miembros aptos de la Horda que se presenten ante la maestra arquera Li en el Espinazo del Dragón, en el Valle de la Flor Eterna.\n\nEl Shadopan intenta impedir que el ejército mántide invada toda Pandaria.\n\nPuedes llegar hasta la maestra arquera Li haciéndote con una cometa y volando hacia el Espinazo del Dragón.\n\n¡El destino llama!', '', '', '', '', '', 'Habla con: Maestra arquera Li. Zona: Espinazo del Dragón.', 26365),
(49542, 'esES', 'La orden del Jefe de Guerra: ¡Estepas de Tong Long!', 'Habla con Taran Zhu en las Estepas de Tong Long.', 'Por la presente se ordena a todos los miembros aptos de la Horda que se presenten ante Taran Zhu en las Estepas de Tong Long.\n\nEl Shadopan quiere recuperar su tierra de las garras de los yaungol, pero no son suficientes en número.\n\nAyúdalos en su ataque a los yaungol.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con: Taran Zhu. Zona: Estepas de Tong Long.', 26365),
(49541, 'esES', 'La orden del Jefe de Guerra: ¡Cima Kun-Lai!', 'Habla con el mensajero grúmel en el Valle de los Cuatro Vientos.', 'Por la presente se ordena a todos los miembros aptos de la Horda que ayuden al eremita Cho en la Cima Kun-Lai.\n\nHabla con un mensajero grúmel en el Valle de los Cuatro Vientos. Te indicará cómo llegar a la Cima Kun-Lai.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con: Mensajero grúmel. Zona: Valle de los Cuatro Vientos.', 26365),
(49540, 'esES', 'La orden del Jefe de Guerra: ¡Espesura Krasarang!', 'Habla con Ken-Ken en la Espesura Krasarang.', 'Por la presente se ordena a todos los miembros aptos de la Horda que se presenten en la Atalaya de Zhu, en la Espesura Krasarang.\n\nCorren rumores de que los pandaren que viven en la Espesura Krasarang están raros.\n\nHabla con Ken-Ken en la Atalaya de Zhu. Tal vez él tenga alguna idea de qué les ha pasado a los pandaren para que estén tan apáticos.', '', '', '', '', '', 'Habla con: Ken-Ken. Zona: Espesura Krasarang.', 26365),
(49539, 'esES', 'La orden del Jefe de Guerra: ¡Valle de los Cuatro Vientos!', 'Habla con Chen Cerveza de Trueno en el Valle de los Cuatro Vientos.', 'Por la presente se ordena a todos los miembros aptos de la Horda que se presenten ante Chen Cerveza de Trueno en el Valle de los Cuatro Vientos.\n\nEl espíritu aventurero de los Cerveza de Trueno es algo de lo que la Horda puede aprender.\n\nViaja con ellos y descubre los secretos de Pandaria.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con: Chen Cerveza de Trueno. Zona: Valle de los Cuatro Vientos.', 26365),
(49538, 'esES', 'La orden del Jefe de Guerra: ¡Bosque de Jade!', 'Preséntate ante el general Nazgrim en el Fuerte Grommash.', 'Por la presente se ordena a todos los miembros aptos de la Horda que se presenten ante el general Nazgrim en Orgrimmar.\n\nLas recientes victorias en nuestras batallas navales contra la Alianza refuerzan nuestra determinación.\n\nPreséntate en el Fuerte Grommash y ayuda a la flota invasora de la Horda.\n\n¡Por la Horda!', '', '', '', '', '', '', 26365),
(49537, 'esES', 'La orden del Jefe de Guerra: ¡Corona de Hielo!', 'Habla con Atracador del cielo Klum en Dalaran, por encima del Bosque Canto de Cristal.', 'Por la presente se ordena a todos los miembros aptos de la Horda que se presenten ante Atracador del cielo Klum en Dalaran, por encima del Bosque Canto de Cristal.\n\nLa Ofensiva Grito de Guerra necesita más aventureros para reforzar su ataque contra la Ciudadela de la Corona de Hielo y el propio Arthas.\n\nEs hora de hacer que el Rey Exánime pague por sus crímenes.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con: Atracador del cielo Klum. Zona: Dalaran, por encima del Bosque Canto de Cristal.', 26365),
(49536, 'esES', 'La orden del Jefe de Guerra: ¡Las Cumbres Tormentosas!', 'Habla con Rin Duoctano en Dalaran, por encima del Bosque Canto de Cristal.', 'Por la presente se ordena a todos los miembros aptos de la Horda que se presenten ante Rin Duoctano en Dalaran, por encima del Bosque Canto de Cristal.\n\nLos goblins de K3 han construido una ciudad en Las Cumbres Tormentosas y han enviado a su embajador a reclutar aventureros.\n\nHabla con Rin y mira qué necesita la gente de K3. Serían unos aliados valiosos en nuestra lucha contra el Rey Exánime.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con: Rin Duoctano. Zona: Dalaran, por encima del Bosque Canto de Cristal.', 26365),
(49535, 'esES', 'La orden del Jefe de Guerra: ¡Cuenca de Sholazar!', 'Habla con el archimago Pentarus en Dalaran, por encima del Bosque Canto de Cristal.', 'Por la presente se ordena a todos los miembros aptos de la Horda que se presenten ante el archimago Pentarus en Dalaran, por encima del Bosque Canto de Cristal.\n\nSe envió a Hemet Nesingwary junto a su equipo de expedición a explorar la Cuenca de Sholazar, pero ha desaparecido.\n\nNo sabemos gran cosa acerca del exuberante paisaje de Sholazar, pero tal vez en la cuenca haya secretos que puedan darnos alguna ventaja en nuestra lucha contra el Rey Exánime.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con: Archimago Pentarus. Zona: Dalaran, por encima del Bosque Canto de Cristal.', 26365),
(49534, 'esES', 'La orden del Jefe de Guerra: ¡Zul\'Drak!', 'Habla con el señor cruzado Lantinga en Zul\'Drak.', 'Por la presente se ordena a todos los miembros aptos de la Horda que se presenten ante el señor cruzado Lantinga en Zul\'Drak.\n\nLa Plaga está masacrando a la población local en Zul\'Drak y convirtiéndola para que sea parte de su ejército.\n\nAyuda a la Cruzada Argenta y a la Espada de Ébano a detener esta amenaza.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con: Señor cruzado Lantinga. Zona: Zul\'Drak.', 26365),
(49533, 'esES', 'La orden del Jefe de Guerra: ¡Fiordo Aquilonal!', 'Habla con el boticario Lysander en el Fiordo Aquilonal.', 'Por la presente se ordena a todos los miembros aptos de la Horda que se presenten ante el boticario Lysander en el Fiordo Aquilonal.\n\nLos Renegados han establecido un fuerte en las costas orientales de Rasganorte.\n\nEs el momento de atacar a la Plaga con nuestro poderío militar y una nueva peste que está desarrollando la Sociedad Real de Boticarios.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con: Boticario Lysander. Zona: Fiordo Aquilonal.', 26365),
(49532, 'esES', 'La orden del Jefe de Guerra: ¡Valle Sombraluna!', 'Habla con el Guardia de sangre Gulmok en el Valle Sombraluna, Terrallende.', 'Por la presente se ordena a todos los miembros aptos de la Horda que se presenten ante el Guardia de sangre Gulmok en el Valle Sombraluna, Terrallende.\n\nLa Legión Ardiente intenta asediar la Aldea Sombraluna, pero nuestra fuerza de élite Kor\'kron les está enseñando lo que significa enfrentarse a la Horda.\n\nAplasta a la Legión.\n\n¡Por la Horda!', '', '', '', '', '', 'Habla con: Guardia de sangre Gulmok. Zona: Valle Sombraluna, Terrallende.', 26365),
(49354, 'esES', 'Recuerda la Fuente del Sol', 'Completa la gesta \"Recuerda la Fuente del Sol\".', 'El señor regente quiere enseñar La Fuente del Sol a la primera arcanista Thalyssra.\n\nNo sé si permitirá a Alleria Brisaveloz asistir al encuentro, pero a nosotros nos han invitado.', '', '', '', '', '', '', 26365),
(49266, 'esES', 'Forja de los Eones', 'Contempla el ritual de los forjados por la Luz.', 'Durante mucho tiempo, los forjados por la Luz han sido una señal de esperanza contra la Legión.\n\nPero muy pocos saben lo que cuesta convertirse en la encarnación de la cólera de la Luz.\n\nEste pequeño grupo de campeones del Ejército de la Luz está listo para enfrentarse a su última prueba.\n\n¿Nos honrarás con tu presencia?', '', '', '', '', '', '', 26365),
