UPDATE `quest_template_locale` SET `LogDescription`='Apportez les Armes de Martelforge à Iain Barbe-en-Feu, au Cimetière de Barbe-en-Feu.', `QuestDescription`='Vous avez nettoyé le camp et survécu aux missions de Martelforge ? Pas mal pour $gun:une:c; $C ! Apportez ça à Iain au cimetière de Barbe-en-Feu, il devrait avoir un petit quelque chose pour vous récompenser...', `VerifiedBuild`=26365 WHERE (`ID`=27815 AND `locale`='frFR');
