INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES
(50432, 'frFR', 'Innovation titanesque', '', '', '', '', '', '', '', '', 26365),
(50420, 'frFR', 'Un cadeau secoué doucement', '', '', '', '', '', '', '', '', 26365),
(50374, 'frFR', 'Le sang d\’Azeroth', 'Délivrez le message de Magni Barbe-de-Bronze.', 'Les dirigeants de l\’Alliance et de la Horde ont-ils seulement conscience de la nature de ce qu\’ils se disputent ?\n\nCette substance qui jaillit de la Plaie et qu\’ils convoitent tant est l\’essence vitale d\’Azeroth !\n\nLa blessure doit être refermée et non exploitée ! Nous ne pouvons nous résoudre à laisser mourir notre seul foyer !\n\nJ\’ai confiance en vous, $n, et je sais que les membres de l\’Alliance attachent de l\’importance à votre jugement. Vous devez leur faire entendre raison. Cette folie doit cesser à tout prix !\n\nLe sort de notre monde en dépend…', '', '', '', '', '', '', 26365),
(50373, 'frFR', 'Une arrivée récente', 'Parlez à l\’Archimage Khadgar.', 'La situation en Silithus suscite un vif intérêt.\n\nL\’archimage Khadgar s\’est présenté ici pendant votre absence. Il a fait le déplacement pour vous parler, $n.\n\nIl s\’agirait d\’une affaire urgente. J\’irais le retrouver sur-le-champ si j\’étais vous.', '', '', '', '', '', '', 26365),
(50372, 'frFR', 'Recherche dans le désert', 'Parlez à Tammy Tissebricole.', 'L\’épée de Sargeras a semé la dévastation sur ces terres.\n\nBien que leurs ruches n\’aient pas résisté au désastre, j\’ai récemment reçu des rapports faisant état du retour des silithides.\n\nNous devons découvrir ce qui a permis aux silithides de survivre et comment la lame affecte leurs ruches.\n\nLa couronne souhaite que les silithides survivants soient disséqués et étudiés.\n\nTammy Tissebricole conduit les recherches et a besoin d\’un « récolteur ».', '', '', '', '', '', '', 26365),
(50371, 'frFR', 'Convocation à Hurlevent', 'Retrouvez Mathias Shaw à Hurlevent.', 'Le roi Wrynn souhaite que vous retourniez à Hurlevent dès que possible. Une affaire urgente requiert votre attention.\n\nJe vous expliquerai tout dans le détail dès votre arrivée.\n\nVous me trouverez au Repos du lion. Dépêchez-vous… Il s\’agit d\’une occasion qui pourrait bien nous échapper !', '', '', '', '', '', '', 26365),
(50364, 'frFR', 'Le sang d\’Azeroth', 'Délivrez le message de Magni Barbe-de-Bronze.', 'Les dirigeants de l\’Alliance et de la Horde ont-ils seulement conscience de la nature de ce qu\’ils se disputent ?\n\nCette substance qui jaillit de la Plaie et qu\’ils convoitent tant est l\’essence vitale d\’Azeroth !\n\nLa blessure doit être refermée et non exploitée ! Nous ne pouvons nous résoudre à laisser mourir notre seul foyer !\n\nJ\’ai confiance en vous, $n, et je sais que les membres de la Horde attachent de l\’importance à votre jugement. Vous devez leur faire entendre raison. Cette folie doit cesser à tout prix !\n\nLe sort de notre monde en dépend…', '', '', '', '', '', '', 26365),
(50360, 'frFR', 'La requête de Khadgar', 'Parlez à l\’Archimage Khadgar.', 'L\’Archimage Khadgar s\’est présenté pendant votre absence. Il semblait vouloir vous parler, $n.\n\nAllez voir ce qu\’il veut, et ne traînez pas ! Plus vite il sera parti, mieux je me porterai.\n\nNous n\’avons pas besoin d\’un Humain qui vient fourrer son nez dans les affaires de la Horde.', '', '', '', '', '', '', 26365),
(50358, 'frFR', 'Recherche dans le désert', 'Parlez à Pixni Oxybombe.', 'L\’épée de Sargeras a semé la dévastation sur ces terres.\n\nBien que leurs ruches n\’aient pas résisté au désastre, j\’ai récemment reçu des rapports faisant état du retour des silithides.\n\nSi cela ne tenait qu\’à moi, je réduirais ces maudites ruches en cendres une bonne fois pour toutes ! Malheureusement cette décision ne dépend pas de moi.\n\nLa Dame noire exige que les silithides survivants soient disséqués et étudiés.\n\nPixni Oxybombe conduit les recherches et a besoin d\’un « récolteur ».', '', '', '', '', '', '', 26365),
(50341, 'frFR', 'Une découverte récente', 'Parlez à Nathanos le Flétrisseur.', 'Plus tôt aujourd\’hui, les dirigeants de la Horde se sont réunis pour célébrer notre victoire contre la Légion ardente.\n\nAlors que les autres festoyaient, Gallywix a demandé à ce que la Dame noire lui accorde une audience privée.\n\nIl aurait, semble-t-il, découvert une mystérieuse substance en Silithus… un minerai magique hors du commun.\n\nLe chef de guerre m\’a ordonné de vous exposer les détails, en prévision d\’une mission spéciale.', '', '', '', '', '', '', 26365),
(50338, 'frFR', 'L\’Agence Tout Riskiki', 'Tuez la Retombée visqueuse.', 'Comment avez-vous atterri ici, $n ?\n\nJe suis Loupe-Piste, de l\’Agence Tout Riskiki. Votre aide nous serait précieuse.\n\nAidez-nous à vaincre les habitants irradiés qui occupent Gnomeregan, $R, et je ferai de vous un membre honoraire de l\’Agence Tout Riskiki !\n\nPrenez ce parachute, sautez dans le Hall des engrenages, et éliminez la retombée visqueuse. Quand ce sera fait, présentez-vous au commando Bar-à-Coups-Bas, dans le couloir ouest. Vous vous entendrez comme larrons en foire, tous les deux, j\’en suis sûr.', '', '', '', '', '', '', 26365),
(50337, 'frFR', 'L\’Agence Tout Riskiki', 'Tuez l\’Électrocuteur 6000.', 'Ce Loupe-Piste est cinglé, $c. Ne montez jamais, je dis bien JAMAIS, avec ce gnome dans un avion.\n\nLa discrétion, ça n\’a jamais été mon dada, et ça ne va certainement pas être le vôtre non plus. Descendez ce couloir jusqu\’à la baie de lancement et neutralisez l\’électrocuteur 6000.\n\nL\’électrocuteur s\’occupe des avions…\n\n… et j\’déteste les avions ! Je ne monterai jamais dans aucun avion !\n\nUtilisez ce parachute pour sauter, et quand vous aurez détruit votre cible, allez voir le commando Fuité pour connaître votre prochaine mission.', '', '', '', '', '', '', 26365),
(50336, 'frFR', 'L\’Agence Tout Riskiki', 'Tuez le Mekgénieur Thermojoncteur.', 'Je ne sympathiserais ni avec Bar-à-Coups-Bas, ni avec Loupe-Piste, si j\’étais vous. Ça tourne pas rond, dans leur caboche.\n\nMettons-nous au travail ! Descendez cette galerie jusqu\’à ce que vous arriviez à la cour du Bricoleur. J\’espère que vous saurez quoi faire quand vous rencontrerez Thermojoncteur.\n\nNotre chef, le commando Hann Ibal vous attend quelque part, en bas. Il vous récompensera généreusement pour tout votre travail.\n\nEnfin, je l\’espère pour vous, en tout cas…', '', 'Faites attention à ses bombes !', 'Mekgénieur Thermojoncteur', '', '', '', 26365),
(50335, 'frFR', 'Désir de vengeance', 'Triomphez de Randolph Moloch, du Seigneur Surchauffe et de Lardeur.', 'Je me fiche de savoir à qui vous avez prêté allégeance, $R. Je veux la tête de Lardeur !\n\nIl a massacré mes hommes, avec l\’aide de tous les crétins jetés dans ce trou hors de portée de la Lumière. La Horde et l\’Alliance n\’arrivent peut-être pas à se mettre d\’accord, mais s\’il y a une chose que je ne tolère pas, c\’est qu\’on assassine mes hommes de sang froid.', '', '', '', '', '', '', 26365),
(50319, 'frFR', 'Terra incognita', 'Trouvez le Tableau de commandement du chef de guerre à Orgrimmar.', 'Bonjour. Je suis l\’ambassadeur Gardenoire.\n\n<Gardenoire pose le regard sur une feuille de papier qu\’il tient à la main.>\n\nAu nom du chef de guerre, c\’est un honneur de vous accueillir à Orgrimmar, $R.\n\n<Il vous jette un coup d\’œil, sourit d\’un air peu convaincu, puis se penche de nouveau sur son papier.>\n\nDe nombreuses aventures vous attendent, recevez donc l\’assurance de… Rah…\n\nVous savez quoi ? Je n\’ai pas arrêté de répéter ce maudit discours d\’accueil toute la journée. J\’en ai assez !\n\nAllez parler à votre émissaire et trouvez-vous de quoi vous occuper. Oust !', '', '', '', '', '', '', 26365),
(50318, 'frFR', 'Une visite à point nommé en Norfendre', 'Retrouvez Vormu à Dalaran, au-dessus de la Forêt du Chant de cristal.', 'La lumière décline dans l\’ombre d\’un corps céleste, instillant la peur dans le cœur des prophètes. Trouvez la sage Vormu à Dalaran et apportez-lui votre aide.\n\nNon, pas à cette Dalaran, l\’autre Dalaran. Oui, je sais, c\’est déroutant.', '', '', '', '', '', '', 26365),
(50316, 'frFR', 'Perturbation détectée : Ulduar', 'Revivez l\’expédition à Ulduar.', 'Vous avez du retard. Dois-je vous rappeler la fois où Yogg-Saron s\’est échappé de sa prison dans les profondeurs d\’Ulduar ? Oui, vous étiez déjà en retard ce jour-là. La ponctualité n\’a jamais été votre fort.\n\nBon, partez maintenant ! Les Dieux très anciens ! Les gardiens ! Les histoires de titans ! Nous avons bien des lieux et époques à redécouvrir.\n\n<Vormu trépigne d\’impatience alors que vous partez.>', '', '', '', '', '', '', 26365),
(50313, 'frFR', 'Terra incognita', 'Trouvez le Tableau de l\’appel des héros à Hurlevent.', 'Bienvenue à Hurlevent, $R ! Le roi Anduin Wrynn vous invite à découvrir notre jolie ville. Dites, je parie que vous êtes du genre pieux, non ? Nous avons une magnifique cathédrale qui vaut vraiment le détour !\n\nMais tout cela peut attendre. Un émissaire des Sancteforge va vous parler des aventures qui vous attendent au-delà des murs de Hurlevent.\n\nJe vous souhaite un excellent séjour, $n. Ah, et excusez-nous d\’avance pour les éventuels troubles. Nos citoyens ne font pas tous preuve de la même discipline que celle qui a fait la légende de l\’armée de la Lumière !', '', '', '', '', '', '', 26365),
(50305, 'frFR', 'Terra incognita', 'Trouvez le Tableau de l\’appel des héros à Hurlevent.', 'Bienvenue à Hurlevent, $R ! Le roi Anduin Wrynn vous invite à découvrir notre jolie ville. Dites, je parie que vous êtes du genre oiseau de nuit. Nous avons de nombreuses tavernes et une scène musicale incontournable.\n\nMais tout cela peut attendre. Un émissaire des Ren\’dorei va vous parler des aventures qui vous attendent au-delà des murs de Hurlevent.\n\nJe vous souhaite un excellent séjour, $n. Ah, et un petit conseil amical… contrôlez vos pouvoirs ténébreux. Tous, à Hurlevent, ne sont pas aussi ouverts d\’esprit que moi.', '', '', '', '', '', '', 26365),
(50303, 'frFR', 'Terra incognita', 'Trouvez le Tableau de commandement du chef de guerre à Orgrimmar.', 'Bonjour. Je suis l\’ambassadeur Gardenoire.\n\n<Gardenoire pose le regard sur une feuille de papier qu\’il tient à la main.>\n\nAu nom du chef de guerre, c\’est un honneur de vous accueillir à Orgrimmar, $R.\n\n<Il vous jette un coup d\’œil, sourit d\’un air peu convaincu, puis se penche de nouveau sur son papier.>\n\nAlors qu\’une vie d\’aventure s\’offre à vous… Rah !\n\nÇa suffit ! J\’en ai assez de lire ce maudit discours d\’accueil à longueur de journée.\n\nAllez vous trouver un endroit grouillant de trucs à combattre, puis parlez à votre émissaire.\n\nMaintenant, laissez-moi tranquille.', '', '', '', '', '', '', 26365),
(50254, 'frFR', 'Un deuxième allié pour la cause', 'Choisissez un second allié à recruter.', 'Un nouvel allié nous sera bien utile. Mais un second le sera plus encore.\n\nEn ce moment même, l\’Alliance complote et manigance contre nous. Nous devons agir vite, ou ils nous frapperont avant que nous soyons en mesure de contrer leur attaque.\n\nUn mot de vous et nous commencerons le recrutement de notre nouvel allié. Il n\’y a aucune hésitation à avoir.', '', '', '', '', '', '', 26365),
(50248, 'frFR', 'Un deuxième allié pour la cause', 'Choisissez un second allié à recruter.', 'Vous avez mené à bien la première étape du renforcement de l\’Alliance, $n. Bien joué.\n\nMais je crains qu\’un seul nouvel allié ne suffise pas à impressionner la Horde. Le chef de guerre ne cessera sa campagne que lorsque les effectifs d\’Orgrimmar dépasseront largement ceux de la garnison d\’Hurlevent.\n\nJ\’ai bien peur de devoir insister pour que vous œuvriez sans attendre à notre prochaine alliance. Nous ne pouvons nous permettre de prendre du retard.', '', '', '', '', '', '', 26365),
(50247, 'frFR', 'Les chiens aboient, le courrier passe !', 'Aidez le Maître de poste à trier au moins 15 lettres dans le temps imparti.', 'À cause des récents troubles, je suis à court de postiers-lémentaires pour veiller au tri.\n\nC\’est une tâche très simple, je vous l\’assure : il suffit de jeter la lettre dans le bon portail pour qu\’elle soit emportée jusqu\’aux coins les plus reculés d\’Azeroth et même au-delà.\n\nJ\’aime bien tourner ce boulot en petit jeu. Combien de lettres pouvez-vous trier en une minute ?', '', '', '', '', '', '', 26365),
(50242, 'frFR', 'Bien choisir ses alliés', 'Parlez à Baine et Liadrin pour en apprendre davantage sur les alliés potentiels.', 'Baine Sabot-de-Sang et Dame Liadrin sont venus nous suggérer des alliés potentiels à intégrer à nos rangs. Ils savent l\’un comme l\’autre ce dont la Horde a besoin pour rester forte.\n\nVous avez prouvé votre valeur et votre attachement à la Horde à d\’innombrables reprises, $n. Je vous laisse décider du choix à faire.\n\nÉcoutez ce que ces candidats ont à dire, puis tranchez. Faites-moi part de votre décision et nous poursuivrons notre plan de renforcement de la Horde.', '', '', '', '', '', '', 26365),
(50239, 'frFR', 'Bien choisir ses alliés', 'Parlez à Turalyon et Alleria pour en apprendre davantage sur des alliés potentiels.', 'Le grand exarque Turalyon et Alleria Coursevent nous ont suggéré de potentiels alliés. Ces deux héros ont consacré leur vie à défendre notre monde, et j\’ai entièrement confiance en leur jugement.\n\nVous êtes $gun loyal champion:une championne fidèle; de l\’Alliance, $n. Je vous laisse choisir nos premiers alliés.\n\nÉcoutez ce qu\’ils ont à dire, puis prenez votre décision et dites-moi par où nous commencerons.', '', '', '', '', '', '', 26365),
(50232, 'frFR', 'Le survivant du Crépuscule', 'Récupérez la Tête de Michiel Marche-Vide.', 'Ces terres étaient autrefois envahies par les membres du Marteau du crépuscule.\n\nOn pensait le culte exterminé, mais les ténèbres survivent toujours dans l\’ombre.\n\nLa plupart des sectateurs ont été tués par Sargeras au cours de l\’attaque, mais quelques-uns ont survécu. La destruction de Silithus a rendu leur dirigeant, Michiel Marche-Vide, beaucoup plus hardi.\n\nNous ne pouvons pas permettre que ce fanatique rassemble les survivants pour passer à l\’action. Rapportez-moi sa tête !', '', '', '', '', '', '', 26365),
(50231, 'frFR', 'Des larves par dizaines', 'Récupérez $1oa Rejetons silithides.', 'Comment les silithides ont-ils pu survivre à l\’épée de Sargeras ?\n\nJe n\’ai jamais rencontré d\’espèce capable de s\’adapter aussi vite aux changements de son environnement.\n\nIl doit y avoir moyen de dompter ces sales bêtes, d\’exploiter leurs facultés évolutives !\n\nNous devons commencer à les dresser toutes jeunes ! Oubliez les adultes, nous avons besoin de bébés !\n\nVolez autant de larves silithides que vous pourrez en transporter. Je me charge de trouver le moyen de les dresser !', '', '', '', '', '', '', 26365),
(50230, 'frFR', 'La source de puissance', 'Récupérez $1oa Cerveaux de silithide.', 'Les silithides sont agités et plus forts que jamais !\n\nD\’après mon hypothèse, ils se nourriraient d\’une sorte de puissance souterraine. Proche de l\’épée de Sargeras, probablement.\n\nMon travail consiste à étudier les effets de ce mystérieux pouvoir sur les silithides.\n\nApportez-moi autant de cerveaux intacts de silithide que vous pourrez en transporter, et je commencerai mes recherches !', '', '', '', '', '', '', 26365),
(50229, 'frFR', 'Un petit bout de tissu', 'Récupérez $1oa morceaux de Soie du Crépuscule.', '$n, c\’est vous qui faites tout ce grabuge ? C\’est moi, Angus Brassetempête !\n\nIl y a des sectateurs partout, donc je vous conseille de parler à voix basse. Approchez et surtout, bouchez-vous le nez pendant qu\’on parle !\n\nCe sont les seules toilettes de tout Silithus ! Et croyez-moi, j\’ai passé toute la nuit à chercher. Je m\’étais enfin décidé à les leur emprunter avant qu\’ils se réveillent, quand je me suis rendu compte qu\’il y avait un petit… euh… problème.\n\nIl n\’y a plus de… enfin, vous savez bien… de tissu pour…\n\nBref ! Voyez si vous pouvez me trouver un bout de tissu et me le glisser sous la porte !', '', '', '', '', '', '', 26365),
(50228, 'frFR', 'Le survivant du Crépuscule', 'Récupérez la Tête de Michiel Marche-Vide.', 'Silithus était autrefois envahie par les membres du Marteau du crépuscule.\n\nOn pensait le culte exterminé, mais il a survécu dans l\’ombre. Ses adeptes sont moins nombreux, mais tout aussi fanatiques qu\’à son apogée.\n\nLa plupart des sectateurs ont été tués par Sargeras au cours de l\’attaque, mais quelques-uns ont survécu. La destruction de Silithus a rendu leur dirigeant, Michiel Marche-Vide, beaucoup plus hardi.\n\nNous ne pouvons pas permettre que Michiel rassemble les survivants pour passer à l\’action. Il faut l\’arrêter !', '', '', '', '', '', '', 26365),
(50227, 'frFR', 'Des larves par dizaines', 'Récupérez $1oa Rejetons silithides.', 'Comment les silithides ont-ils pu survivre à l\’épée de Sargeras ? Tout ceci me laisse perplexe.\n\nJe n\’ai jamais rencontré d\’espèce capable de s\’adapter aux changements de son environnement aussi rapidement que les silithides !\n\nLes larves, ou rejetons comme on les appelle parfois, peuvent évoluer en une variété complète de créatures silithides suivant les besoins de la ruche.\n\nEst-ce le cerveau de la ruche qui décide de l\’évolution de la larve, ou bien y a-t-il des facteurs environnementaux en jeu ?\n\nLa science nous donnera la réponse !', '', '', '', '', '', '', 26365),
(50226, 'frFR', 'La source de puissance', 'Récupérez $1oa Cerveaux de silithide.', 'La destruction de Silithus semblait avoir eu raison des silithides, mais ceux-ci ont récemment resurgi des profondeurs.\n\nSous une forme plus puissante encore que la précédente !\n\nSi mon hypothèse se révèle correcte, ils doivent tirer leur puissance d\’une source d\’énergie située sous l\’épée de Sargeras.\n\nMa mission consiste à étudier l\’impact de ce mystérieux pouvoir sur les ruches silithides.\n\nRapportez-moi autant de cerveaux de silithide intacts que vous pourrez, que je commence mes recherches !', '', '', '', '', '', '', 26365),
(50131, 'frFR', 'Étranger à la rescousse', 'Devenez neutre auprès des Sporeggar.', 'Merci pour tout ce que vous avez fait pour mon peuple, mais mes semblables ont toujours peur des étrangers.\n\nCombattez les seigneurs-tourbe et protégez les sacs de spores dans le vallon des Pontes. L\’avenir même de mon peuple est en jeu !', '', '', '', '', '', '', 26365),
(50130, 'frFR', 'Un gage de bonne volonté', 'Devenez amical auprès des Sporeggar.', 'La vie dans le marécage n\’est pas simple, $n. Nous nous efforçons d\’évoluer en harmonie avec tous ses occupants, mais des ennemis n\’en menacent pas moins notre existence.\n\nPrêtez-nous main-forte. Aidez les habitants de Sporeggar à s\’épanouir.', '', '', '', '', '', '', 26365),
(50071, 'frFR', 'Pour la Lumière !', 'Retournez à Hurlevent et parlez au roi Anduin Wrynn.', 'Les Draeneï sancteforge se feront un plaisir de rejoindre l\’Alliance.\n\nVotre cause est désormais la nôtre, $n.\n\nRetournons à Hurlevent, où le capitaine Fareeya s\’adressera à nos frères d\’armes.', '', '', '', '', '', '', 26365),
(50055, 'frFR', 'La perspective de l\’orateur', 'Remettez la Lettre de Khadgar à Magni Barbe-de-Bronze en Silithus.', 'Sargeras a infligé une blessure à notre monde, $n. Une balafre qui ne guérira pas de sitôt…\n\nMais les plaies ouvertes par les conflits entre l\’Alliance et la Horde doivent elles aussi être pansées.\n\nTrop de vies ont déjà été perdues dans la lutte pour sauver Azeroth de la destruction. Je refuse de voir en perdre d\’autres sans rien faire.\n\nJ\’ai un service à vous demander avant que vous partiez. Apportez cette lettre à Magni Barbe-de-Bronze. Il est en Silithus où il cherche le moyen de soigner la plaie.\n\nLa lettre contient des informations qui pourraient lui être utiles.', '', '', '', '', '', '', 26365),
(50054, 'frFR', 'Le rivage Bouillonnant', 'Terminez le Champ de bataille du Rivage Bouillonnant.', 'D\’après nos rapports, il existe une île au large des côtes de Silithus qui regorge de filons d\’azérite.\n\nIl est vital que nous prenions possession de cette île avant même que l\’Alliance ait vent de son existence.\n\nNathanos le Flétrisseur dirige personnellement la force d\’intervention et il a demandé que vous vous mettiez à ses ordres. Allez dans l\’honneur !', '', '', '', '', '', '', 26365),
(50053, 'frFR', 'Bons à rien de prospecteurs !', 'Motivez $1oa Prospecteurs fainéants.', 'Je ne suis pas né de la dernière pluie. Je sais qu\’il ne faut jamais confier un travail d\’Orc à un Gobelin.\n\nGallywix et son cartel servent peut-être le chef de guerre, mais si nous voulons avoir des résultats, nous avons intérêt à ne pas les lâcher d\’une semelle !\n\nJ\’ai déjà pris des Gobelins en flagrant délit d\’oisiveté et de fainéantise. Il n\’y a pas de place dans la Horde pour les tire-au-flanc !\n\nAllez botter les fesses de ces bons à rien de prospecteurs et remettez-les au travail !', '', '', '', '', '', '', 26365),
(50052, 'frFR', 'Espions interdits', 'Tuez $1oa Agents du SI:7 en Silithus.', 'Salut, $n. Vous tombez à pic.\n\nJe ne sais plus où donner de la tête ! Des insectes tueurs, une substance étrange… cette épée GIGANTESQUE plantée dans le sol… Franchement, cet endroit ne me dit rien qui vaille !\n\nEt, comme si ça ne suffisait pas, je reçois régulièrement des rapports indiquant que des espions de l\’Alliance rôdent autour du camp !\n\nLa Dame noire a besoin de ce minerai mystérieux et je ne voudrais surtout pas la décevoir, si vous voyez ce que je veux dire.\n\nAllez au camp d\’extraction et éliminez ces espions du SI:7 avant qu\’ils fassent capoter toute l\’opération !', '', '', '', '', '', '', 26365),
(50049, 'frFR', 'La perspective de l\’orateur', 'Remettez la Lettre de Khadgar à Magni Barbe-de-Bronze en Silithus.', 'Sargeras a infligé une blessure à notre monde, $n. Une balafre qui ne guérira pas de sitôt…\n\nMais les plaies ouvertes par les conflits entre l\’Alliance et la Horde doivent elles aussi être pansées.\n\nTrop de vies ont déjà été perdues dans la lutte pour sauver Azeroth de la destruction. Je refuse de voir en perdre d\’autres sans rien faire.\n\nJ\’ai un service à vous demander avant que vous partiez. Apportez cette lettre à Magni Barbe-de-Bronze. Il est en Silithus où il cherche le moyen de soigner la plaie.\n\nLa lettre contient des informations qui pourraient lui être utiles.', '', '', '', '', '', '', 26365),
(50048, 'frFR', 'Le rivage Bouillonnant', 'Terminez le Champ de bataille du Rivage Bouillonnant.', 'D\’après nos rapports, il existe une île au large des côtes de Silithus qui regorge de filons d\’azérite.\n\nIl est vital que nous prenions possession de cette île avant même que la Horde ait vent de son existence.\n\nLe maître Mathias Shaw dirige personnellement la force d\’intervention et il a demandé que vous vous mettiez à ses ordres. Bonne chance !', '', '', '', '', '', '', 26365),
(50047, 'frFR', 'Échantillons gratuits', 'Récoltez $1oa Échantillons de Minerai mystérieux.', 'La couronne a ordonné au SI:7 d\’infiltrer l\’équipe d\’extraction gobeline et de rassembler toutes les informations disponibles sur cette mystérieuse nouvelle substance.\n\nMes agents se sont introduits dans le campement, mais l\’heure tourne et nous devons rapidement mettre la main sur un échantillon.\n\nEn d\’autres termes, j\’ai besoin de quelqu\’un qui tente une approche frontale. Quelqu\’un capable de mener une mission suicide et d\’en revenir bien vivant.\n\nQuand vous serez de retour, apportez les échantillons de minerai à Khargus Mantepierre pour qu\’il les analyse.', '', '', '', '', '', '', 26365),
(50046, 'frFR', 'C\’est du sabotage', 'Posez $1oa paquets de dynamite.', 'La Horde n\’a pas chômé. Les gobelins extraient déjà sans vergogne ce mystérieux minerai de la Plaie.\n\nHeureusement pour nous, l\’ingénierie gobeline est loin d\’être aussi subtile que celle que nous pratiquons à Gnomeregan ! Il suffit d\’une dose adéquate de cette bonne vieille dynamite pour envoyer un déchiqueteur gobelin à la casse.\n\nLeur technologie est aussi simpliste qu\’indigente !\n\nPrenez ces paquets de dynamite et posez-les sur les déchiqueteurs inactifs disséminés sur le site d\’extraction.\n\nAvec leurs doigts crochus, il faudra des semaines à ces avares de gobelins pour tout réparer !', '', '', '', '', '', '', 26365),
(50035, 'frFR', 'Bret Test - SQL', '', '', '', '', '', '', '', '', 26365),
(49982, 'frFR', 'Témoin de la blessure', 'Parlez à Grol Lame-de-Guerre en Silithus.', 'Le cartel Baille-Fonds a déjà commencé les travaux d\’excavation en Silithus pour extraire cette mystérieuse substance.\n\nLe chef de guerre a été très clair : c\’est à vous de superviser personnellement les manœuvres d\’extraction en Silithus et d\’empêcher l\’Alliance d\’interférer.\n\nGallywix n\’a pas eu l\’air enchanté de votre nomination, mais personne ne tient tête à Sylvanas…\n\nVous devez vous rendre en Silithus immédiatement. Le commandant de la base attend votre arrivée.', '', '', '', '', '', '', 26365),
(49981, 'frFR', 'Témoin de la blessure', 'Retrouvez le Commandant du SI:7 en Silithus.', 'J\’ai déjà envoyé des agents en Silithus pour y établir un camp de base.\n\nLeurs ordres sont d\’observer les agissements de la Horde et de documenter toute information qu\’ils pourraient trouver au sujet de cette mystérieuse substance.\n\nL\’un de mes commandants du SI:7 attend votre arrivée en ce moment même. Hâtez-vous.', '', '', '', '', '', '', 26365),
(49977, 'frFR', 'Convocation à Orgrimmar', 'Parlez à Nathanos le Flétrisseur.', 'Le chef de guerre a demandé que vous reveniez à Orgrimmar dès que possible. Il y a un problème urgent qui requiert votre attention.\n\nJe vous ferai part des détails dès votre arrivée.\n\nVous me trouverez non loin de l\’entrée de Fort Grommash. La Dame noire a des projets pour vous, $n. Personnellement, je ne la ferais pas attendre…', '', '', '', '', '', '', 26365),
(49976, 'frFR', 'Dons des déchus', 'Parlez à Mathias Shaw.', 'Plus tôt dans la journée, le roi Wrynn s\’est rendu au Repos du lion pour annoncer officiellement la fin de la guerre.\n\nUne fois la foule dissipée, je me suis joint au roi et à ses conseillers à titre privé pour discuter de la mystérieuse substance découverte en Silithus.\n\nCe minerai éclatant serait d\’une grande puissance. Et la Horde n\’a naturellement pas perdu de temps pour en extraire de la Plaie.\n\nLe roi m\’a ordonné de vous tenir au courant des détails, en vue d\’une mission de la plus haute importance.', '', '', '', '', '', '', 26365),
(49973, 'frFR', 'Le domaine de Thalyssra', 'Retrouvez Dame Liadrin au Domaine de la Première arcaniste, à Suramar.', 'Le seigneur régent Theron a invité les Sacrenuit à le rejoindre à Lune-d\’Argent.\n\nTout d\’abord, nous devons leur faire part de l\’invitation. Et je préfère traiter ce genre d\’affaires en personne.\n\nJe vous demande de m\’accompagner à Suramar et d\’y rencontrer dame Thalyssra.', '', '', '', '', '', '', 26365),
(49933, 'frFR', 'Pour la Horde', 'Allez voir l\’ambassadeur Gardenoire à l\’ambassade, à Orgrimmar.', 'Après des millénaires d\’isolement, nous sommes de nouveau prêts à faire partie d\’un tout supérieur. Notre alliance avec la Horde nous offre cette possibilité, c\’est une chance unique qui ne doit pas être gâchée.\n\nL\’ambassadeur du chef de guerre vous attend à Orgrimmar, $n. C\’est là que vous entamerez votre voyage sur Azeroth. Défendez cette terre et prouvez que les Shal\’dorei y ont leur place.\n\nMontrez au monde qui nous sommes, $c. Faites honneur à la Horde, car c\’est tout Suramar que vous glorifierez.', '', '', '', '', '', 'Allez voir l\’ambassadeur Gardenoire à l\’ambassade, à Orgrimmar.', 26365),
(49930, 'frFR', 'L\’appel aux alliés', 'Retrouvez le Chef de guerre Sylvanas Coursevent à l\’Ambassade, à Orgrimmar.', 'L\’altruisme dont a fait preuve le commandement de la Horde pendant la guerre contre la Légion a affaibli nos rangs. Nos ennemis se réjouissent à l\’idée de voler ce qui nous appartient. Nous ne les laisserons pas faire.\n\nJ\’ai autorisé la construction d\’une ambassade à Orgrimmar. Grâce à elle, nous gagnerons d\’autres appuis à notre cause. Rejoignez-moi là-bas immédiatement.', '', '', '', '', '', '', 26365),
(49929, 'frFR', 'L\’appel aux alliés', 'Retrouvez le Roi Wrynn à l\’Ambassade, à Hurlevent.', 'La Horde ne reculera devant rien pour accaparer le sang d\’Azeroth. Mais je crains que la guerre contre la Légion ait trop affaibli nos rangs pour que nous puissions parer leurs attaques.\n\nIl est temps pour l\’Alliance de rallier de nouveaux membres à sa cause. Nous avons établi une ambassade à Hurlevent pour conduire ces efforts. Retrouvez-moi là-bas.', '', '', '', '', '', '', 26365),
(49928, 'frFR', 'Héritage du Vide', 'Retrouvez Alleria Coursevent à la Faille de Telogrus.', 'Au cours de vos périples sur Azeroth, vous avez appris à maîtriser ces forces qui ne vous laissent aucun répit.\n\nVous êtes désormais une arme redoutable au service de l\’Alliance, $p. Regagnez la faille du Vide et nous célébrerons votre réussite comme il se doit.', '', '', '', '', '', '', 26365),
(49866, 'frFR', 'Direction la Pandarie !', 'Présentez-vous à Rell Ventenuit à Hurlevent.', 'Rendez-vous immédiatement au donjon de Hurlevent où le roi Varian Wrynn vous fait mander.$b$bNous avons eu vent d\’un conflit dans les mers du sud et avons besoin $gd\’un:d\’une:c; $c de votre trempe pour en savoir plus.', '', '', '', '', '', '', 26365),
(49865, 'frFR', 'Sauveteur du Cataclysme', 'Présentez-vous à Naraat le Parleterre à Hurlevent.', 'Aile de mort, l\’Aspect de la mort draconique, a envoyé ses serviteurs écumer Azeroth à la recherche de toute forme de vie.$b$bNaraat le Parleterre pourra vous en dire plus sur le mont Hyjal, Vashj\’ir et les autres zones ciblées par Aile de mort.', '', '', '', '', '', '', 26365),
(49864, 'frFR', 'Guerres sur deux fronts', 'Utilisez les missions spéciales pour choisir votre prochaine destination.', '$n ! L\’Alliance entend faire une nouvelle fois appel à vous pour une mission de la plus haute importance.\n\nParticiperez-vous aux efforts de guerre contre Aile de mort et le Marteau du crépuscule ?\n\nOu volerez-vous vers le sud jusqu\’en Pandarie, afin de vous emparer des ressources de ce territoire avant la Horde ?\n\nFaites votre choix et joignez-vous au combat !', '', '', '', '', '', '', 26365),
(49863, 'frFR', 'Direction le Norfendre !', 'Faites voile pour le Norfendre.$b$bMontez à bord du navire amarré au Port de Hurlevent et en partance pour la Toundra Boréenne, ou embarquez sur celui des Paluns qui se rend au Fjord Hurlant.', 'Le roi-liche s\’agite au nord.$b$bSon armée de morts-vivants marche sur le Norfendre, ne laissant que mort et destruction dans son sillage.$b$bVous devez les arrêter, $n.', '', '', '', '', '', '', 26365),
(49862, 'frFR', 'Direction l\’Outreterre !', 'Franchissez la Porte des ténèbres dans les Terres Foudroyées.', 'La Légion ardente, une armée composée d\’innombrables démons, a attaqué l\’Outreterre, le monde natal dévasté des Draeneï. Nous avons besoin des aventuriers de l\’Alliance pour empêcher l\’annihilation totale de l\’Outreterre par l\’envahisseur.$b$bRendez-vous dans les terres Foudroyées et franchissez la Porte des ténèbres.', '', '', '', '', '', '', 26365),
(49860, 'frFR', 'L\’appel de l\’aventure', 'Utilisez les missions spéciales pour choisir votre prochaine destination.', 'Les récits de votre héroïsme ont gagné les confins d\’Azeroth… et au-delà. Vos talents sont sollicités ailleurs.\n\nAllez-vous franchir la Porte des ténèbres pour combattre la Légion ardente sur le monde dévasté d\’Outreterre ?\n\nOu voler vers le nord pour rejoindre l\’armée qui affronte le roi-liche en Norfendre ?\n\nLe choix vous appartient, $gaventurier:aventurière;.', '', '', '', '', '', '', 26365),
(49855, 'frFR', 'Catastrophe au mont Hyjal', 'Parlez à l\’Émissaire cénarien Sabot-Noir pour obtenir un moyen de transport pour Reflet-de-Lune.', 'Nos frères du Cercle cénarien nous signalent que les serviteurs d\’Aile de mort ont lancé une offensive destructrice sur l\’Arbre-Monde et menacent de le réduire en cendres !$B$BAdressez-vous à l\’émissaire cénarien Sabot-Noir afin d\’accéder à Reflet-de-Lune. Vous le trouverez dans le quartier tauren de la vallée de la Sagesse, à Orgrimmar.', 'Passage vers Reflet-de-Lune obtenu', '', '', '', '', 'Parlez à l\’Émissaire Chant-du-vent à Havrenuit, à Reflet-de-Lune.', 26365),
(49852, 'frFR', 'Direction la Pandarie !', 'Présentez-vous au Fort Grommash à Orgrimmar.', 'Vous êtes $gsommé:sommée; de vous présenter sur-le-champ au fort Grommash.$b$bOn nous a rapporté l\’existence d\’un nouveau continent au sud. Nous comptons vous y envoyer, $gaccompagné:accompagnée; d\’un petit contingent de soldats, afin de revendiquer le territoire et ses ressources pour la Horde.', '', '', '', '', '', '', 26365),
(49851, 'frFR', 'Sauveteur du Cataclysme', 'Présentez-vous au Long-voyant Krogar à Orgrimmar.', 'Aile de mort, l\’Aspect de la mort draconique, a envoyé ses serviteurs écumer Azeroth à la recherche de toute forme de vie.$b$b Le long-voyant Krogar d\’Orgrimmar pourra vous en dire plus sur le mont Hyjal, Vashj\’ir et les autres zones ciblées par Aile de mort.', '', '', '', '', '', '', 26365),
(49846, 'frFR', 'Guerres sur deux fronts', 'Utilisez les missions spéciales pour choisir votre prochaine destination.', '$n ! La Horde entend faire une nouvelle fois appel à vous pour une mission de la plus haute importance.\n\nParticiperez-vous aux efforts de guerre contre Aile de mort et le Marteau du crépuscule ?\n\nOu volerez-vous vers le sud jusqu\’en Pandarie, afin de vous emparer des ressources de ce territoire avant l\’Alliance ?\n\nFaites votre choix et joignez-vous au combat !', '', '', '', '', '', '', 26365),
(49817, 'frFR', 'Direction le Norfendre !', 'Prenez un zeppelin pour le Norfendre.$b$bMontez à bord de celui amarré dans les Clairières de Tirisfal en partance pour le Fjord Hurlant, ou embarquez sur celui d\’Orgrimmar en partance pour la Toundra Boréenne.', 'Le roi-liche s\’agite au nord.$b$bSon armée de morts-vivants marche sur le Norfendre, ne laissant que mort et destruction dans son sillage.$b$bVous devez les arrêter, $n.', '', '', '', '', '', '', 26365),
(49816, 'frFR', 'Direction l\’Outreterre !', 'Franchissez la Porte des ténèbres dans les Terres Foudroyées.', 'La Légion ardente, une armée composée d\’innombrables démons, a attaqué l\’Outreterre, le monde natal dévasté des Orcs. Nous avons besoin des aventuriers de la Horde pour empêcher l\’annihilation totale de l\’Outreterre par l\’envahisseur.$b$bRendez-vous dans les terres Foudroyées et franchissez la Porte des ténèbres.', '', '', '', '', '', '', 26365),
(49813, 'frFR', 'L\’appel de l\’aventure', 'Utilisez les missions spéciales pour choisir votre prochaine destination.', 'Les récits de votre héroïsme ont gagné les confins d\’Azeroth… et au-delà. Vos talents sont sollicités ailleurs.\n\nAllez-vous franchir la Porte des ténèbres pour combattre la Légion ardente sur le monde dévasté d\’Outreterre ?\n\nOu voler vers le nord pour rejoindre l\’armée qui affronte le roi-liche en Norfendre ?\n\nLe choix vous appartient, $gaventurier:aventurière;.', '', '', '', '', '', '', 26365),
(49812, 'frFR', 'Délas à Dalaran', 'Trouvez Délas Croc-de-Lune au Monument à Antonidas, à Dalaran.', 'Délas Croc-de-Lune a besoin de notre aide. En échange, elle nous aidera à déchiffrer le codex de commandement.$B$BTrouvez-la au plus vite, je vous en prie. Elle se recueille souvent au monument dédié à la mémoire d\’Antonidas, le grand héros du Kirin Tor.', '', '', '', '', '', 'Trouvez Délas Croc-de-Lune au Monument à Antonidas, à Dalaran.', 26365),
(49788, 'frFR', 'Pour l\’Alliance', 'Allez voir l\’ambassadeur Mauregarde à l\’ambassade, à Hurlevent.', 'Contrôler les murmures requiert discipline et une concentration de tous les instants. Votre dévouement est une véritable source d\’inspiration pour les Ren\’dorei… car nous sommes les enfants du Vide.\n\nVous avez appris de nombreuses leçons, ici, mais il est temps pour vous de vous aventurer au-delà de la faille de Telogrus.\n\nRendez-vous à Hurlevent, le cœur de l\’Alliance. L\’ambassadeur du roi vous y accueillera et vous guidera tout au long de votre périple.\n\nAzeroth doit être défendue. Personne n\’est plus à même de répondre à cet appel que vous, $p.', '', '', '', '', '', 'Allez voir l\’ambassadeur Mauregarde à l\’ambassade, à Hurlevent.', 26365),
(49787, 'frFR', 'Les terres Fantômes', 'Trouvez la demeure d\’Umbric dans les Terres Fantômes.', 'Un Elfe de sang du nom d\’Umbric et ses serviteurs ont été chassés de Lune-d\’Argent pour avoir étudié la magie du Vide.\n\nBien que cette pratique soit interdite, c\’est une sentence extrême.\n\nCependant, cela ouvre des possibilités pour l\’Alliance. Si nous parvenons à trouver ces parias, nous pourrons peut-être les rallier à notre cause et, bien plus important, les aider à comprendre le Vide avant qu\’il les corrompe.', '', '', '', '', '', '', 26365),
(49784, 'frFR', 'Héritage des Sacrenuit', 'Allez voir la Première arcaniste Thalyssra au Palais Sacrenuit.', 'Vous faites partie des premiers Shal\’dorei à avoir exploré les étendues d\’Azeroth. Il est temps pour vous de rentrer à la maison.\n\nRevenez à Suramar. Grâce à vous, notre peuple a retrouvé sa place dans ce monde, et nous tenons à vous remercier.', '', '', '', '', '', '', 26365),
(49783, 'frFR', 'L\’héritage de Haut-Roc', 'Retrouvez Mayla Haut-Roc à Totem-du-Tonnerre.', 'Vous avez fait un long séjour en Azeroth, $p. Et au cours de celui-ci, vous avez porté haut les couleurs de Haut-Roc.\n\nTotem-du-Tonnerre vous invite à présent à revenir. Il est temps que l\’on vous remercie pour tout ce que vous avez accompli.', '', '', '', '', '', '', 26365),
(49782, 'frFR', 'Héritage des Sancteforge', 'Allez voir le Capitaine Fareeya sur le Vindicaar.', 'Vous avez fait preuve d\’une grande dévotion à l\’Alliance, $p. Je n\’aurais fait mieux moi-même. Enfin si… mais de peu.\n\nRejoignez-moi à bord du Vindicaar. Vos efforts assidus méritent récompense.', '', '', '', '', '', '', 26365),
(49773, 'frFR', 'Pour la Horde', 'Allez voir l\’ambassadeur Gardenoire à l\’ambassade, à Orgrimmar.', 'Une nouvelle ère s\’ouvre pour Haut-Roc, $n.\n\nAprès des siècles d\’isolement, les liens avec nos cousins de Kalimdor se sont resserrés. Notre combat pour sauver notre terre nous a permis de nouer de solides amitiés avec les peuples de la Horde. \n\nAfin de montrer à nos nouveaux alliés la puissance dont nous sommes capables, de braves Taurens comme vous se doivent de répondre à l\’appel de l\’aventure.\n\nVous avez reçu une invitation en provenance d\’Orgrimmar. Retrouvez-y l\’ambassadeur à l\’ambassade du chef de guerre, puis entamez votre périple en Azeroth. \n\nQue la Terre-Mère soit avec vous, $n.', '', '', '', '', '', 'Allez voir l\’ambassadeur Gardenoire à l\’ambassade, à Orgrimmar.', 26365),
(49772, 'frFR', 'Pour l\’Alliance', 'Allez voir l\’ambassadeur Mauregarde à l\’ambassade, à Hurlevent.', 'L\’armée de la Lumière a été fondée dans un seul et unique but : vaincre la Légion. Mais bien que cet objectif soit aujourd\’hui atteint, le devoir nous appelle de nouveau.\n\nAzeroth est un monde assailli d\’ennemis. Utilisez votre savoir pour vaincre les ténèbres et faire respecter les idéaux de l\’Alliance.\n\nAllez à Hurlevent, trouvez l\’ambassadeur du roi à l\’ambassade, puis apportez la justice de la Lumière à tous.\n\nPheta thones gamera, $n.', '', '', '', '', '', 'Allez voir l\’ambassadeur Mauregarde à l\’ambassade, à Hurlevent.', 26365),
(49756, 'frFR', 'Forces ténébreuses', 'Éliminez Qy\’telek, héraut d\’Uul\’gyneth.', 'Les Dieux très anciens ont déchaîné l\’un de leurs sans-visage sur les Pitons-du-Tonnerre. Ce monstre est en train de profaner le foyer de mes semblables !\n\nNous devons unir nos forces pour débarrasser cet endroit d\’une telle ignominie.\n\nJ\’ose espérer que la destruction de ce héraut fera fuir nos ennemis de Mulgore. Que nos armes frappent juste, $n !', '', 'Héraut d\’Uul\’gyneth', 'Qy\’telek', '', '', '', 26365),
(49698, 'frFR', 'Les Sancteforge', 'Accompagnez Turalyon jusqu\’au Vindicaar.', '$n, vous avez vu de vos yeux ce dont mes frères sont capables. Depuis des millénaires, nous sommes forgés spécialement pour combattre la Légion.\n\nAujourd\’hui, je crois qu\’il est temps de prendre les armes aux côtés de nos frères et sœurs de l\’Alliance.\n\nRejoignez-nous au Vindicaar. Je pense que nous avons bien des choses à discuter avec le capitaine Fareeya.', '', '', '', '', '', 'Allez voir le capitaine Fareeya sur le Vindicaar.', 26365),
(49614, 'frFR', 'Les Sacrenuit', 'Retournez voir le Chef de guerre à Orgrimmar.', 'Le chef de guerre en sera ravi.\n\nThalyssra et ses Sacrenuit feront des alliés de poids, à n\’en point douter.', '', '', '', '', '', '', 26365),
(49613, 'frFR', 'Lune-d\’Argent', 'Rejoignez Dame Liadrin à Lune-d\’Argent.', 'Les Sacrenuit ont vécu isolés des millénaires durant. Mais ils se sont à nouveau dévoilés au monde et c\’est à nous de les guider sur le droit chemin.\n\nLe seigneur régent Theron les a conviés à Lune-d\’Argent.\n\nVenez, $n, rendons-nous à Lune-d\’Argent avec Thalyssra.', '', '', '', '', '', '', 26365),
(49567, 'frFR', 'L\’appel du héros : Nagrand !', 'Parlez au Lieutenant Balfor en Nagrand, sur Draenor.', 'Tous les citoyens de l\’Alliance en état de porter les armes sont sommés de se présenter au lieutenant Balfor en Nagrand, sur Draenor.\n\nYrel a convaincu l\’Alliance d\’y établir un avant-poste dans le cadre de notre lutte contre le clan Chanteguerre.\n\nRalliez Nagrand par la voie des airs et rejoignez Balfor et la caravane de ravitaillement.\n\nGloire à l\’Alliance !', '', '', '', '', '', 'Parlez au Lieutenant Balfor en Nagrand, sur Draenor.', 26365),
(49566, 'frFR', 'L\’appel du héros : les flèches d\’Arak !', 'Parlez à Azik dans les Flèches d\’Arak.', 'Tous les citoyens de l\’Alliance en état de porter les armes sont sommés de se présenter à Azik, dans les flèches d\’Arak.\n\nDes arakkoa dépourvus d\’ailes ont été aperçus fuyant les flèches d\’Arak. À en croire la rumeur, c\’est une super-arme de la Horde de Fer qui provoquerait cette panique.\n\nNous devons découvrir les raisons précises de leur exil, ainsi que la cause de cette étrange atrophie de leurs ailes.\n\nGloire à l\’Alliance !', '', '', '', '', '', 'Parlez à Azik dans les Flèches d\’Arak.', 26365),
(49565, 'frFR', 'L\’appel du héros : Talador !', 'Parlez au Contremaître Eksos en Talador.', 'Tous les citoyens de l\’Alliance en état de porter les armes sont sommés de se présenter au contremaître Eksos, en Talador.\n\nLa Horde de Fer a jeté son dévolu sur cette région idéalement située pour envoyer rapidement et facilement des renforts aux quatre coins de Draenor.\n\nAussi le contremaître Eksos a-t-il été chargé d\’y établir un avant-poste. Rendez-vous sur place et arrêtez la Horde de Fer.\n\nGloire à l\’Alliance !', '', '', '', '', '', 'Parlez au Contremaître Eksos en Talador.', 26365),
(49564, 'frFR', 'L\’appel du héros : Gorgrond !', 'Parlez à Yrel en Gorgrond.', 'Tous les citoyens de l\’Alliance en état de porter les armes sont sommés de se présenter à Yrel, en Gorgrond.\n\nElle organise une expédition à destination des terres sauvages de la région avec le concours de Maraad.\n\nEn explorant ces terres plus avant, nous espérons trouver un moyen d\’exploiter les faiblesses de la Horde de Fer.\n\nAidez-les dans leur entreprise.\n\nGloire à l\’Alliance !', '', '', '', '', '', 'Parlez à Yrel en Gorgrond.', 26365),
(49562, 'frFR', 'L\’appel du héros : la vallée d\’Ombrelune !', 'Parlez au Prophète Velen dans la Vallée d\’Ombrelune, sur Draenor.', 'Tous les citoyens de l\’Alliance en état de porter les armes sont sommés de se présenter au prophète Velen dans la vallée d\’Ombrelune, sur Draenor.\n\nLa destruction de la Porte des ténèbres a empêché la Horde de Fer d\’envahir Azeroth, mais ces vauriens n\’ont pas dit leur dernier mot.\n\nAvec la bénédiction de Velen, nous allons établir une base stratégique sur Draenor pour lutter contre les ennemis de notre monde.\n\nGloire à l\’Alliance !', '', '', '', '', '', 'Parlez au Prophète Velen dans la Vallée d\’Ombrelune, sur Draenor.', 26365),
(49561, 'frFR', 'L\’appel du héros : les terres de l\’Angoisse !', 'Parlez au Maître-archer Li sur l\’Échine du Serpent.', 'Tous les citoyens de l\’Alliance en état de porter les armes sont sommés de se présenter au maître-archer Li à l\’Échine du Serpent, dans le val de l\’Éternel printemps.\n\nLes Pandashan sont aux prises avec une armée de mantides qui menace d\’envahir l\’ensemble de la Pandarie.\n\nVous pouvez vous rendre sur place par la voie des airs à l\’aide d\’un cerf-volant.\n\nGloire à l\’Alliance !', '', '', '', '', '', 'Parlez au Maître-archer Li sur l\’Échine du Serpent.', 26365),
(49560, 'frFR', 'L\’appel du héros : les steppes de Tanglong !', 'Parlez à Taran Zhu dans les Steppes de Tanglong.', 'Tous les citoyens de l\’Alliance en état de porter les armes sont sommés de se présenter à Taran Zhu, dans les steppes de Tanglong.\n\nLes Pandashan veulent à tout prix reconquérir leurs terres tombées aux mains des yaungols, mais leurs effectifs sont insuffisants.\n\nAidez-les à chasser l\’envahisseur !\n\nGloire à l\’Alliance !', '', '', '', '', '', 'Parlez à Taran Zhu dans les Steppes de Tanglong.', 26365),
(49559, 'frFR', 'L\’appel du héros : le sommet de Kun-Lai !', 'Parlez au Messager grumelot dans la Vallée des Quatre vents.', 'Tous les citoyens de l\’Alliance en état de porter les armes sont sommés de prêter main-forte au chroniqueur Cho, au sommet de Kun-Lai.\n\nAdressez-vous à un messager grumelot dans la vallée des Quatre vents, et il vous indiquera le chemin.\n\nGloire à l\’Alliance !', '', '', '', '', '', 'Parlez au Messager grumelot dans la Vallée des Quatre vents.', 26365),
(49558, 'frFR', 'L\’appel du héros : les étendues sauvages de Krasarang !', 'Parlez à Ken-Ken dans les Étendues sauvages de Krasarang.', 'Tous les citoyens de l\’Alliance en état de porter les armes sont sommés de se présenter au guet de Zhu, dans les étendues sauvages de Krasarang.\n\nLe bruit court que les Pandarens de la région sont plongés dans une étrange léthargie.\n\nAllez voir Ken-Ken, au guet de Zhu. Il pourra peut-être vous en dire plus sur cet étrange phénomène.', '', '', '', '', '', 'Parlez à Ken-Ken dans les Étendues sauvages de Krasarang.', 26365),
(49557, 'frFR', 'L\’appel du héros : la vallée des Quatre vents !', 'Parlez à Chen Brune d\’Orage dans la Vallée des Quatre vents.', 'Tous les citoyens de l\’Alliance en état de porter les armes sont sommés de se présenter à Chen Brune d\’Orage, dans la vallée des Quatre vents.\n\nLes Brune d\’Orage sont réputés pour leur audace, et l\’Alliance gagnerait à s\’en inspirer.\n\nPartez à l\’aventure avec eux et découvrez les secrets de la Pandarie.\n\nGloire à l\’Alliance !', '', '', '', '', '', 'Parlez à Chen Brune d\’Orage dans la Vallée des Quatre vents.', 26365),
(49556, 'frFR', 'L\’appel du héros : la forêt de Jade !', 'Allez voir le Grand amiral Jes-Tereth au Donjon de Hurlevent.', 'Tous les citoyens de l\’Alliance en état de porter les armes sont sommés de se présenter au donjon de Hurlevent.\n\nLe grand amiral Jes-Tereth rassemble une équipe en vue d\’une mission de la plus haute importance.\n\nRendez-vous à Hurlevent séance tenante pour recevoir vos instructions.\n\nGloire à l\’Alliance !', '', '', '', '', '', '', 26365),
(49555, 'frFR', 'L\’appel du héros : la Couronne de glace !', 'Parlez à l\’Officier Van Rossem à Dalaran, au-dessus de la Forêt du Chant de cristal.', 'Tous les citoyens de l\’Alliance en état de porter les armes sont sommés de se présenter à l\’officier Van Rossem à Dalaran, au-dessus de la forêt du Chant de cristal.\n\nL\’expédition de la Bravoure a besoin d\’aventuriers supplémentaires pour participer au siège de la citadelle de la Couronne de glace où Arthas s\’est retranché.\n\nL\’heure du châtiment a sonné pour le roi-liche.\n\nGloire à l\’Alliance !', '', '', '', '', '', 'Parlez à l\’Officier Van Rossem à Dalaran, au-dessus de la Forêt du Chant de cristal.', 26365),
(49554, 'frFR', 'L\’appel du héros : les pics Foudroyés !', 'Parlez à Rin Duoctane à Dalaran, au-dessus de la Forêt du Chant de cristal.', 'Tous les citoyens de l\’Alliance en état de porter les armes sont sommés de se présenter à Rin Duoctane à Dalaran, au-dessus de la forêt du Chant de cristal.\n\nLes gobelins de K3 ont fondé une ville dans les pics Foudroyés et ils nous envoient leur ambassadeur pour recruter des aventuriers.\n\nAllez voir Rin et voyez comment pourvoir aux besoins des habitants de K3. Ils constitueraient de précieux alliés dans notre lutte contre le roi-liche.\n\nGloire à l\’Alliance !', '', '', '', '', '', 'Parlez à Rin Duoctane à Dalaran, au-dessus de la Forêt du Chant de cristal.', 26365),
(49553, 'frFR', 'L\’appel du héros : le bassin de Sholazar !', 'Parlez à l\’Archimage Pentarus à Dalaran, au-dessus de la Forêt du Chant de cristal.', 'Tous les citoyens de l\’Alliance en état de porter les armes sont sommés de se présenter à l\’archimage Pentarus à Dalaran, au-dessus de la forêt du Chant de cristal.\n\nParti explorer le bassin de Sholazar, Hemet Nesingwary a disparu avec l\’ensemble de son expédition.\n\nNous en savons très peu sur cette contrée luxuriante, mais elle abrite peut-être des secrets susceptibles de nous aider dans notre lutte contre le roi-liche.\n\nGloire à l\’Alliance !', '', '', '', '', '', 'Parlez à l\’Archimage Pentarus à Dalaran, au-dessus de la Forêt du Chant de cristal.', 26365),
(49552, 'frFR', 'L\’appel du héros : Zul\’Drak !', 'Parlez au Seigneur croisé Lantinga à Zul\’Drak.', 'Tous les citoyens de l\’Alliance en état de porter les armes sont sommés de se présenter au seigneur croisé Lantinga, à Zul\’Drak.\n\nLe Fléau massacre la population locale avant d\’incorporer ses victimes dans son armée.\n\nAidez la Croisade d\’argent et la Lame d\’ébène à neutraliser cette terrible menace.\n\nGloire à l\’Alliance !', '', '', '', '', '', 'Parlez au Seigneur croisé Lantinga à Zul\’Drak.', 26365),
(49551, 'frFR', 'L\’appel du héros : le fjord Hurlant !', 'Parlez à Macalroy au Fjord Hurlant.', 'Tous les citoyens de l\’Alliance en état de porter les armes sont sommés de se présenter à Macalroy, au fjord Hurlant.\n\nSituée au cœur de la région, la cité portuaire de Valgarde occupe une position clé pour attaquer le Fléau.\n\nAidez les soldats stationnés sur place.\n\nGloire à l\’Alliance !', '', '', '', '', '', 'Parlez à Macalroy au Fjord Hurlant.', 26365),
(49550, 'frFR', 'L\’appel du héros : la vallée d\’Ombrelune !', 'Parlez au Chef d\’escadrille Nuainn dans la Vallée d\’Ombrelune, en Outreterre.', 'Tous les citoyens de l\’Alliance en état de porter les armes sont sommés de se présenter au chef d\’escadrille Nuainn dans la vallée d\’Ombrelune, en Outreterre.\n\nLa Légion ardente assiège le bastion des Marteaux-Hardis et les nôtres sont en danger de mort.\n\nNous devons soutenir le bastion et mettre un terme aux assauts incessants de ces démons !\n\nGloire à l\’Alliance !', '', '', '', '', '', 'Parlez au Chef d\’escadrille Nuainn dans la Vallée d\’Ombrelune, en Outreterre.', 26365),
(49549, 'frFR', 'Ordre du chef de guerre : Nagrand !', 'Parlez au Garde de pierre Brox en Nagrand, sur Draenor.', 'Tous les membres de la Horde en état de porter les armes sont sommés de se présenter au garde de pierre Brox, en Nagrand, sur Draenor.\n\nThrall est en train d\’établir un avant-poste de la Horde et a besoin d\’aide pour repousser le clan Chanteguerre.\n\nParlez à Bron Corne-Céleste afin de vous envoler pour Nagrand et rejoignez la caravane de ravitaillement.\n\nPour la Horde !', '', '', '', '', '', 'Parlez au Garde de pierre Brox en Nagrand, sur Draenor.', 26365),
(49548, 'frFR', 'Ordre du chef de guerre : les flèches d\’Arak !', 'Parlez à Azik dans les Flèches d\’Arak.', 'Tous les membres de la Horde en état de porter les armes sont sommés de se présenter à Azik, dans les flèches d\’Arak.\n\nDes arakkoa dépourvus d\’ailes ont été aperçus fuyant les flèches d\’Arak. À en croire la rumeur, c\’est une super-arme de la Horde de Fer qui provoquerait cette panique.\n\nNous devons découvrir les raisons précises de leur exil, ainsi que la cause de cette étrange atrophie de leurs ailes.\n\nPour la Horde !', '', '', '', '', '', 'Parlez à Azik dans les Flèches d\’Arak.', 26365),
(49547, 'frFR', 'Ordre du chef de guerre : Talador !', 'Parlez au Contremaître Grobash en Talador.', 'Tous les membres de la Horde en état de porter les armes sont sommés de se présenter au contremaître Grobash, en Talador.\n\nLa Horde de Fer a jeté son dévolu sur cette région idéalement située pour envoyer rapidement et facilement des renforts aux quatre coins de Draenor.\n\nAussi le contremaître Grobash a-t-il été chargé d\’y établir un avant-poste. Rendez-vous sur place et arrêtez la Horde de Fer.\n\nPour la Horde !', '', '', '', '', '', 'Parlez au Contremaître Grobash en Talador.', 26365),
(49546, 'frFR', 'Ordre du chef de guerre : Gorgrond !', 'Parlez à Durotan en Gorgrond.', 'Tous les membres de la Horde en état de porter les armes sont sommés de se présenter à Durotan, en Gorgrond.\n\nDurotan est à la recherche d\’alliés capables de nous aider à explorer les terres sauvages de Gorgrond. Ce faisant, il espère y découvrir d\’anciens pouvoirs à même d\’anéantir la Horde de Fer.\n\nAllez lui parler et percez les mystères de cette contrée tourmentée.\n\nPour la Horde !', '', '', '', '', '', 'Parlez à Durotan en Gorgrond.', 26365),
(49545, 'frFR', 'Ordre du chef de guerre : la crête de Givrefeu !', 'Parlez au Long-voyant Drek\’Thar sur la Crête de Givrefeu.', 'Tous les membres de la Horde en état de porter les armes sont sommés de se présenter au long-voyant Drek\’Thar, sur la crête de Givrefeu.\n\nLa destruction de la Porte des ténèbres a empêché la Horde de Fer d\’envahir Azeroth, mais ces vauriens n\’ont pas dit leur dernier mot.\n\nForts de l\’aide du long-voyant Drek\’Thar, nous allons être en mesure d\’établir une tête de pont stratégique sur Draenor, afin de contrer nos ennemis.\n\nPour la Horde !', '', '', '', '', '', 'Parlez au Long-voyant Drek\’Thar sur la Crête de Givrefeu.', 26365),
(49543, 'frFR', 'Ordre du chef de guerre : les terres de l\’Angoisse !', 'Parlez au Maître-archer Li sur l\’Échine du Serpent.', 'Tous les membres de la Horde en état de porter les armes sont sommés de se présenter au maître-archer Li, sur l\’Échine du Serpent, dans le val de l\’Éternel printemps.\n\nLes Pandashan sont aux prises avec une armée de mantides qui menace d\’envahir l\’ensemble de la Pandarie.\n\nVous pouvez vous rendre sur place par la voie des airs à l\’aide d\’un cerf-volant.\n\nLe destin vous appelle !', '', '', '', '', '', 'Parlez au Maître-archer Li sur l\’Échine du Serpent.', 26365),
(49542, 'frFR', 'Ordre du chef de guerre : les steppes de Tanglong !', 'Parlez à Taran Zhu dans les Steppes de Tanglong.', 'Tous les membres de la Horde en état de porter les armes sont sommés de se présenter à Taran Zhu, dans les steppes de Tanglong.\n\nLes Pandashan veulent à tout prix reconquérir leurs terres tombées aux mains des yaungols, mais leurs effectifs sont insuffisants.\n\nAidez-les à chasser l\’envahisseur !\n\nPour la Horde !', '', '', '', '', '', 'Parlez à Taran Zhu dans les Steppes de Tanglong.', 26365),
(49541, 'frFR', 'Ordre du chef de guerre : le sommet de Kun-Lai !', 'Parlez au Messager grumelot dans la Vallée des Quatre vents.', 'Tous les membres de la Horde en état de porter les armes sont sommés d\’aider le chroniqueur Cho, au sommet de Kun-Lai.\n\nAdressez-vous à un messager grumelot dans la vallée des Quatre vents. Il vous indiquera le chemin.\n\nPour la Horde !', '', '', '', '', '', 'Parlez au Messager grumelot dans la Vallée des Quatre vents.', 26365),
(49540, 'frFR', 'Ordre du chef de guerre : les étendues sauvages de Krasarang !', 'Parlez à Ken-Ken dans les Étendues sauvages de Krasarang.', 'Tous les membres de la Horde en état de porter les armes sont sommés de se présenter au guet de Zhu, dans les étendues sauvages de Krasarang.\n\nLe bruit court que les Pandarens de la région sont plongés dans une étrange léthargie.\n\nAllez voir Ken-Ken, au guet de Zhu. Il pourra peut-être vous en dire plus sur cet étrange phénomène.', '', '', '', '', '', 'Parlez à Ken-Ken dans les Étendues sauvages de Krasarang.', 26365),
(49539, 'frFR', 'Ordre du chef de guerre : la vallée des Quatre vents !', 'Parlez à Chen Brune d\’Orage dans la Vallée des Quatre vents.', 'Tous les membres de la Horde en état de porter les armes sont sommés de se présenter à Chen Brune d\’Orage, dans la vallée des Quatre vents.\n\nLes Brune d\’Orage sont réputés pour leur audace, et la Horde gagnerait à s\’en inspirer.\n\nPartez à l\’aventure avec eux et découvrez les secrets de la Pandarie.\n\nPour la Horde !', '', '', '', '', '', 'Parlez à Chen Brune d\’Orage dans la Vallée des Quatre vents.', 26365),
(49538, 'frFR', 'Ordre du chef de guerre : la forêt de Jade !', 'Parlez au Général Nazgrim au Fort Grommash.', 'Tous les membres de la Horde en état de porter les armes sont sommés de se présenter au général Nazgrim, à Orgrimmar.\n\nNos récentes victoires navales contre l\’Alliance n\’ont fait que renforcer notre détermination.\n\nGagnez le fort Grommash et mettez-vous au service de la flotte d\’invasion de la Horde.\n\nPour la Horde !', '', '', '', '', '', '', 26365),
(49537, 'frFR', 'Ordre du chef de guerre : la Couronne de glace !', 'Parlez au Saccageur-des-cieux Klum à Dalaran, au-dessus de la Forêt du Chant de cristal.', 'Tous les membres de la Horde en état de porter les armes sont sommés de se présenter au saccageur-des-cieux Klum à Dalaran, au-dessus de la forêt du Chant de cristal.\n\nL\’Offensive chanteguerre a besoin d\’aventuriers supplémentaires pour participer au siège de la citadelle de la Couronne de glace où Arthas s\’est retranché.\n\nIl est temps que le roi-liche paie pour ses crimes.\n\nPour la Horde !', '', '', '', '', '', 'Parlez au Saccageur-des-cieux Klum à Dalaran, au-dessus de la Forêt du Chant de cristal.', 26365),
(49536, 'frFR', 'Ordre du chef de guerre : les pics Foudroyés !', 'Parlez à Rin Duoctane à Dalaran, au-dessus de la Forêt du Chant de cristal.', 'Tous les membres de la Horde en état de porter les armes sont sommés de se présenter à Rin Duoctane à Dalaran, au-dessus de la forêt du Chant de cristal.\n\nLes gobelins de K3 ont fondé une ville dans les pics Foudroyés et ils nous envoient leur ambassadeur pour recruter des aventuriers.\n\nAllez voir Rin et voyez comment pourvoir aux besoins des habitants de K3. Ils feraient de précieux alliés dans notre lutte contre le roi-liche.\n\nPour la Horde !', '', '', '', '', '', 'Parlez à Rin Duoctane à Dalaran, au-dessus de la Forêt du Chant de cristal.', 26365),
(49535, 'frFR', 'Ordre du chef de guerre : le bassin de Sholazar !', 'Parlez à l\’Archimage Pentarus à Dalaran, au-dessus de la Forêt du Chant de cristal.', 'Tous les membres de la Horde en état de porter les armes sont sommés de se présenter à l\’archimage Pentarus à Dalaran, au-dessus de la forêt du Chant de cristal.\n\nParti explorer le bassin de Sholazar, Hemet Nesingwary a disparu avec l\’ensemble de son expédition.\n\nNous en savons très peu sur cette contrée luxuriante, mais elle abrite peut-être des secrets susceptibles de nous aider dans notre lutte contre le roi-liche.\n\nPour la Horde !', '', '', '', '', '', 'Parlez à l\’Archimage Pentarus à Dalaran, au-dessus de la Forêt du Chant de cristal.', 26365),
(49534, 'frFR', 'Ordre du chef de guerre : Zul\’Drak !', 'Parlez au Seigneur croisé Lantinga à Zul\’Drak.', 'Tous les membres de la Horde en état de porter les armes sont sommés de se présenter au seigneur croisé Lantinga, à Zul\’Drak.\n\nLe Fléau massacre la population locale avant d\’incorporer ses victimes dans son armée.\n\nAidez la Croisade d\’argent et la Lame d\’ébène à neutraliser cette terrible menace.\n\nPour la Horde !', '', '', '', '', '', 'Parlez au Seigneur croisé Lantinga à Zul\’Drak.', 26365),
(49533, 'frFR', 'Ordre du chef de guerre : le fjord Hurlant !', 'Parlez à l\’Apothicaire Lysander dans le Fjord Hurlant.', 'Tous les membres de la Horde en état de porter les armes sont sommés de se présenter à l\’apothicaire Lysander, dans le fjord Hurlant.\n\nLes Réprouvés ont établi une place forte sur le littoral est du Norfendre.\n\nL\’heure est venue de frapper le Fléau avec notre formidable puissance militaire et l\’arme calamiteuse actuellement développée par la Société royale des apothicaires.\n\nPour la Horde !', '', '', '', '', '', 'Parlez à l\’Apothicaire Lysander dans le Fjord Hurlant.', 26365),
(49532, 'frFR', 'Ordre du chef de guerre : la vallée d\’Ombrelune !', 'Parlez au Garde de sang Gulmok dans la Vallée d\’Ombrelune, en Outreterre.', 'Tous les membres de la Horde en état de porter les armes sont sommés de se présenter au garde de sang Gulmok, dans la vallée d\’Ombrelune, en Outreterre.\n\nLa Légion ardente tente d\’assiéger le village d\’Ombrelune, mais nos troupes d\’élite kor\’kronnes leur montrent ce qu\’il en coûte de se frotter à la Horde.\n\nÉcrasez la Légion.\n\nPour la Horde !', '', '', '', '', '', 'Parlez au Garde de sang Gulmok dans la Vallée d\’Ombrelune, en Outreterre.', 26365),
(49474, 'frFR', 'L\’éclat maudit', 'Apportez l\’Éclat maudit à l\’Arcanomancien Vridiel, à Dalaran dans les îles Brisées.', '<Un éclat métallique irisé de reflets chatoyants attire votre regard. À votre contact, l\’une de ses extrémités gonfle et se met à fondre. Les gouttes qui tombent au sol émettent un grésillement accompagné d\’une odeur âcre et fétide.$b$bUn artisan de Dalaran qui a l\’habitude de travailler le métal pourra certainement vous en dire davantage.>', '', '', '', '', '', '', 26365),
(49473, 'frFR', 'Un effort titanesque', 'Apportez $1oa unités d\’Essence d\’éveil à l\’Arcanomancien Vridiel.', 'La réaction s\’intensifie de jour en jour. Même si nous gagnons le combat contre la Légion, nous pourrions très bien perdre quelque chose qui nous est cher.\n\nS\’agit-il des prémices d\’un bouleversement bien plus profond ? Quel sera le destin de notre monde, $n ?\n\nJe tenterai de le découvrir dans ma forge, comme je l\’ai toujours fait. Vos réponses, en revanche, vous attendent ailleurs.', '', '', '', '', '', '', 26365),
(49472, 'frFR', 'Un effort titanesque', 'Apportez $1oa unités d\’Essence d\’éveil à l\’Arcanomancien Vridiel.', 'Illidan a peut-être forcé le cours du destin, mais rapprocher Argus et Azeroth n\’a fait qu\’accélérer la réaction provoquée par l\’effondrement du Puits de la nuit.\n\nJe suis persuadé que les manigances de la Légion sur Argus déstabilisent encore davantage les pouvoirs que nous exploitons depuis des années sans connaître les tenants et aboutissants.\n\nBien entendu, il me faut encore étayer cette théorie.', '', '', '', '', '', '', 26365),
(49445, 'frFR', 'Un échange primordial', '', '', '', '', '', '', '', '', 26365),
(49354, 'frFR', 'Le souvenir du Puits de soleil', 'Terminez le scénario « Le souvenir du Puits de soleil ».', 'Le seigneur régent Theron souhaite montrer notre Puits de soleil à la première arcaniste Thalyssra.\n\nJ\’ignore s\’il autorisera Alleria Coursevent à participer à la rencontre, néanmoins nous sommes invités à les rejoindre.', '', '', '', '', '', '', 26365),
(49293, 'frFR', 'Assaut du site d\’invasion', 'Terminez 3 Sites d\’invasion standards.', 'Nous ne devons pas céder le moindre monde à la Légion. Poursuivez l\’offensive sur le site d\’invasion et écrasez la Légion sous votre talon.', '', '', '', '', '', '', 26365),
(49266, 'frFR', 'Forge des Éons', 'Assistez au rituel des Sancteforge.', 'Cela fait bien longtemps que les Sancteforge représentent la flamme de l\’espoir face à la Légion.\n\nMais très peu savent vraiment ce qu\’on exige de ceux qui sont appelés à incarner le courroux de la Lumière.\n\nCes rares champions élus parmi l\’armée de la Lumière sont prêts à subir leur épreuve finale.\n\nNous ferez-vous l\’honneur d\’assister à ce rituel ?', '', '', '', '', '', '', 26365),
(49224, 'frFR', 'Le creuset de Halo-du-Néant', 'Imprégnez une relique avec Fortification de Halo-du-Néant en utilisant le Creuset de Halo-du-Néant.', 'Le creuset vibre de puissance. Bientôt, nos armes brilleront autant que les étoiles !\n\nNous avons besoin de ce pouvoir si nous voulons avoir une chance de vaincre la Légion ici.\n\n$n, vous avez subi de rudes épreuves pour nous conduire à ce moment. L\’honneur d\’exploiter la puissance du creuset en premier vous revient de droit.', '', '', '', '', '', '', 26365),
(49198, 'frFR', 'Site d\’invasion prioritaire : inquisiteur Méto', '', '', '', '', '', '', '', '', 26365),
