UPDATE `quest_template_locale` SET `QuestDescription`='Es ist so weit, $n.$b$bXe\'ra ist zu uns zurückgekehrt. Wir haben das Herz des Lichts. Wir haben ihren Auserwählten.$b$bKommt, lasst uns sie erneuern. Lasst uns einen hellen, neuen Weg gegen unsere Feinde beschreiten.$b$bSprecht mich an, wenn Ihr bereit seid.', `VerifiedBuild`=29981 WHERE (`ID`=47743 AND `locale`='deDE');
UPDATE `quest_template_locale` SET `QuestDescription`='Wenn Ihr noch mehr sammeln wollt, stört das Gaunah kein bisschen. Aber ich brauch da jetzt nich\' mehr so viel von, also zahl\' ich Euch weniger.', `VerifiedBuild`=29981 WHERE (`ID`=47595 AND `locale`='deDE');
