UPDATE `quest_template_locale` SET `QuestDescription`='Ich habe durch meine Beobachtung des Lachenden Schädels bereits einiges erfahren.$b$bMan dachte immer, es sei wegen der Säure, die diese hinterlassen, unmöglich, die Gorentunnel zu durchqueren, aber die Orcs haben einen Ausweg gefunden.$b$bWir brauchen einige Hüllen der Goren aus der Schlucht.$b$bNähert Euch ihren Tunnelausgängen, und die Goren springen heraus. Auf die Art könnt Ihr viele gleichzeitig töten und schneller an die Hüllen kommen.', `VerifiedBuild`=29981 WHERE (`ID`=36210 AND `locale`='deDE');
