UPDATE `quest_template_locale` SET `LogDescription`='Sprecht mit einem Feuerschluckmeister in einer der Hordehauptstädte.', `QuestDescription`='Möchtet Ihr ein paar heiße neue Fertigkeiten erlernen?$b$bOb Ihr jemals meine beeindruckende Fertigkeit erreichen werdet, sei zwar dahingestellt, aber ein oder zwei Kunststückchen werdet Ihr wahrscheinlich aufschnappen können, wenn Ihr $gein aufmerksamer Schüler:eine aufmerksame Schülerin; seid. Sprecht mit einem Feuerschluckmeister in einer der Hauptstädte der Horde. Sie sollten Euch weiterhelfen können.', `VerifiedBuild`=29981 WHERE (`ID`=11915 AND `locale`='deDE');
