UPDATE `quest_template_locale` SET `LogDescription`='Anachoretin Karja in Area 52 möchte, dass Ihr die erste Hälfte von Socrethars Stein von Konstruktionsmeister Morug in der Konstruktionsbasis: Vergessenheit und die zweite Hälfte des Steins von Konstruktionsmeister Silroth in der Konstruktionsbasis: Gehenna besorgt.$B$BDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.', `VerifiedBuild`=29981 WHERE (`ID`=10407 AND `locale`='deDE');
