UPDATE `quest_template_locale` SET `LogTitle`='Conocer al manitas mayor', `LogDescription`='Preséntate al manitas mayor Mekkatorque en Nueva Ciudad Manitas.', `QuestDescription`='No sé si te acordarás de Mekkatorque. Nevin dice que la pérdida de memoria es bastante común en un superviviente de Gnomeregan.$b$bMekkatorque, o mejor dicho, el manitas mayor Mekkatorque es el líder de los gnomos. Se ha acostumbrado a reunirse personalmente con cada superviviente que ha sido rescatado. Deberías presentarte tú $gmismo:misma;. Lo encontrarás junto con su consejo de guerra bajo la tienda del centro de la ciudad.', `QuestCompletionLog`='Preséntate a: manitas mayor Mekkatorque. Zona: Nueva Ciudad Manitas.', `VerifiedBuild`=29981 WHERE (`ID`=41218 AND `locale`='esES');
