(55635, 'esMX', 'Una voz en el viento', 'Busca la fuente de la voz misteriosa en Drustvar.', '<Una voz misteriosa y distante invade tu mente.>\n\nMis encantamientos no se han perdido...\n\nBúscame...\n\nDrustvar...\n\nAmparo del Ocaso...', '', '', '', '', '', '', 30706),
(55596, 'esMX', 'Reparaciones sagradas', 'Regresa con Seshuli en Dazar\'alor e infórmale sobre tu descubrimiento.', '<Seshuli seguramente querrá enterarse del progreso que has logrado en este santuario.$b$bEspecialmente la parte en la que tuviste que luchar para atravesar Atal\'Dazar.>', '', '', '', '', '', '', 30706),
(55594, 'esMX', 'Pulido y encerado', 'Regresa con Seshuli en Dazar\'alor e infórmale sobre tu descubrimiento.', '<Seshuli seguramente querrá enterarse del progreso que has logrado en este santuario.$b$bSientes que deberías ir a informarle lo antes posible.>', '', '', '', '', '', '', 30706),
(55592, 'esMX', 'Un comienzo prometedor', 'Regresa con Seshuli en Dazar\'alor e infórmale sobre tu descubrimiento.', '<Ahora que el santuario está reparado, estás $Gansioso:ansiosa; por salir a buscar el próximo.$b$bQuizás Seshuli conozca más ubicaciones.>', '', '', '', '', '', '', 30706),
(55590, 'esMX', 'Reparaciones sagradas', 'Regresa con Samuel D. Colton III en Boralus e infórmale sobre tu descubrimiento.', '<Samuel seguramente querrá enterarse del progreso que has logrado en este santuario.$b$bEspecialmente la parte en la que tuviste que luchar para atravesar una prisión.>', '', '', '', '', '', '', 30706),
(55586, 'esMX', 'Pulido y encerado', 'Regresa con Samuel D. Colton III en Boralus e infórmale sobre tu descubrimiento.', '<Samuel seguramente querrá enterarse del progreso que has logrado en este santuario.$b$bSientes que deberías ir a informarle lo antes posible.>', '', '', '', '', '', '', 30706),
