UPDATE `quest_template_locale` SET `LogTitle`='L\’assaut des Ogres', `QuestDescription`='Ces Ogres sont juste devant un site de fouilles de la Société de Conservation de Gentepression.$b$bCe sont des monstres brutaux, violents et puants. Et pour être honnête, je leur en veux vraiment d\’avoir cassé mon planeur.$b$bSoyez sympa et exterminez-moi tout ça, d\’accord ?$b$bÇa rendra service à tout le monde. Enfin ça me rendra service à moi, en tout cas.', `VerifiedBuild`=29981 WHERE (`ID`=35972 AND `locale`='frFR');
UPDATE `quest_template_locale` SET `QuestDescription`='Je suis pendu ici depuis une éternité !$b$bJe survolais la zone, vaquant à mes affaires quand… blam ! Un de ces stupides Ogres m\’a jeté une pierre !$b$bMon planeur s\’est cassé et me voilà… dans une position très inconfortable.$b$bAidez-moi à descendre ? Vous n\’aurez pas affaire à un ingrat.', `VerifiedBuild`=29981 WHERE (`ID`=35945 AND `locale`='frFR');
