UPDATE `quest_template_locale` SET `QuestDescription`='Ces Ogres ne cessent de capturer des membres de mon clan pour en faire leurs esclaves !$B$BTuez quelques Ogres, je les retiendrai s\’ils tentent de fuir.', `VerifiedBuild`=29981 WHERE (`ID`=33898 AND `locale`='frFR');
