UPDATE `quest_template_locale` SET `QuestDescription`='Faire la nounou ! Vous vous rendez compte !$B$BIls m\’ont arraché à des fouilles prometteuses et m\’ont envoyé rejoindre l\’expédition de l\’amiral Odesyus pour que je serve de nourrice à ce petit Gnome !$B$BJe n\’ai jamais entendu parler d\’un Gnome qui aurait voulu devenir prospecteur.$B$BMais il paraît que maintenant, on prend les Gnomes, et celui-là est fasciné par les Murlocs !$B$BBon, il est parti et s\’est installé dans une vieille carapace de tortue, sur la rive nord de l\’île. Allez le voir, si vous ne me croyez pas !', `VerifiedBuild`=29981 WHERE (`ID`=10063 AND `locale`='frFR');
