UPDATE `quest_template_locale` SET `LogTitle`='Guile of the Raptor', `LogDescription`='Acquire 12 Raptor Hearts from Highland Fleshstalkers for Tor\'gan.', `QuestDescription`='With some additional objects, I will be able to create an item that will imbue Gor\'mul with the guile of the raptor! With the power of the raptor flowing through him, Gor\'mul will know our new power and rediscover the warrior that lies dormant!$b$bI will require raptor hearts to fashion the item and spell.', `VerifiedBuild`=29981 WHERE (`ID`=701 AND `locale`='frFR');
