(52167, 'itIT', 'Incursori irriducibili', '', '', '', '', '', '', '', '', 29981),
(52166, 'itIT', 'Alfiere senza volto', '', '', '', '', '', '', '', '', 29981),
(52165, 'itIT', 'Caos automatizzato', 'Sconfiggi Grixis Minibotto in uno scontro tra mascotte.', '', '', '', '', '', '', '', 29981),
