(53119, 'zhCN', '失落海洋卷轴', '', '', '', '', '', '', '', '', 30706),
(53118, 'zhCN', '凹陷的硬币', '', '', '', '', '', '', '', '', 30706),
(53117, 'zhCN', '锈蚀的联盟徽记', '', '', '', '', '', '', '', '', 30706),
(53116, 'zhCN', '锈蚀的部落徽记', '', '', '', '', '', '', '', '', 30706),
