INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(201778, 'deDE', 'Wir sind so weit. Geht voran!', 'Wir sind so weit. Geht voran!', 35284),
(753, 'deDE', 'Was? Soll das heißen, Ihr könnt den hier nicht selbst töten?', '', 35284),
