UPDATE `quest_template_locale` SET `QuestDescription`='Я хочу переслать в Громовой Утес связку шкур степных животных. Мой коллега, Ахану, занимается кожевенным делом и найдет им достойное применение.$B$BНе возьмешься доставить ему шкуры?$B$BДо Громового Утеса проще всего добраться на ветрокрыле. Отнеси шкуры Таку, нашему укротителю ветрокрылов, и поговори с ним насчет перелета в Громовой Утес.', `VerifiedBuild`=32978 WHERE (`ID`=6361 AND `locale`='ruRU');
