INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(187442, 'esMX', '', 'Tus pecados son numerosos. Pero nada es tan cruel como enviar a tus bestias contra inocentes.', 37474),
(38124, 'esMX', 'Abraza la oscuridad... la oscuridad eterna.', 'Abraza la oscuridad... la oscuridad eterna.', 37474),
(180366, 'esMX', '', '¡Es todo un placer para mí darte la bienvenida a Bastión! Aunque… jamás había visto a otra alma como la tuya. No importa.', 37474),
(187280, 'esMX', 'Ahora que la Casa de los Ojos ha sido eliminada, nuestro secreto está a salvo. No queda nadie que pueda impedir nuestro trabajo.', '', 37474),
(185904, 'esMX', '¿Celos? Por favor. ¡Yo era superior a él en todo sentido!', '', 37474),
(182733, 'esMX', 'La Marca del saber. Un faro contra la ignorancia y el miedo.', '', 37474),
(180367, 'esMX', '', '¡Te unirás a los kyrian! Nuestro deber sagrado es llevar las almas mortales ante la Árbitra para que las juzgue.', 37474),
(194383, 'esMX', '', 'Esto será emocionante.', 37474),
(182626, 'esMX', '', 'Cuando estés $ulisto:lista;, acércate a la llama y sigue las instrucciones de la vigía.', 37474),
(202313, 'esMX', '', '¡Déjeme atravesarlos! ¡Cortarlos! ¡Tajarlos! ¡Diseccionarlos! Hacer trizas su...', 37474),
(202311, 'esMX', 'Mmm, eso no suena mal... podríamos despellejar a estas criaturas petulantes y necias, y usar sus huesos para pavimentar el camino a nuestro ascenso. Qué hacer...', '', 37474),
(202310, 'esMX', '', '¡Tengo una mejor idea, amo! ¡Mejor déjeme atravesarlos!', 37474),
(202312, 'esMX', '', '¡Por favoooooor, amo! ¡Ha pasado tanto tiempo desde que corté carne viva!', 37474),
(202315, 'esMX', 'Ayudaré a nuestros invitados con su expiación. ¡Una vez que se arrepientan, podrás descuartizar sus cuerpos y enviar sus almas gritando a las Fauces!', '', 37474),
(205464, 'esMX', '', 'Ahora entiendo por qué todos dicen que las criaturas de tu mundo son formidables. No me sorprende que hayas podido expulsar a la Legión Ardiente. En nombre de Ardenweald, te doy las gracias por tu fuerza.', 37474),
(191962, 'esMX', '<Hay una escritura tallada en la base de la estatua.>\n\nEl respeto se gana en la prueba del poder. Saludamos a quienes se elevan sobre las masas.', '', 37474),
(191966, 'esMX', '<El texto está tallado en la base de la estatua.>\n\nLas verdades ocultas pueden hallarse en la oscuridad. Los hábiles se pueden Escabullir entre sus enemigos sin ser vistos.', '', 37474),
(180775, 'esMX', 'Debo advertir... al droman...', 'Debo advertir... al droman...', 37474),
(193577, 'esMX', '', 'Sujeta esto un momento.', 37474),
(186290, 'esMX', 'Elegante y poderosa. Una corona para defender un reino.', '', 37474),
(193598, 'esMX', '', '¿Dónde puedo ponerte?', 37474),
(193582, 'esMX', '', '¿Quieres poner esto a prueba?', 37474),
(203409, 'esMX', 'Un arma... forjada en los fuegos de Torghast. Nace una espada de agonía.', '', 37474),
(196707, 'esMX', '¡Dispara cuando quieras!', '', 37474),
(199409, 'esMX', '¿Quieres mi ánima? ¿No puede hacerlo la vieja tirnenn?', '', 37474),
(199444, 'esMX', 'Listo ¡Terminemos con esto!', '', 37474),
(198956, 'esMX', 'No tengo tiempo para esto, $c; la Condesa espera.', 'No tengo tiempo para esto, $c; la Condesa espera.', 37474),
(206412, 'esMX', '', '¿Qué te trae a Maldraxxus, $p?\n\n¿Más champiñones para los festines de la corte o más soldados de Maldraxxus para defender tus muros?\n\n<La baronesa Vashj sonríe.>\n\nDebo admitir que disfruto de la Corte de Brasas mucho más de lo que esperaba.\n\nPuede que el príncipe Renathal sea el anfitrión en el centro de la escena, pero no te he perdido de vista a ti.\n\nEres tú quien mueve los hilos detrás de escena, tal como yo lo hice en mis días en la corte de la reina.\n\n<La baronesa Vashj queda absorta en sus pensamientos.>\n\nLo que haces no es nada sencillo.', 37474),
(202361, 'esMX', '¡Kyrian! Te doy la bienvenida a los Archivos Eternos, donde un influjo de ánima nos permite fortalecer tus habilidades.\n\nAceptar mi mejora te otorgará lo siguiente:\n\n|TInterface\\ICONS\\Achievement_Raid_Thunder_King.blp:20|t|cFFFF0000|Hspell:335576|h[Estrella polar de ascendiente]|h|r: Mientras tengas más del 90% de salud, tus hechizos y habilidades que infligen daño tienen una probabilidad de lanzar Marcado para la recolección sobre tu objetivo, que les inflige daño con el tiempo. Si derrotas a un enemigo durante este tiempo, recogerás su ánima y eso te escudará por un período breve.', '¡Kyrian! Te doy la bienvenida a los Archivos Eternos, donde un influjo de ánima nos permite fortalecer tus habilidades.\n\nAceptar mi mejora te otorgará lo siguiente:\n\n|TInterface\\ICONS\\Achievement_Raid_Thunder_King.blp:20|t|cFFFF0000|Hspell:335576|h[Estrella polar de ascendiente]|h|r: Mientras tengas más del 90% de salud, tus hechizos y habilidades que infligen daño tienen una probabilidad de lanzar Marcado para la recolección sobre tu objetivo, que les inflige daño con el tiempo. Si derrotas a un enemigo durante este tiempo, recogerás su ánima y eso te escudará por un período breve.', 37474),
(199451, 'esMX', '', 'Ja... Bueno, una vez con Tejesueños...', 37474),
(183994, 'esMX', '', '¡Un poco a la izquierda!', 37474),
(183995, 'esMX', '', '¡Izquierda! ¡Derecha! ¡Tu otra derecha!', 37474),
(183993, 'esMX', '¿Puedes llegar arriba?', '', 37474),
(204708, 'esMX', 'Blanchy muerta levanta los cascos con obediencia, feliz de que alguien cure sus heridas. Respira con dificultad y parece muy sedienta.', '', 37474),
(204686, 'esMX', 'Blanchy muerta se sacude y refriega el hocico sobre ti mientras el cepillo de pelo le quita las partículas que le irritaban la piel. Cuando comienza a andar, notas que tiene los cascos muy astillados.', '', 37474),
(204663, 'esMX', 'Blanchy muerta acepta la avena con entusiasmo. Tiene el pelaje enmarañado y la piel irritada por ácaros, espinas punzantes y trozos de suciedad maloliente.', '', 37474),
(204710, 'esMX', 'Blanchy muerta se queda inmóvil mientras le colocas la manta de montura. Se estremece un poco. Luego se detiene y comienza a buscar algún bocadillo en tus sacos.', '', 37474),
(204668, 'esMX', 'Blanchy muerta se detiene abruptamente para no atropellarte.', '', 37474),
(204903, 'esMX', 'Blanchy muerta tiene la mirada enajenada y una forma esquelética y sombría. Es evidente que está famélica y necesita una buena comida.', '', 37474),
(121956, 'esMX', 'Yo, el Maestro Mathias Shaw, por la presente autorizo que la Espada carmesí le dé caza al agente pícaro Amber Kearnen.', '', 37474),
(204709, 'esMX', 'Blanchy muerta bebe con avidez toda el agua del cubo y te da un golpe suave con la cabeza. Ha aplacado su sed, pero deja de temblar.', '', 37474),
(54130, 'esMX', '', 'Rell está malherido.$b$b<La agente Kearnen golpea el puño contra el suelo.>$b$b¡Se muere, $n! ¡Se muere y no podemos hacer nada!', 37474),
(204749, 'esMX', 'Las fosas nasales de Blanchy muerta se dilatan cuando huele las manzanas. A continuación, las mastica con entusiasmo. Blanchy muerta suspira satisfecha y apoya la cabeza en tu hombro.', '', 37474),
(121283, 'esMX', 'Amber Kearnen era una buena muchacha... Aún no puedo creer que tuvimos que matarla.', '', 37474),
(197546, 'esMX', '', '¡Basta! Sus movimientos espasmódicos me dan náuseas.', 37474),
(207303, 'esMX', 'Gracias por ayudarnos a defender el bosque, $Ucazador:cazadora;.', 'Gracias por ayudarnos a defender el bosque, $Ucazador:cazadora;.', 37474),
(189107, 'esMX', 'A pesar de nuestro interés en taumaturgos capaces, no reduciremos nuestras expectativas.', '', 37474);
INSERT INTO `faction_locale` (`ID`, `locale`, `Name_lang`, `Description_lang`, `VerifiedBuild`) VALUES
(2463, 'esMX', 'Marasmius', 'Marasmius, un habitante increíblemente ancestral de Ardenweald con forma de hongo, es una parte integral del ecosistema del reino. Tu disposición para ayudar ha ablandado su corazón, o lo que sea que tenga que se parezca a uno, y siempre hay mucho trabajo que hacer.', 37474),
(2410, 'esMX', 'El Ejército Inmortal', 'Hace mucho tiempo, el Primus erigió las cinco casas de Maldraxxus para formar la mayor fuerza de combate conocida. Con su poder, ingenio y astucia, esta armada implacable jura defender las Tierras de las Sombras contra todas las amenazas.', 37474),
(2465, 'esMX', 'Cazadores salvajes', 'Invocados para servir a la Reina del invierno en tiempos de gran necesidad, los Cazadores salvajes reúnen a los mejores cazadores y cazadoras de todo Ardenweald. Frente a la sequía y el peligro, están listos para defender el bosque.', 37474),
(2462, 'esMX', 'Maestros de puntadas', 'Los maestros de puntadas son famosos por su habilidad para fusionar carne, hueso y ánima y convertirlos en ensamblajes, lo que los hace aliados poderosos en Maldraxxus. Viven en el Bloque del carnicero desde que se separaron de la traicionera Casa de Ensamblajes.', 37474),
(2464, 'esMX', 'Corte de la Noche', 'La sequía de ánima y la invasión de los drust consumen la atención de la Reina del invierno y los Cazadores salvajes, lo que deja a los droman de la Corte de la Noche a cargo de la protección de las semillas silvestres y la defensa de las arboledas.', 37474);
