INSERT INTO `mail_template_locale` (`ID`, `locale`, `Body_lang`, `VerifiedBuild`) VALUES
(568, 'esMX', 'Para $p:\n\nNo pondré en duda la exactitud del cálculo actual de presas que informaste en tu carta anterior.\n\nPero sí debo decir que es sospechosamente alto. Debe ser porque eres $Gel:la; Surcafauces...\n\nEn fin, disfruto de nuestra competencia e intentaré alcanzarte.\n\nHe registrado un total de ciento treinta y ocho golpes de gracia desde que recibí tu última carta, y tengo--\n\n<Las palabras de la capitana de los Cazadores Korayn se desvanecen en la página.>\n\n¡En total son ciento cincuenta y dos presas! Por supuesto, algunas de la última oleada fueron ácaros, ¡pero no puedo ser selectiva cuando tú me llevas tanta ventaja!\n\nIntenta superar eso,\nCapitana de los Cazadores Korayn', 37474);
INSERT INTO `chr_classes_locale` (`ID`, `locale`, `Name_lang`, `NameMale_lang`, `NameFemale_lang`, `Description_lang`, `RoleInfoString_lang`, `DisabledString_lang`, `HyphenatedNameMale_lang`, `HyphenatedNameFemale_lang`, `VerifiedBuild`) VALUES
(3, 'esMX', 'Cazador', 'Cazador', 'Cazadora', 'Los cazadores se sienten como en casa en la naturaleza y sienten especial afinidad por las bestias. Dependen de sus armas y sus mascotas para infligir daño.', 'Funciones: |cffffffffDaño a distancia o daño cuerpo a cuerpo|r', 'Debes elegir una raza diferente para ser de esta clase.', 'Cazador', 'Cazadora', 37474),
(1, 'esMX', 'Guerrero', 'Guerrero', 'Guerrera', 'Los guerreros son luchadores que visten placas y buscan la perfección en el combate armado. A medida que luchan, generan ira que usan para potenciar ataques especiales.', 'Funciones: |cffffffffTanque o daño cuerpo a cuerpo|r', 'Debes elegir una raza diferente para ser de esta clase.', 'Guerrero', 'Guerrera', 37474),
(11, 'esMX', 'Druida', 'Druida', 'Druida', 'Los druidas cambian de forma y sienten afinidad por el reino animal y vegetal. Se especializan en sanación, lanzamiento de hechizos a distancia, o también pueden adoptar la forma de un felino o de un oso para luchar cuerpo a cuerpo.', 'Funciones: |cffffffffTanque, Sanador, Daño a distancia o Daño cuerpo a cuerpo|r', 'Debes elegir una raza diferente para ser de esta clase.', 'Druida', 'Druida', 37474),
(5, 'esMX', 'Sacerdote', 'Sacerdote', 'Sacerdotisa', 'Los sacerdotes son sanadores muy completos con una variedad de herramientas. No obstante, también pueden sacrificar la sanación para infligir daño con magia de las sombras.', 'Funciones: |cffffffffSanador o daño a distancia|r', 'Debes elegir una raza diferente para ser de esta clase.', 'Sacerdote', 'Sacerdotisa', 37474),
(8, 'esMX', 'Mago', 'Mago', 'Maga', 'Los magos son los icónicos hechiceros de Azeroth que aprenden su arte a través de exhaustiva investigación y estudios. Compensan su armadura ligera con una potente selección de hechizos ofensivos y defensivos.', 'Función: |cffffffffDaño a distancia|r', 'Debes elegir una raza diferente para ser de esta clase.', 'Mago', 'Maga', 37474),
(7, 'esMX', 'Chamán', 'Chamán', 'Chamán', 'Los chamanes utilizan el poder de los elementos para mejorar su daño con armas o con hechizos. Invocan tótems en combate: pequeños objetos para incapacitar, sanar o infligir daño a los enemigos.', 'Funciones: |cffffffffSanador, Daño a distancia o Daño cuerpo a cuerpo|r', 'Debes elegir una raza diferente para ser de esta clase.', 'Chamán', 'Chamán', 37474),
(12, 'esMX', 'Cazador de demonios', 'Cazador de demonios', 'Cazadora de demonios', 'Los cazadores de demonios son una clase héroe, lo que significa que comienzan en un nivel alto. Son forajidos oscuros que usan magia demoníaca prohibida para cazar y matar a sus enemigos.', 'Funciones: |cffffffffTanque o daño cuerpo a cuerpo|r', 'Requiere la expansión Legion y un personaje de nivel 70.', 'Cazador de demonios', 'Cazadora de demonios', 37474),
(9, 'esMX', 'Brujo', 'Brujo', 'Bruja', 'Los brujos utilizan magia de fuego o de las sombras para infligir daño, drenar o maldecir a su enemigo. Invocan a demonios como sirvientes.', 'Función: |cffffffffDaño a distancia|r', 'Debes elegir una raza diferente para ser de esta clase.', 'Brujo', 'Bruja', 37474),
(4, 'esMX', 'Pícaro', 'Pícaro', 'Pícara', 'Los pícaros suelen ser asesinos o exploradores, aunque también hay muchos que trabajan en solitario. A menudo, pueden acercarse sigilosamente a sus enemigos o atacar a sus oponentes por la espalda para acabar con ellos rápido.', 'Función: |cffffffffDaño cuerpo a cuerpo|r', 'Debes elegir una raza diferente para ser de esta clase.', 'Pícaro', 'Pícara', 37474);
